//-------------------------------------------------------------------------------
//  fv_saveMode   : 수정과 신규 저장 모드 플래그 
//	fv_faciNum	  : 선택된 시설물 번호
//  fv_Kind       : 시설 종류
//-------------------------------------------------------------------------------

var fv_saveMode = "";
var fv_faciNum = "";
var fv_govNum = "";
var fv_Kind = "";
var G_GOV_NUM = "";

/*===============================================================================
 * 함수명: D1101010010F_OnLoadCompleted()
   설명: 화면 로드 후 조회 조건 기본 셋팅
   return 내용: 없음
===============================================================================*/ 
function D1101010010F_T01_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	
	if (IsExistVar("PORTLET_FLAG", true)) {
	    if (PORTLET_FLAG == "p") {
			edt_scada_num.Text = FACI_NUM;
			fn_D1_Search();
			grd_GovRoom_OnCellClick();
	    }
	}
	
	if (IsExistVar("S_FLAG", true)) {
	    if (S_FLAG == "SCADA") {
			edt_scada_num.Text = S_FACI_NUM;
			fn_D1_Search();
			grd_GovRoom_OnCellClick();
	    }
	}
	
	fn_init();
	gfn_D1_SetGrid_Sort(grd_GovRoom);
}

//-------------------------------------------------------------------------------
// 함수명: fn_ZipSearch()
//   설명: 우편번호 조회클릭시 우편번호 조회 popup화면 로딩
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_ZipSearch(obj)
{
   // if(fv_faciNum != "")
	//{
		var arrList = gfn_Dialog("A10", "A1101020010U", "", 1100, 600, true, -1, -1);
		
		if(arrList == 0){
			return;
		}		
		
		if (arrList != "" && arrList != null) {
			
			if (cob_Gu.Value != arrList[2]) {
				 alert("해당 시/구를 선택해주세요.");
				 return;
			}
			
			if(obj.id == "btn_DZip"){   
				edt_lot_num_union  = arrList[17];  		 // 지번주소조합
				edt_new_addr_union = arrList[23]; 		 // 새주소조합
				edt_zip_seq        = arrList[0];         // 우편일련번호     
				edt_hill           = arrList[10];        // 산번지
				edt_addr_m         = arrList[11];        // 본번
				edt_addr_s         = arrList[12];        // 부번  
				edt_second_addr    = arrList[13];    	 // 보조주소
				edt_bld_dong       = arrList[14];        // 동
				edt_hosu           = arrList[15];        // 호수
				edt_zip_no1        = arrList[2];         // 우편번호1
				edt_zip_no2        = arrList[3];         // 우편번호2
				// edt_new_addr_bld_seq     = arrList[18];  // 새주소일련번호
				// edt_new_addr_ungrnoud_yn = arrList[25];  // 새주소지하여부
				// edt_new_hosu             = arrList[21];  // 새주소호수 
			}
		}	
		
		G_ACTIVEWIN = parent.id;
	//}	
}

// ===============================================================================
// 함수명: D1101010010F_T01_OnLoadCompleted()
//   설명: 화면 로드 후 조회 조건 기본 셋팅
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_init()
{
    //-----------------------------------------
	//  콤보박스의 내용을 셋팅한다.
	//-----------------------------------------	
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branc"));
	gfn_D1_NULL_SetComboBox_Comcode("B10033",gfn_setDsObject_Comcode("cob_GovRoomType"));
	gfn_D1_NULL_SetComboBox_Comcode("D10008",gfn_setDsObject_Comcode("cob_GovRoomMaterFlag"));
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd"));
	gfn_D1_NULL_SetComboBox_Comcode("D10011",gfn_setDsObject_Comcode("cob_LeaseStsCd"));	
	//gfn_D1_NULL_SetComboBox_Comcode("D10202",gfn_setDsObject_Comcode("cob_LeaseCond"));	
	gfn_D1_NULL_SetComboBox_Comcode("A10190",gfn_setDsObject_Comcode("cob_BoundaryFenceYn"));	
	gfn_D1_NULL_SetComboBox_Comcode("D10016",gfn_setDsObject_Comcode("cob_DoorType"));	
	gfn_D1_NULL_SetComboBox_Comcode("D10010",gfn_setDsObject_Comcode("cob_GovFlag"));	
	gfn_D1_NULL_SetComboBox_Comcode("C30067",gfn_setDsObject_Comcode("cob_GovModelNm"));
	gfn_D1_NULL_SetComboBox_Comcode("C30111",gfn_setDsObject_Comcode("cob_Std"));
	gfn_D1_NULL_SetComboBox_Comcode("D10009",gfn_setDsObject_Comcode("cob_Ssv1Model"));		// 임시 ... 찾지 못했음 	
	gfn_D1_NULL_SetComboBox_Comcode("D10006",gfn_setDsObject_Comcode("cob_InGatePress"));
	gfn_D1_NULL_SetComboBox_Comcode("D10007",gfn_setDsObject_Comcode("cob_ExitPress"));
	gfn_D1_NULL_SetComboBox_Comcode("D10014",gfn_setDsObject_Comcode("cob_SafeValveKind"));		// 임시 ... 찾지 못했음 
	gfn_D1_NULL_SetComboBox_Comcode("B30007",gfn_setDsObject_Comcode("cob_IntoValveDiamCd"));
	gfn_D1_NULL_SetComboBox_Comcode("B30007",gfn_setDsObject_Comcode("cob_ExitDiamCd"));
	gfn_D1_NULL_SetComboBox_Comcode("C30068",gfn_setDsObject_Comcode("cob_GovRoomMaker"));
	gfn_D1_NULL_SetComboBox_Comcode("D10109",gfn_setDsObject_Comcode("cob_InstType"));
	gfn_D1_NULL_SetComboBox_Comcode("D10109",gfn_setDsObject_Comcode("cob_EtcInstType"));	// 임시 ... 찾지 못했음 
	gfn_D1_NULL_SetComboBox_Comcode("D10006",gfn_setDsObject_Comcode("cob_MaxIntoPress"));
	gfn_D1_NULL_SetComboBox_Comcode("D10111",gfn_setDsObject_Comcode("cob_Recoder"));	// 임시 ... 찾지 못했음 
	gfn_D1_NULL_SetComboBox_Comcode("D10112",gfn_setDsObject_Comcode("cob_FilterType"));
	gfn_D1_NULL_SetComboBox_Comcode("D10051",gfn_setDsObject_Comcode("cob_ElementMaterial"));	// 임시 ... 찾지 못했음 
	//gfn_D1_NULL_SetComboBox_Comcode("C30111",gfn_setDsObject_Comcode("cob_ElementStd"));	// 임시 ... 찾지 못했음 
	gfn_D1_NULL_SetComboBox_Comcode("A10200",gfn_setDsObject_Comcode("cob_CathodicElecYn"));
	gfn_D1_NULL_SetComboBox_Comcode("D10197",gfn_setDsObject_Comcode("cob_LeaseClass"));
	gfn_D1_NULL_SetComboBox_Comcode("D10018",gfn_setDsObject_Comcode("cob_sound"));
	gfn_D1_NULL_SetComboBox_Comcode("D10186",gfn_setDsObject_Comcode("cob_FaciStsCd"));
				
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") {
	    cob_Branc.value = '';
	} else {
	    cob_Branc.Value = gfn_GetGlobalValue("G_BRANCH_CD");
	    cob_Branc.Enable = false;
	}
	    
	cob_BranchCd.Enable = false;    
	cob_Branc_OnChanged();
	
	grd_GovRoom.NoDataText="조회결과가 없습니다.";
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Search()
//  설명: 정압실/정압기등록 화면 리스트를 조회 하는 함수
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Search()
{	
    // if (cob_Branc.value == "" || cob_Branc.value == null) {
         // alert("지사를 선택하십시오.");
         // cob_Branc.SetFocus();
         // return;
    // }
    
    edt_Area.Text = "";
	ds_GovRoom.ClearData();
	ds_GovRoomDetail.ClearData();
	ds_GovDetail.ClearData();
	ds_GovList.ClearData();
	
    var ServiceId     = "select1";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_FacilityInfoMgr-service";
    var InputDsList   = "";
    var OutputDsList  = "ds_GovRoom=ds_GovRoom";
    var Argument      = "select1=1";
        Argument     += " BRANCH_CD=" + quote(cob_Branc.Value);
        Argument     += " Zip1=" + quote(cob_ZipNo1.Value );
        Argument     += " TEAM_CD=" + quote(cob_Team.Value);
        Argument     += " FACI_NUM="+quote(edt_FaciNumSearch.Text );
        Argument     += " S_FACI_NUM="+quote(edt_scada_num.Text);
        Argument     += " BLOCK_CD=" + quote(cob_PBlockCd.Value);

    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);

    st_Reg.Text = ds_GovRoom.GetRowCount();
    
    fv_faciNum = "";
    fv_govNum = "";
    edtFileList.text = "";
    edtFileList1.text = "";
    edtFileList2.text = "";
    edtFileList3.text = "";
    
    grd_GovRoom_OnCellClick();        
}

//------------------------------------------------------------------------------- 
// 함수명: grd_GovRoom_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
//   설명: 정압실/정압기등록 상세내용를 조회 하는 fn_D1_DetailSearch()을 부르는 이벤트용 함수
//         그리드 클릭시 이벤트 처리
// arguments   
//          obj: Object 이 Event가 발생된 Component.
//          nRow: Integer Event가 발생한 Cell의 Row Index.
//          nCell: Integer Event가 발생한 Cell의 Column Index.
//          nX: Integer Grid 내에서 Click 된 마우스 포인트의 X 좌표.
//          nY Integer Grid 내에서 Click 된 마우스 포인트의 Y 좌표.
//          nPivotIndex CrossTabGrid에서 Event가 발생한 Cell의 Pivot Index.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function grd_GovRoom_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if (nCell == 15) {
        var args = "BUNGI='04'";
	    args += " FACI_NUM=" + ds_GovRoom.GetColumn(nRow, "FACI_NUM");
	
		Dialog("D10::D1402060011U.xml", args, 1208, 840, "Resize=true Scroll=true" , -1, -1);
    } else {
    
		ds_GovRoomDetail.ClearData();
		ds_GovDetail.ClearData();
		
		if (IsExistVar("PORTLET_FLAG", true)) {
			if (IsExistVar("FACI_NUM", true)) {
				fv_faciNum = FACI_NUM;
				cob_Branc.Enable = false;
				edt_FaciNumSearch.Enable = false;
				btn_WorkNum.Enable = false;
				Button3.Enable = false;
				btn_DZip.Enable = false;
				Button23.Enable = false;
				btn_Add.Enable = false;
				btn_Save.Enable = false;
				btn_GovDetail.Enable = false;
				btn_GovWorkNum.Enable = false;
				Button5.Enable = false;
			}	
			
		} else if (IsExistVar("S_FLAG", true)) {
			if (S_FLAG == "SCADA") {
				fv_faciNum = S_FACI_NUM;
				cob_Branc.Enable = false;
				edt_FaciNumSearch.Enable = false;
				btn_WorkNum.Enable = false;
				Button3.Enable = false;
				btn_DZip.Enable = false;
				Button23.Enable = false;
				btn_Add.Enable = false;
				btn_Save.Enable = false;
				btn_GovDetail.Enable = false;
				btn_GovWorkNum.Enable = false;
				Button5.Enable = false;
			}
		} else {
			fv_faciNum = ds_GovRoom.getcolumn(grd_GovRoom.CurrentRow, "FACI_NUM");
		}	
		
		fn_D1_DetailSearch(fv_faciNum);
		fn_D1_FileListNm(fv_faciNum);
		fn_D1_FileListNmImg(fv_faciNum);
		ds_GovList.ClearData();
		gov_List_Search(fv_faciNum);
		cob_BranchCd_OnChanged();
		edtFileList1.text = "";
		edtFileList3.text = "";
		fv_saveMode = "updateGovRoom";
		
		edt_PipeDiam1_OnCharChanged();
		
		cob_GovNum.Index = 0;
		G_GOV_NUM = cob_GovNum.Value;
		cob_GovNum_OnChanged();
	}
	
	// 장표
	if(nCell == 14){
		btn_MultiReport_OnClick();
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_DetailSearch(FACI_NUM)
//   설명: 정압실/정압기등록  상세내용를 조회 하는 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_DetailSearch(FACI_NUM)
{	
	ds_GovRoomDetail.ClearData();
		
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var ServiceId     = "select1Detail1";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_FacilityInfoMgr-service";
    var InputDsList   = "";
    var OutputDsList  = "ds_GovRoomDetail=ds_GovRoomDetail";
    var Argument      = "select1Detail1=1";
        Argument     += " FACI_NUM=" + quote(FACI_NUM); 
    var CallBack      = "fn_D1_CallBack";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);    
	
	var str_LeaseCond = cob_LeaseCond.Value;
	var str_SiteClass = cob_SiteClass.Value;

	cob_LeaseClass_OnChanged();	
	
	cob_LeaseCond.Value = str_LeaseCond;
	cob_SiteClass.Value = str_SiteClass;
	
	edt_Width_OnCharChanged();
}

//------------------------------------------------------------------------------- 
// 함수명: gov_List_Search()
//  설명: 정압기리스트를 조회 하는 함수
// arguments   
//          FACI_NUM  : 정압실 시설물 번호
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function gov_List_Search(FACI_NUM)
{	
    var ServiceId     = "selectGovList";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+FACI_NUM;
    var InputDsList     = "";
    var OutputDsList = "ds_GovList=ds_GovList";
    var Argument      = "selectGovList";
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    edt_GovCount.Value = ds_GovList.RowCount();
}

//------------------------------------------------------------------------------- 
// 함수명: GovDetailSearch(FACI_NUM)
//   설명: 정압기 상세내용를 조회 하는 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function GovDetailSearch(GOV_NUM)
{	
	ds_GovDetail.ClearData();
	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var ServiceId     = "select1Detail2";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+fv_faciNum+"&GOV_NUM="+GOV_NUM;
    var InputDsList     = "";
    var OutputDsList = "ds_GovDetail=ds_GovDetail";
    var Argument      = "select1Detail2";
    var CallBack     = "fn_D1_CallBack";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
}

//------------------------------------------------------------------------------- 
// 함수명: cob_GovNum_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
//   설명: 정압기 상세내용를 조회 하는 함수
// arguments   
//			obj       : obj  
//          strCode      : SstrCode
//          strText      : strText 
//          nOldIndex     : nOldIndex
//          nNewIndex    : nNewIndex
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function cob_GovNum_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
    if (strCode == "" || strCode == null) {
        strCode = G_GOV_NUM;
    }
        
    GovDetailSearch(strCode);
	fv_govNum = strCode;
	fn_D1_FileSecondListNm();
	fn_D1_FileSecondListNmImg();
	fv_saveMode     = "updateGov";
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonAddRow()
//   설명: 정압실/정압기등록 내용를 신규 추가 하는 모드로 변경 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonAddRow()
{	
	fv_faciNum = '';

    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
        alert("지사를 선택해주세요.");
        cob_BranchCd.Value = "";
        cob_Gu.Value = '';
        return;
    }
        
    edtFileList1.Text = "";
    edtFileList3.Text = "";
	ds_GovRoomDetail.ClearData();
	ds_GovDetail.ClearData();
	ds_GovList.ClearData();
	ds_GovRoomDetail.AddRow();
	cob_BranchCd.Value = cob_Branc.Value;   
	cob_BranchCd_OnChanged();
	cob_Gu.Value = cob_ZipNo1.Value;
    //GetFaciNum();
    edtFileList.Text = "";
    edtFileList2.Text = "";
    Edit31.Text = "";
    fv_saveMode     = "insertGovRoom";
    
    //-----------------------------------------
	//  콤보박스의 초기 설정
	//-----------------------------------------
	edt_Area.Text = "";
	
	cob_FaciStsCd.Index = 1;
}

//------------------------------------------------------------------------------- 
// 함수명: btn_Com01_OnClick(obj)
//   설명: 정압기등록 신규 추가 하는 모드로 변경 하는 함수
// arguments 
//          obj : obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function btn_Com01_OnClick(obj)
{
    if (edt_FaciNum.Text == "" || edt_FaciNum.Text == null) {
        alert("정압실을 선택해 주십시요.");
		return false;
    }
    
    edtFileList1.Text = "";
    edtFileList3.Text = "";
	ds_GovDetail.ClearData();
    ds_GovDetail.AddRow();   
    //GetGovNum();	
    fv_saveMode     = "insertGov";
}


//------------------------------------------------------------------------------- 
// 함수명: fn_CommonSave()
//   설명: 정압실/정압기등록 상세 내용를 저장 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSave()
{
	
	// 20090810 
	if(edt_GovRoomNm.Text == "")
	{
		alert("정압실명을 입력해주세요.");
		edt_GovRoomNm.SetFocus();
		return;
	}
	
	/*
	if(edt_WorkNum.Text == "")
	{
		alert("공사번호를 선택해주세요.");
		edt_WorkNum.SetFocus();
		return;
	}
	*/
	
	//20090810 
	if(cob_GovRoomType.Text == "")
	{
		alert("정압실형태를 선택해주세요.");
		cob_GovRoomType.SetFocus();
		return;
	}
	
	//20090810 
	if(cob_GovRoomMaterFlag.Text == "")
	{
		alert("정압실재질을 선택해주세요.");
		cob_GovRoomMaterFlag.SetFocus();
		return;
	}
	
	/*if(edt_WorkFirmCd.Text == "")
	{
		alert("시공회사를 선택해주세요.");
		edt_WorkFirmCd.SetFocus();
		return;
	}
	*/
    if(edt_lot_num_union.Text == "")
	{
		alert("주소를 선택해주세요.");
		edt_lot_num_union.SetFocus();
		return;
	}

	// 20090810 
	if(cal_InstYmd.Value == "")
	{
		alert("설치일자를 선택해주세요.");
		cal_InstYmd.SetFocus();
		return;
	}

	if(cob_LeaseStsCd.Text == "")
	{
		alert("점용상태를 선택해주세요.");
		cob_LeaseStsCd.SetFocus();
		return;
	}
	
	var result = cob_LeaseClass.Value;
	//		alert(result);
	//		alert(cob_LeaseCond.Value);
	if(result == '10' || result == '20'){  // 사유지와 특수부지는 점용조건이 존재 : 필수
		// 자사부지와 학교부지는 점용조건이 존재하지 않음 : 선택
		
		if(cob_LeaseCond.Value == "" or cob_LeaseCond.Value == null){
			alert("점용조건을 선택해주세요.");
			cob_LeaseCond.SetFocus();
			return;
		//else
			//alert("aaaa");
		}
	}
	
	if(edt_Width.Text == "")
	{
		alert("가로를 입력해주세요.");
		edt_Width.SetFocus();
		return;
	}
	
	if(edt_Vertical.Text == "")
	{
		alert("세로를 입력해주세요.");
		edt_Vertical.SetFocus();
		return;
	}
	
	if(edt_Height.Text == "")
	{
		alert("높이를 입력해주세요.");
		edt_Height.SetFocus();
		return;
	}
	
	// 20090810 
	if(cob_LeaseClass.Text == "")
	{
		alert("점용료분류를 선택해주세요.");
		cob_LeaseClass.SetFocus();
		return;
	}
	
	// 20090810 
	if(cob_SiteClass.Text == "")
	{
		alert("분지분류를 선택해주세요.");
		cob_SiteClass.SetFocus();
		return;
	}
	
	// 20090810 
	if(cob_sound.Text == "")
	{
		alert("정압실방음형태를 선택해주세요.");
		cob_sound.SetFocus();
		return;
	}
	
	if(cob_BoundaryFenceYn.Text == "")
	{
		alert("경계책유무를 선택해주세요.");
		cob_BoundaryFenceYn.SetFocus();
		return;
	}	
	
	//20090810 
	if(cob_DoorType.Text == "")
	{
		alert("출입문형태를 선택해주세요.");
		cob_DoorType.SetFocus();
		return;
	}
	
	//20090810 
	//선택으로 변경 권기철 20091007
	//if(Edit2.Text == "")
	//{
	//	alert("법적소음기준을 입력해주세요.");
	//	Edit2.SetFocus();
	//	return;
	//}
	
	//20090810 
	//if(Edit3.Text == "")
	//{
	//	alert("보정치를 입력해주세요.");
	//	Edit3.SetFocus();
	//	return;
	//}
	
	fv_Kind = "GR";			// 이페이지는 정압실과 정압기를 둘다 저장 수정 해줘야 하는 페이지 임 
	
	//-------------------------------------------------------------------------------
	//GetFaciNum() : 채번 ds_GetNum으로 시설물 번호를 넣음 
	//-------------------------------------------------------------------------------
	
	var kind = "";
	if(TRIM(edt_FaciNum.Text)!=null ){
		fv_saveMode = "updateGovRoom";
    }
    if(fv_saveMode=="insertGovRoom"){
        GetFaciNum();
		ds_GovRoomDetail.AddColumn("CRT_EMPID", "STRING", 256);
		ds_GovRoomDetail.AddColumn("CRT_IP", "STRING", 256);
		ds_GovRoomDetail.AddColumn("FACI_STS_CD", "STRING", 256);
		ds_GovRoomDetail.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_GovRoomDetail.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		//ds_GovRoomDetail.SetColumn(0, "FACI_STS_CD", fv_Kind);
		//ds_GovRoomDetail.SetColumn(0, "FACI_STS_CD", cob_FaciStsCd.Value);
		ds_GovRoomDetail.SetColumn(0, "FACI_NUM", ds_GetNum.GetColumn(0,"FACI_NUM"));
		kind = ds_GetNum.GetColumn(0,"FACI_NUM");
    }else if(fv_saveMode=="updateGovRoom"){
		ds_GovRoomDetail.AddColumn("UPD_EMPID", "STRING", 256);
		ds_GovRoomDetail.AddColumn("UPD_IP", "STRING", 256);
		ds_GovRoomDetail.AddColumn("FACI_STS_CD", "STRING", 256);
		ds_GovRoomDetail.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_GovRoomDetail.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		//ds_GovRoomDetail.SetColumn(0, "FACI_STS_CD", cob_FaciStsCd.Value);
	//	ds_GovRoomDetail.SetColumn(0, "FACI_NUM", fv_faciNum);
		kind = fv_faciNum;
    }
    
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------

	var Transition   = fv_saveMode;
    var ServicePart  = "D10";
    var Url          = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service";
    var InputDsList  = "ds_GovRoomDetail=ds_GovRoomDetail:u";
    var OutputDsList = "ds_GovRoomDetail=ds_GovRoomDetail";
    var Argument     = fv_saveMode;
    
    gfn_SyncCall(Transition, 
                 ServicePart, 
                 Url, 
                 InputDsList, 
                 OutputDsList, 
                 Argument);
                 
	if(ErrorCode<0)
	{
		alert(gfn_B3_GetConstraintErrorMsg(Transition, ErrorMsg,"저장"));		
		return false;
		
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	}	
	
	if(fv_saveMode=="insertGovRoom")
	{
		// 팀-전체
		cob_Team.Value = "";     // 전체
		cob_PBlockCd.Value = ""; // 전체
	}
		
	fn_D1_Search();
	//fn_D1_DetailSearch(kind);
}

