//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
//   설명: 정압실/정압기등록를 삭제 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonDeleteRow()
{	
    if(!Confirm("삭제하시겠습니까?"))	//"삭제하시겠습니까?" 
     {
        return false;
     }
        
    if(ds_GovRoom.GetRowCount()==0){ 
        alert("삭제하실 내역이 없습니다.");	//"삭제하실 내역이 없습니다."
		return;
    }
    
    var str_FaciNum  = ds_GovRoom.GetColumn(ds_GovRoom.row, "FACI_NUM");
        
	fn_D1_FileDelete(str_FaciNum, "", "GOVROOM");
	
    ds_GovRoom.DeleteRow(ds_GovRoom.row);    
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition   = "deleteGovRoom";
    var ServicePart  = "D10";
    var Url          = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service&FACI_NUM="+fv_faciNum;
    
    var InputDsList  = "ds_GovRoom=ds_GovRoom:u";
    var OutputDsList = "ds_GovRoom=ds_GovRoom";
    var Argument     = "deleteGovRoom";
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
	if(ErrorCode<0)
	{
	    if(ErrorMsg!=""){
	       return;
	    }
		
		ds_GovRoom.Reset();
		return false;
	}
		
    fn_D1_Search();
}

//------------------------------------------------------------------------------- 
// 함수명: btn_Delete_OnClick(obj)
//   설명: 정압기등록를 삭제 하는 함수
// arguments   
//          obj : obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function btn_Delete_OnClick(obj)
{
	
	if (edt_GovNum.Text == "" || edt_GovNum.Text == null) {
        alert("정압기를 선택해 주십시요.");
		return false;
    }
    
	if(!Confirm("삭제하시겠습니까?"))	//"삭제하시겠습니까?" 
     {
        return false;
     };
        
    if(ds_GovDetail.GetRowCount()==0){ 
        alert("삭제하실 내역이 없습니다.");	//"삭제하실 내역이 없습니다."
		return;
    }
    
    var str_FaciNum   = ds_GovDetail.GetColumn(ds_GovDetail.row, "GR_FACI_NUM");
	var str_DetailNum = ds_GovDetail.GetColumn(ds_GovDetail.row, "FACI_NUM"   );
	
    fn_D1_FileDelete(str_FaciNum, str_DetailNum, "GOV");
    
	ds_GovDetail.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
	ds_GovDetail.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS")); 
	              
    ds_GovDetail.DeleteRow(ds_GovDetail.row);
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition    = "deleteGov";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName="
					  + "D1_FacilityInfoMgr-service";
					  //&FACI_NUM=" + cob_GovNum.Value;
    var InputDsList   = "ds_GovDetail=ds_GovDetail:u";
    var OutputDsList  = "ds_GovDetail=ds_GovDetail";
    var Argument      = "deleteGov=1";
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
	if(ErrorCode<0){
	
		ds_GovDetail.Reset();
		return false;
	}
		
	grd_GovRoom_OnCellClick("",ds_GovRoom.row,"","","","");
}

function fn_D1_FileDelete(str_FACI_NUM, str_DETAIL_NUM, str_Flag){
  	    
	ds_FaciFile.ClearData();
	ds_FaciFile.AddRow();
	
	if(str_Flag == 'GOV'){
		ds_FaciFile.SetColumnNF(0, "FACI_NUM"  , str_FACI_NUM  );
		ds_FaciFile.SetColumnNF(0, "DETAIL_NUM", str_DETAIL_NUM);
	}else if(str_Flag == 'GOVROOM'){
		ds_FaciFile.SetColumnNF(0, "FACI_NUM"  , str_FACI_NUM  );
	}
	
	ds_FaciFile.UpdateControl = false;
	ds_FaciFile.SetRowType(0, "delete");
	
	// 첨부파일 삭제
	var Transition    = "deleteGov";
	var ServicePart   = "D10";
	var Url           = "handle.do?ServiceName=D1_FacilityFileInfoMgr-service";    
	var InputDsList   = "ds_FaciFile=ds_FaciFile:u";
	var OutputDsList  = "";
	var Argument      = "";
	
	if(str_Flag == 'GOV'){
	
		Argument = "deleteFaciFile1";
	
	}else if(str_Flag == 'GOVROOM'){
	
		Argument = "deleteFaciFile";
	}
	
	gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
}    

//------------------------------------------------------------------------------- 
// 함수명: GetFaciNum()
//   설명: 채번 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function GetFaciNum()
{	
	ds_GetNum.ClearData();
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "selectNum";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service";
    var InputDsList     = "";
    var OutputDsList = "ds_GetNum=ds_GetNum";
    var Argument      = "selectGovRoomNum";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    var num =ParseInt(ds_GetNum.GetColumn(0,"FACI_NUM"))+1;
    
    var numAfter = "" ;
    var i = 0;
    for(;(i+Length(ToString(num)))<6;i++){
		numAfter = "0"+numAfter; 
    }
    numAfter = numAfter + num;    
    ds_GetNum.SetColumn(0,"FACI_NUM", "GR"+numAfter);
    fv_faciNum = "GR"+numAfter;
}

//------------------------------------------------------------------------------- 
// 함수명: GetGovNum()
//   설명: 채번 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function GetGovNum()
{	
	ds_GetGovNum.ClearData();
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "selectNum";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service"+"&FACI_NUM="+fv_faciNum;
    var InputDsList     = "";
    var OutputDsList = "ds_GetGovNum=ds_GetGovNum";
    var Argument      = "selectGovNum";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);

    ds_GetGovNum.SetColumn(0,"FACI_NUM", "GV" + "0" + (parseInt(ds_GetGovNum.GetColumn(0, "FACI_NUM")) + 1));
    fv_govNum = ds_GetGovNum.GetColumn(0,"FACI_NUM");
}

