function cal_InstYmd_OnChanged(obj,strOldText,strNewText) {

	if (cal_InstYmd.Value > today()) {
	
		alert("정압기 설치일자가 오늘 일자 보다 큽니다. ");
		
		cal_InstYmd.Value = "";
	    cal_InstYmd.SetFocus();
	    
		return false;
	}
}

function cal_InstYmdGov_OnChanged(obj,strOldText,strNewText) {

    fn_DateCheckToday(cal_InstYmdGov);
    
    if (cal_InstYmd.Value > cal_InstYmdGov.Value) {
	
	    cal_InstYmdGov.value = "";
	    alert("정압기 설치일은 정압실 설치일보다 커야합니다.");
	    return;
	    break;
	}
}

function cal_SelfInspectYmd_OnChanged(obj,strOldText,strNewText)
{
	if (cal_SelfInspectYmd.Value < cal_InstYmdGov.Value) {
	
	    cal_SelfInspectYmd.value = "";
	    alert("자율검사일은 설치일자보다 커야합니다.");
	    return;
	    break;
	}
}

function cal_RunningYmd_OnChanged(obj,strOldText,strNewText)
{
	fn_DateCheckToday(cal_RunningYmd);
	
	if (cal_RunningYmd.Value < cal_InstYmdGov.Value) {
	
	    cal_RunningYmd.value = "";
	    alert("가동일자는 설치일자보다 커야합니다.");
	    return;
	    break;
	}
}

function fn_D1_ExcelExport()
{	
    ds_Search.ClearData();
	var row = ds_Search.AddRow();
	ds_Search.SetColumn(row,"BRANCH_CD",cob_Branc.Value);
	ds_Search.SetColumn(row,"TEAM_CD",cob_Team.Value);
	ds_Search.SetColumn(row,"BLOCK_CD",cob_PBlockCd.Value);
	ds_Search.SetColumn(row,"ZIP_NO1",cob_ZipNo1.Value);
	ds_Search.SetColumn(row,"FACI_NM",edt_FaciNumSearch.Text);
	ds_Search.SetColumn(row,"FACI_NUM",edt_scada_num.Text);
	
	ds_excel.ClearData();
	
    var strSvcID        = "Search_Gv";
	var strPart         = "D10";	             
	var strURL          = "handle.do?ServiceName=D1_FacilityInfoMgr_Excel-service";
	var strInDatasets   = "ds_Search=ds_Search";
	var strOutDatasets  = "ds_excel=ds_excel";
	var strArgument     = "Search_Gv=1";
	
	
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );			   
    
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branc.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo1.Text," ",20);
    SearchCondition = SearchCondition + "팀 :" + RPad(cob_Team.Text," ",20);
    SearchCondition = SearchCondition + "구간 :" + RPad(cob_PBlockCd.Text," ",20);    
    SearchCondition = SearchCondition + "정압실번호 :" + RPad(edt_FaciNumSearch.Text," ",20);
	gfn_ExportExcel("정압실 조회 ", 5, SearchCondition, "grd_excel"); 
}

function D1101010010F_T01_OnUnloadCompleted(obj) {

	if (ds_GovRoomDetail.GetUpdate() || ds_GovDetail.GetUpdate()) {
		if (!confirm(gfn_GetMessage("M000016","%AAA%","변경된 내용이 있습니다.종료"))) {
			return false;
		}
	}
}

//-------------------------------------------------------------------------------
// 함수명: btn_MultiReport_OnClick()
// 설명: 공통 레포트 팝업 함수를 호출한다(멀티 보고서), 멀티 보고서 파라미터를 전달을
//       위한 dataset을 사용한다
// input parameter: 
// output parameter: 
// return 내용: 없음
//-------------------------------------------------------------------------------
function btn_MultiReport_OnClick(obj)
{
	ds_ReportList.ClearData();	
		
	var nCount = ds_GovList.GetRowCount();
	
	if(nCount == '0'){
		alert("시설물 카드로 출력할 정압기 정보가 없습니다. ");
		return;
	}
		
	// 함수 호출용 파라미터를 만든다
	var strTitle     ;				// 출력물 제목
    var strPart      ;         		// 시스템 명 
	var strReport    ; 				// 출력물 ID 
	var strParameter ;      		// 파라미터구문 (단 ',' 로구분할것 )	

		for(var i=0; i<nCount; i++){
		
			//행추가
			ds_ReportList.addRow();
			
			// 함수 호출용 파라미터를 설정한다
			strTitle 	 ="정압기 시설물 카드 조회"; 
			strPart 	 ="D10";           
			strReport    ="D1202010037P"; 
			strParameter  ="FACI_NUM="    + ds_GovRoom.GetColumn(ds_GovRoom.row, "FACI_NUM")
					     +",GOV_NUM="    + ds_GovList.GetColumn(i, "GOV_NUM")
						 ;
							 			
			ds_ReportList.SetColumn(i, "SYSTEM_ID"   ,strPart     ); // 시스템 명 
			ds_ReportList.SetColumn(i, "REPORT_ID"   ,strReport   ); // 출력물 ID 
			ds_ReportList.SetColumn(i, "REPORT_ARGS" ,strParameter); // 파라미터구문 
			ds_ReportList.SetColumn(i, "REPORT_TITLE",strTitle    ); // 출력물 제목
		}
	
	// 데이터셋 생성후 OZ 멀티 출력 함수 호출, concatpage 사용여부를 true, false로 결정한다(공백("")이라도 넣어야 한다)
	gfn_MultiReportPopup("정압기 시설물 카드 조회",ds_ReportList, "true");
}

function cob_LeaseClass_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var str_LeaseClass = ds_GovRoomDetail.GetColumn(ds_GovRoomDetail.row, "LEASE_CLASS");
	
	gfn_SetComboBox_RComcode('D10197', str_LeaseClass, 'D10013','Dataset0') ;
	gfn_SetComboBox_RComcode('D10197', str_LeaseClass, 'D10202','Dataset1') ;
	
	var result = cob_LeaseClass.Value;
			
	var nRow = Dataset0.AppendRow();
	Dataset0.SetColumn(nRow, "", "");
	Dataset0.SetColumn(nRow, "", "");
	Dataset0.SetColumn(nRow, "", "");
	
	var nRow1 = Dataset1.AppendRow();
	Dataset1.SetColumn(nRow1, "", "");
	Dataset1.SetColumn(nRow1, "", "");
	Dataset1.SetColumn(nRow1, "", "");
	
	cob_LeaseCond.Index = 0;	
	cob_SiteClass.Index = 0;
	
	if(result == '10' || result == '20'
	){
		Static113.Color = 'blue';
		cob_LeaseCond.Enable = true;
	}else{
		Static113.Color = 'dimgray';
		cob_LeaseCond.Enable = false;
	}
}

//---------------------------------------------------------------------------------
// 기      능   :  지사에 따른 구에 따른 팀에 따른 구간코드를 불러오기 위해
//---------------------------------------------------------------------------------
function cob_PTeamCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_Search.ClearData();
	ds_Search.AddRow();
	
	ds_Search.SetColumn(0, "BRANCH_CD" , cob_Branc.Value);
	ds_Search.SetColumn(0, "TEAM_CD", cob_Team.Value);

	var strSvcID = "find_block";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_FacilityInfoMgr-service";
	var strInDatasets  = "ds_Search=ds_Search";
	var strOutDatasets  = "ds_Block=ds_Block";
	var strArgument  = "find_block=1";
				
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );					 	           
    
    if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	cob_PBlockCd.Index = 0;
	
	if(ds_Block.GetRowCount() == 0){
		cob_PBlockCd.Enable = false;
	}else{
		cob_PBlockCd.Enable = true;
		// 전체추가
		var str_nRow = ds_Block.AppendRow();
		ds_Block.SetColumn(str_nRow, "BLOCK_CD", ""    );
		ds_Block.SetColumn(str_nRow, "BLOCK_NM", "전체");
	}
}
