//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
//   설명: 정류기등록를 삭제 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonDeleteRow()
{
	
    if(!Confirm(gfn_GetMessage("M000016","%AAA%","삭제")))	//"삭제하시겠습니까?" 
     {
        return false;
     };
        
    if(ds_Rectifier.GetRowCount()==0){ 
        alert(gfn_GetMessage("M000008","%AAA%","삭제대상"));	//"삭제하실 내역이 없습니다."
		return;
    }
    
    var str_FaciNum = ds_Rectifier.GetColumn(ds_Rectifier.row, "FACI_NUM");
	fn_D1_FileDelete(str_FaciNum);    
        
    ds_Rectifier.DeleteRow(ds_Rectifier.row);	
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "deleteRec";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service&FACI_NUM="+fv_faciNum;
    var InputDsList     = "ds_Rectifier=ds_Rectifier:u";
    var OutputDsList = "ds_Rectifier=ds_Rectifier";
    var Argument      = "deleteRec";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    
	if(ErrorCode<0){
	    if(ErrorMsg!=""){
	       alert(ErrorMsg);
	    } else {   
	       alert(gfn_GetMessage("M000014","%AAA%","삭제"));	//"삭제중 오류가 발생했습니다."
	    }
		ds_Rectifier.Reset();
		return false;
	}
    fn_CommonSearch();
}

function fn_D1_FileDelete(str_FACI_NUM){
  	    
	ds_FaciFile.ClearData();
	ds_FaciFile.AddRow();
	
	ds_FaciFile.SetColumn(0, "FACI_NUM", str_FACI_NUM);
	
	ds_FaciFile.UpdateControl = false;
	ds_FaciFile.SetRowType(0, "delete");
	
	// 첨부파일 삭제
	var Transition    = "deleteGov";
	var ServicePart   = "D10";
	var Url           = "handle.do?ServiceName=D1_FacilityFileInfoMgr-service";    
	var InputDsList   = "ds_FaciFile=ds_FaciFile:u";
	var OutputDsList  = "";
	var Argument      = "deleteFaciFile";
	
	gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
}  

//------------------------------------------------------------------------------- 
// 함수명: GetFaciNum()
//   설명: 채번 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function GetFaciNum()
{	
	var FaciNum = "";
	ds_GetNum.ClearData();
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "selectNum";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service";
    var InputDsList     = "";
    var OutputDsList = "ds_GetNum=ds_GetNum";
    var Argument      = "selectRectifierNum";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    var num =ParseInt(ds_GetNum.GetColumn(0,"FACI_NUM"))+1;
    
    var numAfter = "" ;
    var i = 0;
    for(;(i+Length(ToString(num)))<6;i++){
		numAfter = "0"+numAfter; 
    }
    numAfter = numAfter + num;    
    ds_GetNum.SetColumn(0,"FACI_NUM", fv_Kind+numAfter);
    fv_faciNum = ds_GetNum.GetColumn(0,"FACI_NUM");
}
