//-------------------------------------------------------------------------------
// 함수명: fn_ZipSearch()
//   설명: 우편번호 조회클릭시 우편번호 조회 popup화면 로딩
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_ZipSearch(obj)
{
    //if(fv_faciNum != "")
	//{
		var arrList = gfn_Dialog("A10", "A1101020010U", "", 1100, 600, true, -1, -1);
		
		if(arrList == 0){
			return;
		}
		
		if (arrList != "" && arrList != null) {
			
			if (cob_ZipNo1.Value != arrList[2]) {
				 alert("해당 시/구를 선택해주세요.");
				 return;
			}
			
			if(obj.id == "btn_DZip"){   
			
				edt_lot_num_union = arrList[17];  		 // 지번주소조합
				edt_new_addr_union = arrList[23]; 		 // 새주소조합
				edt_zip_seq = arrList[0];         		 // 우편일련번호     
				edt_hill = arrList[10];           		 // 산번지
				edt_addr_m = arrList[11];         		 // 본번
				edt_addr_s = arrList[12];         		 // 부번  
				edt_second_addr = arrList[13];    		 // 보조주소
				edt_bld_dong = arrList[14];       		 // 동
				edt_hosu = arrList[15];           		 // 호수
				// edt_new_addr_bld_seq = arrList[18]; 	 // 새주소일련번호
				// edt_new_addr_ungrnoud_yn = arrList[25];  // 새주소지하여부
				// edt_new_hosu = arrList[21];              // 새주소호수 
				edt_zip_no1 = arrList[2];                // 우편번호1
				edt_zip_no2 = arrList[3];                // 우편번호2
			}
		}	
		
		G_ACTIVEWIN = parent.id;
	//}	
}

// ===============================================================================
// 함수명: D1101010010F_T05_OnLoadCompleted()
//   설명: 화면 로드 후 조회 조건 기본 셋팅
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function D1101010010F_T05_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
    
    if (IsExistVar("PORTLET_FLAG", true)) {
	    if (PORTLET_FLAG == "p") {
			edt_FaciNumSearch.Text = FACI_NUM;
			fn_D1_Search();
			grd_Drain_OnCellClick();
	    }
	}
	//-----------------------------------------
	//  콤보박스의 내용을 셋팅한다.
	//-----------------------------------------
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branc"));
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BrancCd"));
	gfn_D1_NULL_SetComboBox_Comcode("D10039",gfn_setDsObject_Comcode("cob_FaciFlag"));
	gfn_D1_NULL_SetComboBox_Comcode("A10200",gfn_setDsObject_Comcode("cob_LeaseManageYn"));
	gfn_D1_NULL_SetComboBox_Comcode("B20023",gfn_setDsObject_Comcode("cob_LeaseFlag"));
	gfn_D1_NULL_SetComboBox_Comcode("A10200",gfn_setDsObject_Comcode("cob_ElectPotYn"));
	gfn_D1_NULL_SetComboBox_Comcode("D10063",gfn_setDsObject_Comcode("cob_UseFormalCapa"));
	gfn_D1_NULL_SetComboBox_Comcode("D10062",gfn_setDsObject_Comcode("cob_MakerNm"));
	gfn_D1_NULL_SetComboBox_Comcode("D20091",gfn_setDsObject_Comcode("cob_UseVolt"));
	gfn_D1_NULL_SetComboBox_Comcode("D10064",gfn_setDsObject_Comcode("cob_AcElect"));
	gfn_D1_NULL_SetComboBox_Comcode("D10186",gfn_setDsObject_Comcode("cob_FaciStsCd"));
	
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") {
	    cob_Branc.value = '';
	} else {
	    cob_Branc.Value = gfn_GetGlobalValue("G_BRANCH_CD");
	    cob_Branc.Enable = false;
	}
	
	cob_BrancCd.Enable = false;    
	
	cob_BranchCd_OnChanged();
	
	gfn_D1_SetGrid_Sort(grd_Drain);
}

/********************************************************************************
* 기      능   :  지사별 구를 조회하여 콤보박스에 셋팅
********************************************************************************/
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

    // 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", cob_Branc.value, fn_setDsObject_Comcode("1", "cob_ZipNo1"));
    
    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
    //    cob_ZipNo1.Enable = false;
    //권기철 20091008
		cob_ZipNo1.Value = "";
	//	cob_ZipNo1.Index = 0;
    } else {
		cob_ZipNo1.Value = "";
    //    cob_ZipNo1.Enable = true;
    //    cob_ZipNo1.Index  = 0;
    }
    
    gfn_D1_SetComboBox_Teamcd("ds_Team",cob_Branc);
    cob_Team.Value = "";
    
    cob_Team_OnChanged();
}    

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Search()
//  설명: 정류기등록 화면 리스트를 조회 하는 함수
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Search()
{	
    // if (cob_Branc.value == "" || cob_Branc.value == null) {
         // alert("지사를 선택하십시오.");
         // cob_Branc.SetFocus();
         // return;
    // }
    
    ds_DrainageDetail.ClearData();
    var ServiceId     = "select5";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_FacilityInfoMgr-service&BRANCH_CD=" + cob_Branc.Value
					  + "&Zip1="     + cob_ZipNo1.Value 
					  + "&DRAIN_NM=" + edt_DrainNm.Text
					  + "&TEAM_CD="  + cob_Team.Value
					  + "&BLOCK_CD=" + cob_PBlockCd.Value
					  ;
    var InputDsList   = "";
    var OutputDsList  = "ds_Drainage=ds_Drainage";
    var Argument      = "select5";
    var CallBack      = "fn_D1_CallBack";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ds_Drainage.GetRowCount()==0)
        grd_Drain.NoDataText="조회결과가 없습니다.";
    
    st_Reg.Text = ds_Drainage.GetRowCount();
    fv_faciNum = "";
    edtFileList.text = "";
    edtFileList2.text = "";
        
    grd_Drain_OnCellClick();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_DrainageDay
// 설명: 배류기 이력 조회
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_DrainageDay(FACI_NUM)
{	
    ds_DrainageDay.ClearData();
    
    var ServiceId     = "selectDrainageDay";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+edt_FaciNum.Text
					  ;
    var InputDsList   = "";
    var OutputDsList  = "ds_DrainageDay=ds_DrainageDay";
    var Argument      = "selectDrainageDay";
    var CallBack      = "fn_D1_CallBack";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ds_DrainageDay.GetRowCount()==0)
        grd_DraDay.NoDataText="조회결과가 없습니다.";
}

//------------------------------------------------------------------------------- 
// 함수명: Grid0_OnCellClick()
//   설명: 정류기등록 상세내용를 조회 하는 fn_D1_DetailSearch()을 부르는 이벤트용 함수
//         그리드 클릭시 이벤트 처리
// arguments   
//          obj: Object 이 Event가 발생된 Component.
//          nRow: Integer Event가 발생한 Cell의 Row Index.
//          nCell: Integer Event가 발생한 Cell의 Column Index.
//          nX: Integer Grid 내에서 Click 된 마우스 포인트의 X 좌표.
//          nY Integer Grid 내에서 Click 된 마우스 포인트의 Y 좌표.
//          nPivotIndex CrossTabGrid에서 Event가 발생한 Cell의 Pivot Index.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function grd_Drain_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if (nCell == 16) {
        var args = "BUNGI='12'";
	    args += " FACI_NUM=" + ds_Drainage.getcolumn(nRow, "FACI_NUM");
	
	    Dialog("D10::D1402060011U.xml", args, 1208, 840, "Resize=true Scroll=true", -1, -1);
    } else {
   
		ds_DrainageDetail.ClearData();
		
		if (IsExistVar("PORTLET_FLAG", true)) {
			if (IsExistVar("FACI_NUM", true)) {
				fv_faciNum = FACI_NUM;
				cob_Branc.Enable = false;
				Button2.Enable = false;
				Button3.Enable = false;
				btn_DZip.Enable = false;
				btn_Cathodic.Enable = false;
				Button23.Enable = false;
			}	
		} else {
		    fv_faciNum = ds_Drainage.getcolumn(grd_Drain.CurrentRow, "FACI_NUM");
		}
		fn_D1_DetailSearch(fv_faciNum);
		fn_D1_FileListNm(fv_faciNum);
		cob_BranchCd_OnChanged1();
		fn_D1_DrainageDay(fv_faciNum);
		fn_D1_FileListNmImg(fv_faciNum);
		fv_saveMode = "updateDrain";
	}
	
	if (nCell == 15) {
		fn_Card(nRow);
	}
}

