//------------------------------------------------------------------------------- 
// 함수명: fn_CommonAddRow()
//   설명: 정류기등록 내용를 신규 추가 하는 모드로 변경 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonAddRow()
{	
	ds_DrainageDay.Clear();
	fv_faciNum = "";

    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
        alert("지사를 선택해주세요.");
        cob_BrancCd.Value = "";
        cob_Gu.Value = "";
        return;
    }
    
    edtFileList.Text = "";
    edtFileList2.Text = "";
	ds_DrainageDetail.ClearData();
    ds_DrainageDetail.AddRow(); 
    cob_BrancCd.Value = cob_Branc.Value;  
    cob_BranchCd_OnChanged1();
    cob_Gu.Value = cob_ZipNo1.Value;
    //GetFaciNum();
    fv_saveMode     = "insertDrain";
    
    cob_FaciStsCd.Index = 1;
}

