		ds_DrainageDetail.AddColumn("CRT_IP", "STRING", 256);
		ds_DrainageDetail.AddColumn("FACI_FLAG", "STRING", 256);
		ds_DrainageDetail.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_DrainageDetail.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_DrainageDetail.SetColumn(0, "FACI_FLAG", fv_Kind);
		ds_DrainageDetail.SetColumn(0, "FACI_NUM", ds_GetNum.GetColumn(0,"FACI_NUM"));
		kind = ds_GetNum.GetColumn(0,"FACI_NUM");
    }else if(fv_saveMode=="updateDrain"){
		//배류기 등록정보를 수정할수 없게 함 20130417 송석환
		return -1;
		// ds_DrainageDetail.AddColumn("UPD_EMPID", "STRING", 256);
		// ds_DrainageDetail.AddColumn("UPD_IP", "STRING", 256);
		// ds_DrainageDetail.AddColumn("FACI_FLAG", "STRING", 256);
		// ds_DrainageDetail.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		// ds_DrainageDetail.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		// ds_DrainageDetail.SetColumn(0, "FACI_FLAG", fv_Kind);
		// kind = fv_faciNum;
    }
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
	if(ErrorCode<0)
	{
		return false;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	if(fv_saveMode=="insertDrain")
	{
		// 팀-전체
		cob_Team.Value = "";     // 전체
		cob_PBlockCd.Value = ""; // 전체
	}
		
	fn_D1_Search();
}


//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
//   설명: 정류기등록를 삭제 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonDeleteRow()
{
    if(!Confirm(gfn_GetMessage("M000016","%AAA%","삭제")))	//"삭제하시겠습니까?" 
     {
        return false;
     };
        
    if(ds_Drainage.GetRowCount()==0){ 
        alert(gfn_GetMessage("M000008","%AAA%","삭제대상"));	//"삭제하실 내역이 없습니다."
		return;
    }
      
    var str_FaciNum = ds_Drainage.GetColumn(ds_Drainage.row, "FACI_NUM");
	fn_D1_FileDelete(str_FaciNum);    
        
    ds_Drainage.DeleteRow(ds_Drainage.row);	
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition    = "deleteDrain";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service&FACI_NUM="+fv_faciNum;
    var InputDsList   = "ds_Drainage=ds_Drainage:u";
    var OutputDsList  = "ds_Drainage=ds_Drainage";
    var Argument      = "deleteDrain";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    
	if(ErrorCode<0){
	    if(ErrorMsg!=""){
	       alert(ErrorMsg);
	    } else {   
	       
	    }
		ds_Drainage.Reset();
		return false;
	}
    fn_CommonSearch();
}

