//------------------------------------------------------------------------------- 
// 함수명: fn_CommonAddRow()
//   설명: 수취기등록 내용를 신규 추가 하는 모드로 변경 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonAddRow()
{	
	fv_faciNum = "";

    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
        alert("지사를 선택해주세요.");
        cob_BranchCd.Value = "";
        cob_Gu.Value = "";
        return;
    }
    
    edtFileList.Text = "";
    edtFileList2.Text = "";
	ds_WaterDetail.ClearData();
    ds_WaterDetail.AddRow();  
    cob_BranchCd.Value = cob_Branc.Value;  
    cob_BranchCd_OnChanged1();
    cob_Gu.Value = cob_ZipNo1.Value;
    //GetFaciNum();
    fv_saveMode     = "insertWater";
    cob_FaciStsCd.Index = 1;
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonSave()
//   설명: 수취기등록 상세 내용를 저장 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSave()
{
	// if(edt_WorkNum.Text == "")
	// {
		// alert("공사번호를 선택해주세요.");
		// edt_WorkNum.SetFocus();
		// return;
	// }
	
	// if(cob_WorkFirmCd.Text == "")
	// {
		// alert("시공업체를 선택해주세요.");
		// cob_WorkFirmCd.SetFocus();
		// return;
	// }
	
	/* 20090810
	*/
	if(cal_InstYmd.Value == "")
	{
		alert("설치일자를 선택해주세요.");
		cal_InstYmd.SetFocus();
		return;
	}
	/*
	if(cob_MainPipeDiamCd.Text == "")
	{
		alert("원관관경을 선택해주세요.");
		cob_MainPipeDiamCd.SetFocus();
		return;
	}
	
	if(cob_PavementKind.Text == "")
	{
		alert("포장구분을 선택해주세요.");
		cob_PavementKind.SetFocus();
		return;
	}
	
	if(cob_RoadFlag.Text == "")
	{
		alert("시설(도로)구분을 선택해주세요.");
		cob_RoadFlag.SetFocus();
		return;
	}
	*/
	
	if(edt_lot_num_union.Text == "")
	{
		alert("주소를 선택해주세요.");
		edt_lot_num_union.SetFocus();
		return;
	}
	
	var kind = "";
	//-------------------------------------------------------------------------------
	//GetFaciNum() : 채번 ds_GetNum으로 시설물 번호를 넣음 
	//-------------------------------------------------------------------------------
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	var Transition     = fv_saveMode;
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service";
    var InputDsList     = "ds_WaterDetail=ds_WaterDetail:u";
    var OutputDsList = "ds_WaterDetail=ds_WaterDetail";
    var Argument      = fv_saveMode;
    
    if(fv_saveMode=="insertWater"){
        GetFaciNum();
		ds_WaterDetail.AddColumn("CRT_EMPID", "STRING", 256);
		ds_WaterDetail.AddColumn("CRT_IP", "STRING", 256);
		ds_WaterDetail.AddColumn("FACI_FLAG", "STRING", 256);
		ds_WaterDetail.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_WaterDetail.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_WaterDetail.SetColumn(0, "FACI_FLAG", fv_Kind);
		ds_WaterDetail.SetColumn(0, "FACI_NUM", ds_GetNum.GetColumn(0,"FACI_NUM"));
		kind = ds_GetNum.GetColumn(0,"FACI_NUM");
    }else if(fv_saveMode=="updateWater"){
		ds_WaterDetail.AddColumn("UPD_EMPID", "STRING", 256);
		ds_WaterDetail.AddColumn("UPD_IP", "STRING", 256);
		ds_WaterDetail.AddColumn("FACI_FLAG", "STRING", 256);
		ds_WaterDetail.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_WaterDetail.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_WaterDetail.SetColumn(0, "FACI_FLAG", fv_Kind);
		kind = fv_faciNum;
    }
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
	if(ErrorCode<0)
	{
		return false;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	if(fv_saveMode=="insertWater")
	{
		// 팀-전체
		cob_Team.Value = "";     // 전체
		cob_PBlockCd.Value = ""; // 전체
	}
		
	fn_D1_Search();
}


//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
//   설명: 수취기등록를 삭제 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonDeleteRow()
{
	
    if(!Confirm(gfn_GetMessage("M000016","%AAA%","삭제")))	//"삭제하시겠습니까?" 
     {
        return false;
     };
        
    if(ds_Water.GetRowCount()==0){ 
        alert(gfn_GetMessage("M000008","%AAA%","삭제대상"));	//"삭제하실 내역이 없습니다."
		return;
    }
    
        
    var str_FaciNum = ds_Water.GetColumn(ds_Water.row, "FACI_NUM");
	fn_D1_FileDelete(str_FaciNum);    
    
    ds_Water.DeleteRow(ds_Water.row);	
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "deleteWater";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service&FACI_NUM="+fv_faciNum;
    var InputDsList     = "ds_Water=ds_Water:u";
    var OutputDsList = "ds_Water=ds_Water";
    var Argument      = "deleteWater";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    
	if(ErrorCode<0){
	    if(ErrorMsg!=""){
	       alert(ErrorMsg);
	    } else {   
	       
	    }
		ds_Water.Reset();
		return false;
	}
    fn_CommonSearch();
}

function fn_D1_FileDelete(str_FACI_NUM){
  	    
	ds_FaciFile.ClearData();
	ds_FaciFile.AddRow();
	
	ds_FaciFile.SetColumn(0, "FACI_NUM", str_FACI_NUM);
	
	ds_FaciFile.UpdateControl = false;
	ds_FaciFile.SetRowType(0, "delete");
	
	// 첨부파일 삭제
	var Transition    = "deleteGov";
	var ServicePart   = "D10";
	var Url           = "handle.do?ServiceName=D1_FacilityFileInfoMgr-service";    
	var InputDsList   = "ds_FaciFile=ds_FaciFile:u";
	var OutputDsList  = "";
	var Argument      = "deleteFaciFile";
	
	gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
} 

//------------------------------------------------------------------------------- 
// 함수명: GetFaciNum()
//   설명: 채번 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function GetFaciNum()
{	
	var FaciNum = "";
	ds_GetNum.ClearData();
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "selectNum";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service";
    var InputDsList     = "";
    var OutputDsList = "ds_GetNum=ds_GetNum";
    var Argument      = "selectWaterNum";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    var num =ParseInt(ds_GetNum.GetColumn(0,"FACI_NUM"))+1;
    
    var numAfter = "" ;
    var i = 0;
    for(;(i+Length(ToString(num)))<6;i++){
		numAfter = "0"+numAfter; 
    }
    numAfter = numAfter + num;    
    ds_GetNum.SetColumn(0,"FACI_NUM", fv_Kind+numAfter);
    fv_faciNum = ds_GetNum.GetColumn(0,"FACI_NUM");
}



//-------------------------------------------------------------------------------
// 함수명: FACILITY_INFORMATION_SEARCH(obj)
// 설명: --
// arguments
// 			obj: form object: 설명
// return: 없음
//------------------------------------------------------------------------------- 
function FACILITY_INFORMATION_SEARCH(obj)
{	
	//if(fv_faciNum != "")
	//{
		var args = "fa_BranchCd="     + ds_WaterDetail.GetColumn(ds_WaterDetail.row, "BRANCH_CD")
				 + " fa_OfficeFlag="  + ds_WaterDetail.GetColumn(ds_WaterDetail.row, "ZIP_NO1"  ) 
				 ;
	
		var retValue = gfn_Dialog("B20","B2202010012U",args,728,424,true,-1,-1);

		if(retValue == 0){
			return;
		}	

		if(retValue != "" && retValue != null)
		{
			if (cob_Gu.Value != retValue[3]){	
				alert("해당 시/구를 선택해주세요.");
				return;
			}
		
			ds_WaterDetail.SetColumn(0,"WORK_NUM"    ,retValue[0] );
			ds_WaterDetail.SetColumn(0,"WORK_FIRM_NM",retValue[5] );
			ds_WaterDetail.SetColumn(0,"WORK_FIRM_CD",retValue[12]);
									
			var Transition    = "selectWorkInfo";
			var ServicePart   = "D10";
			var Url           = "handle.do?ServiceName=" + "D1_FacilityInfoMgr-service"+"&WORK_NUM=" + retValue[0] ;
			var InputDsList   = "";
			var OutputDsList  = "ds_GetWorkInfo=ds_GetWorkInfo";
			var Argument      = "selectWorkInfo";
						
			gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);			
						
			if(ds_GetWorkInfo.GetRowCount() > 0){
				// 외부공사일 경우 주소 정보 셋팅시켜 줄 수 있음. 투자공사는 공사 시,종점으로 관리			
				edt_lot_num_union  = ds_GetWorkInfo.GetColumn(0, "CURR_ADDR_UNION");  	   // 지번주소조합
				edt_zip_seq        = ds_GetWorkInfo.GetColumn(0, "ZIP_SEQ"        );       // 우편일련번호     
				edt_hill           = ds_GetWorkInfo.GetColumn(0, "HILL"           );       // 산번지
				edt_addr_m         = ds_GetWorkInfo.GetColumn(0, "ADDR1_M"        );       // 본번
				edt_addr_s         = ds_GetWorkInfo.GetColumn(0, "ADDR1_S"        );       // 부번  
				edt_second_addr    = ds_GetWorkInfo.GetColumn(0, "SECOND_ADDR"    );   	   // 보조주소
				edt_bld_dong       = ds_GetWorkInfo.GetColumn(0, "BLD_DONG"       );       // 동
				edt_hosu           = ds_GetWorkInfo.GetColumn(0, "HOSU"           );       // 호수
				edt_zip_no1        = ds_GetWorkInfo.GetColumn(0, "ZIP_NO1"        );       // 우편번호1
				edt_zip_no2        = ds_GetWorkInfo.GetColumn(0, "ZIP_NO2"        );       // 우편번호2
			}
		}
	//}
}

