function cob_PBlockCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//-------------------------------------------------------------------------------
	//  Kind   :  현재 화면 종류
	//  ServiceId   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var ServiceId    = "fn_D1_SearchCdBox";
    var ServicePart  = "D10";
    var strURL 		 = "handle.do?ServiceName=D1_QuarterPatroPlanRegisMgr-service"
					 + "&BRANCH_CD="	+ cob_Branc.Value		// 지사코드
					 + "&TEAM_CD="		+ cob_Team.Value		// 팀코드
					 ;
    var InputDsList  = "";
    var OutputDsList = "ds_BlockCdBx=ds_BlockCdBx";
    var Argument     = "find1_BlockCodeBox";
    
    gfn_SyncCall(ServiceId, ServicePart, strURL, InputDsList, OutputDsList, Argument);
    
    if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	cob_PBlockCd.Index = 0;
	
	if(ds_BlockCdBx.GetRowCount() == 0){
		cob_PBlockCd.Enable = false;
	}else{
		cob_PBlockCd.Enable = true;
		// 전체추가
		var str_nRow = ds_BlockCdBx.AppendRow();
		ds_BlockCdBx.SetColumn(str_nRow, "BLOCK_CD", ""    );
		ds_BlockCdBx.SetColumn(str_nRow, "BLOCK_NM", "전체");
	}
}

function cob_Team_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_Search.ClearData();
	ds_Search.AddRow();
	
	ds_Search.SetColumn(0, "BRANCH_CD" , cob_Branc.Value);
	ds_Search.SetColumn(0, "TEAM_CD", cob_Team.Value);
	
	var strSvcID = "find_block";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_FacilityInfoMgr-service";
	var strInDatasets  = "ds_Search=ds_Search";
	var strOutDatasets  = "ds_Block=ds_Block";
	var strArgument  = "find_block=1";
				
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );					 	    
    
    if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	cob_PBlockCd.Index = 0;
	
	if(ds_Block.GetRowCount() == 0){
		cob_PBlockCd.Enable = false;
	}else{
		cob_PBlockCd.Enable = true;
		// 전체추가
		var str_nRow = ds_Block.AppendRow();
		ds_Block.SetColumn(str_nRow, "BLOCK_CD", ""    );
		ds_Block.SetColumn(str_nRow, "BLOCK_NM", "전체");
	}
}
