/********************************************************************************
* 기      능   :  지사별 구를 조회하여 콤보박스에 셋팅
********************************************************************************/
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

    // 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", cob_Branc.value, fn_setDsObject_Comcode("1", "cob_ZipNo1"));
    
    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
    //    cob_ZipNo1.Enable = false;
    //권기철20091008
    	cob_ZipNo1.Value = "";
    //	cob_ZipNo1.Index =  0;
    } else {
		cob_ZipNo1.Value = "";
    //    cob_ZipNo1.Enable = true;
    //    cob_ZipNo1.Index  = 0;
    }
    
    gfn_D1_SetComboBox_Teamcd("ds_Team",cob_Branc);
    cob_Team.Value = "";
}    

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Search()
//  설명: 공동주택등록 화면 리스트를 조회 하는 함수
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Search()
{	
    // if (cob_Branc.value == "" || cob_Branc.value == null) {
         // alert("지사를 선택하십시오.");
         // cob_Branc.SetFocus();
         // return;
    // }
	cob_RegtrModelNm1.Enable=true;
	cob_RegtrModelNm2.Enable=true;
    ds_UniDetail.ClearData();
    var ServiceId     = "select7";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&BRANCH_CD="+cob_Branc.Value+"&Zip1="+cob_ZipNo1.Value +"&CO_LIVE_NM="+edt_UniNm.Text+"&TEAM_CD="+""+"&FACI_NUM="+edt_FaciNumSearch.Text;
    var InputDsList     = "";
    var OutputDsList = "ds_Uni=ds_Uni";
    var Argument      = "select7";
    var CallBack     = "fn_D1_CallBack";
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    if(ds_Uni.GetRowCount()==0)
         grd_Uni.NoDataText="조회결과가 없습니다.";
    st_Reg.Text = ds_Uni.GetRowCount();
    fv_faciNum = "";
    edtFileList.text = "";
    edtFileList2.text = "";
    
    grd_Uni_OnCellClick();
}

//------------------------------------------------------------------------------- 
// 함수명: grd_Uni_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
//   설명: 공동주택등록 상세내용를 조회 하는 fn_D1_DetailSearch()을 부르는 이벤트용 함수
//         그리드 클릭시 이벤트 처리
// arguments   
//          obj: Object 이 Event가 발생된 Component.
//          nRow: Integer Event가 발생한 Cell의 Row Index.
//          nCell: Integer Event가 발생한 Cell의 Column Index.
//          nX: Integer Grid 내에서 Click 된 마우스 포인트의 X 좌표.
//          nY Integer Grid 내에서 Click 된 마우스 포인트의 Y 좌표.
//          nPivotIndex CrossTabGrid에서 Event가 발생한 Cell의 Pivot Index.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function grd_Uni_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if (nCell == 13) {
        var args = "BUNGI='01'";
            args += " GU_NM=" + quote(ds_Uni.GetColumn(nRow, "ZIP_NO1"))
                 + " DONG_NM_H=" + quote(ds_Uni.GetColumn(nRow, "TOWN"))
                 + " FNU=" + quote(ds_Uni.GetColumn(nRow, "ADDR_M"))  
                 + " SNU=" + quote(ds_Uni.GetColumn(nRow, "ADDR_S"))
                 ;
	   	   
		Dialog("D10::D1402060011U.xml", args, 1208, 840, "Resize=true Scroll=true", -1, -1);
    } else {
		ds_UniDetail.ClearData();
		
		fv_faciNum = grd_Uni.GetCellValue(nRow,1);
		fn_D1_DetailSearch(fv_faciNum);
		cob_BranchCd_OnChanged1();
		cob_ElectCathoDicYn_OnChanged();
		fn_D1_FileListNm(fv_faciNum);
		fn_D1_FileListNmImg(fv_faciNum);
		fv_saveMode = "updateUni";
		cob_PressFlag_OnChanged();
	}	
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FileListNm(strFaciNum)
//   설명: 시설물 파일명을 조회 하는 함수
// arguments   
//          strFaciNum  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FileListNm(strFaciNum)
{	
    var ServiceId     = "search";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+ strFaciNum;
    var InputDsList     = "";
    var OutputDsList = "ds_FileList=ds_FileList";
    var Argument      = "findFile";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	edtFileList.text = "";
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		edtFileList.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
	}
}

