//------------------------------------------------------------------------------- 
// 함수명: fn_CommonAddRow()
//   설명: 공동주택등록 내용를 신규 추가 하는 모드로 변경 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonAddRow()
{	
	fv_faciNum = "";

    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
        alert("지사를 선택해주세요.");
        cob_BranchCd.Value = "";
        cob_Gu.Value = "";
        return;
    }
    
    edtFileList.Text = "";
    edtFileList2.Text = "";
	ds_UniDetail.ClearData();
    ds_UniDetail.AddRow();   
    cob_BranchCd.Value = cob_Branc.Value;  
    cob_BranchCd_OnChanged1();
    cob_ElectCathoDicYn_OnChanged();
    cob_Gu.Value = cob_ZipNo1.Value;
    sp_InstYmd.Value = substr(today(), 0, 4);
    //GetFaciNum();
    fv_saveMode     = "insertUni";
    cob_FaciStsCd.Index = 1;
}

