function fn_D1_ExcelExport()
{	
    ds_Search.ClearData();
   	ds_excel.ClearData();

	var row = ds_Search.AddRow();
	ds_Search.SetColumn(row,"BRANCH_CD",cob_Branc.Value);
	ds_Search.SetColumn(row,"ZIP_NO1",cob_ZipNo1.Value);
	ds_Search.SetColumn(row,"FACI_NUM",edt_FaciNumSearch.Text);
	ds_Search.SetColumn(row,"FACI_NM",edt_UniNm.Text);
		
    var strSvcID        = "Search_Uni";
	var strPart         = "D10";	             
	var strURL          = "handle.do?ServiceName=D1_FacilityInfoMgr_Excel-service";
	var strInDatasets   = "ds_Search=ds_Search";
	var strOutDatasets  = "ds_excel=ds_excel";
	var strArgument     = "Search_Uni=1";	
	
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );			       
			   
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branc.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo1.Text," ",20);
    SearchCondition = SearchCondition + "공동주택명 :" + RPad(edt_UniNm.Text," ",20);
    SearchCondition = SearchCondition + "시설물번호 :" + RPad(edt_FaciNumSearch.Text," ",20);
	gfn_ExportExcel("공동주택 조회 ", 5, SearchCondition, "grd_excel"); 
}
