function sp_InstYmd_OnChanged(obj,strText)
{
	if (obj.text > substr(today(), 0, 4)) {
	     alert("시공년도가 올해보다 큽니다.");
	     obj.text = "";
	     obj.SetFocus();
	     return;
	     break;
	}
}

