function cob_ElectCathoDicYn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cob_ElectCathoDicYn.Value="Y")
	{
		cob_TbQty.Enable=true;
	}else{
		cob_TbQty.Value="";
		cob_TbQty.Enable=false;
	}
}

function sp_InstYmd_OnCharChanged(obj,strPreText,strPostText)
{
	if(gfn_IsNumber(obj.Value) == '0'){
		obj.Value = "";
		return;
	}else{
		return;
	}
}

function cob_PressFlag_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cob_PressFlag.Value=="L")
	{
		cob_RegtrModelNm1.Enable=false;
		cob_RegtrModelNm2.Enable=false;
	}
	else
	{
		cob_RegtrModelNm1.Enable=true;
		cob_RegtrModelNm2.Enable=true;
	}
}
