// ===============================================================================
// 함수명: D1101010010F_T08_D01_OnLoadCompleted()
//   설명: 화면 로드 후 조회 조건 기본 셋팅
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function D1101010010F_T08_D01_OnLoadCompleted()
{
	gfn_LoadForm(obj);
	
	//-----------------------------------------
	//  콤보박스의 내용을 셋팅한다.
	//-----------------------------------------
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branc"));
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd"));	
	
	gfn_D1_NULL_SetComboBox_Comcode("B30007",gfn_setDsObject_Comcode("cob_PipeDiam1Cd"));	
	gfn_D1_NULL_SetComboBox_Comcode("D10034",gfn_setDsObject_Comcode("cob_PipePress1"));	
		
	gfn_D1_NULL_SetComboBox_Comcode("B30007",gfn_setDsObject_Comcode("cob_PipeDiam2Cd"));	
	gfn_D1_NULL_SetComboBox_Comcode("D10034",gfn_setDsObject_Comcode("cob_PipePress2"));	
		
	gfn_D1_NULL_SetComboBox_Comcode("B30007",gfn_setDsObject_Comcode("cob_PipeDiam3Cd"));	
	gfn_D1_NULL_SetComboBox_Comcode("D10034",gfn_setDsObject_Comcode("cob_PipePress3"));	
	gfn_D1_NULL_SetComboBox_Comcode("D10186",gfn_setDsObject_Comcode("cob_FaciStsCd"));
	
	//gfn_SetComboBox_Comcode("A10200",gfn_setDsObject_Comcode("cob_OutsideExposYn"));	
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") {
	    cob_Branc.value = '';
	} else {
	    cob_Branc.Value = gfn_GetGlobalValue("G_BRANCH_CD");
	    cob_Branc.Enable = false;
	}
	
	cob_BranchCd.Enable = false;
	
	cob_BranchCd_OnChanged();
	gfn_D1_SetGrid_Sort(grd_Import);
}

/********************************************************************************
* 기      능   :  지사별 구를 조회하여 콤보박스에 셋팅
********************************************************************************/
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

    // 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", cob_Branc.value, fn_setDsObject_Comcode("1", "cob_ZipNo1"));
    
    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
    //    cob_ZipNo1.Enable = false;
    //권기철20091008
		cob_ZipNo1.Value = "";
	//	cob_ZipNo1.Index = 0;
    } else {
		cob_ZipNo1.Value = "";
    //    cob_ZipNo1.Enable = true;
    //    cob_ZipNo1.Index  = 0;
    }
        
    gfn_D1_SetComboBox_Teamcd("ds_Team",cob_Branc);
    cob_Team.Value = "";
    
    cob_Team_OnChanged();
}    

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Search()
//  설명: 중점관리시설  철길 등록 화면 리스트를 조회 하는 함수
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Search()
{	
    // if (cob_Branc.value == "" || cob_Branc.value == null) {
         // alert("지사를 선택하십시오.");
         // cob_Branc.SetFocus();
         // return;
    // }
    ds_ImportDetail.ClearData();
    ds_ImportPipe.ClearData();
    ds_ImportPipe2.ClearData();
    ds_ImportPipe3.ClearData();
	//-------------------------------------------------------------------------------
	//  Kind   :  현재 화면 종류
	//  ServiceId   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var ServiceId     = "select8";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_FacilityInfoMgr-service" 
					  + "&BRANCH_CD="   + cob_Branc.Value
					  + "&FACI_STS_CD=" + fv_Kind
					  + "&Zip1="        + cob_ZipNo1.Value 
					  + "&FACI_NM="     + edt_FaciNm2.Text
					  + "&TEAM_CD="     + cob_Team.Value
					  + "&FACI_NUM="    + 'RO'
					  + "&BLOCK_CD="    + cob_PBlockCd.Value
					  ;
    var InputDsList   = "";
    var OutputDsList  = "ds_Import=ds_Import";
    var Argument      = "select8";
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    if(ds_Import.GetRowCount()==0)
        grd_Import.NoDataText="조회결과가 없습니다.";
    st_Reg.Text = ds_Import.GetRowCount();
    fv_faciNum = "";
    edtFileList.text = "";
    edtFileList2.text = "";
        
    grd_Import_OnCellClick();
}

//------------------------------------------------------------------------------- 
// 함수명: grd_Import_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
//   설명: 중점관리시설 상세내용를 조회 하는 fn_D1_DetailSearch()을 부르는 이벤트용 함수
//         그리드 클릭시 이벤트 처리
// arguments   
//          obj: Object 이 Event가 발생된 Component.
//          nRow: Integer Event가 발생한 Cell의 Row Index.
//          nCell: Integer Event가 발생한 Cell의 Column Index.
//          nX: Integer Grid 내에서 Click 된 마우스 포인트의 X 좌표.
//          nY Integer Grid 내에서 Click 된 마우스 포인트의 Y 좌표.
//          nPivotIndex CrossTabGrid에서 Event가 발생한 Cell의 Pivot Index.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function grd_Import_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if (nCell == 13) {
        var args = "BUNGI='01'";
            args += " GU_NM=" + quote(ds_Import.GetColumn(nRow, "STR_ZIP_NO1"))
                 + " DONG_NM_H=" + quote(ds_Import.GetColumn(nRow, "TOWN"))
                 + " FNU=" + quote(ds_Import.GetColumn(nRow, "STR_ADDR_M"))  
                 + " SNU=" + quote(ds_Import.GetColumn(nRow, "STR_ADDR_S"))
                 ;
	
		Dialog("D10::D1402060011U.xml", args, 1208, 840, "Resize=true Scroll=true", -1, -1);
    } else {
    
		ds_ImportDetail.ClearData();
		
		fv_faciNum = grd_Import.GetCellValue(nRow,1);
		fn_D1_DetailSearch(fv_faciNum);
		cob_BranchCd_OnChanged1();
		fn_D1_FileListNm(fv_faciNum);
		fn_D1_FileListNmImg(fv_faciNum);
		fv_saveMode = "updateImport";
	}	
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FileListNm(strFaciNum)
//   설명: 시설물 파일명을 조회 하는 함수
// arguments   
//          strFaciNum  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FileListNm(strFaciNum)
{	
    var ServiceId     = "search";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+ strFaciNum;
    var InputDsList     = "";
    var OutputDsList = "ds_FileList=ds_FileList";
    var Argument      = "findFile";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	edtFileList.text = "";
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		edtFileList.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
	}
}

