//------------------------------------------------------------------------------- 
// 함수명: fn_D1_DetailSearch(FACI_NUM)
//   설명: 중점관리시설  상세내용를 조회 하는 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_DetailSearch(FACI_NUM)
{	
	ds_ImportDetail.ClearData();
	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var ServiceId     = "select8Detail";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+FACI_NUM;
    var InputDsList     = "";
    var OutputDsList = "ds_ImportDetail=ds_ImportDetail";
    var Argument      = "select8Detail";
    var CallBack     = "fn_D1_CallBack";    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);	
    
    //-----------------------------------------
	//  배관 1, 2, 3 조회
	//-----------------------------------------
	ds_ImportPipe.ClearData();
    ds_ImportPipe2.ClearData();
    ds_ImportPipe3.ClearData();
	fn_D1_PipeSearch(FACI_NUM, "selectPipe1", "001", "ds_ImportPipe");
	fn_D1_PipeSearch(FACI_NUM, "selectPipe2", "002", "ds_ImportPipe2");
	fn_D1_PipeSearch(FACI_NUM, "selectPipe3", "003", "ds_ImportPipe3");
	if(ds_ImportPipe.GetRowCount()==0)
	{
		update_flag="Y";
		ds_ImportPipe.AddRow();
		ds_ImportPipe2.AddRow();
		ds_ImportPipe3.AddRow();
	}
	else
	{
		update_flag="";
	}
}


//------------------------------------------------------------------------------- 
// 함수명: fn_D1_PipeSearch(FACI_NUM)
//   설명: 중점관리시설  배관 상세내용를 조회 하는 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//          Sid  : 서비스 아이디 
//          pipeManageNum  : 배관관리번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_PipeSearch(FACI_NUM, Sid, pipeManageNum, OutDs)
{
	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var ServiceId       = Sid;
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+FACI_NUM+"&PIPE_MANAGE_NUM="+pipeManageNum;
    var InputDsList     = "";
    var OutputDsList    = OutDs+"="+OutDs;
    var Argument        = Sid; 
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonAddRow()
//   설명: 중점관리시설 내용를 신규 추가 하는 모드로 변경 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonAddRow()
{	
	fv_faciNum = "";

    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
        alert("지사를 선택해주세요.");
        cob_BranchCd.Value = "";
        cob_Gu.Value = "";
        return;
    }
    
    edtFileList.Text = "";
    edtFileList2.Text = "";
	ds_ImportDetail.ClearData();
	ds_ImportPipe.ClearData();
    ds_ImportPipe2.ClearData();
    ds_ImportPipe3.ClearData();
    ds_ImportDetail.AddRow(); 
    ds_ImportPipe.AddRow();
    ds_ImportPipe2.AddRow();
    ds_ImportPipe3.AddRow();
    cob_BranchCd.Value = cob_Branc.Value; 
    cob_BranchCd_OnChanged1();
    cob_Gu.Value       = cob_ZipNo1.Value;
    sp_InstYmd.Value   = substr(today(), 0, 4);
    sp_ConsYear1.Value = substr(today(), 0, 4);
    sp_ConsYear2.Value = substr(today(), 0, 4);
    sp_ConsYear3.Value = substr(today(), 0, 4);    
    //GetFaciNum();
    fv_saveMode     = "insertImport";
    cob_FaciStsCd.Index = 1;
}

