	{
		alert("차단밸브번호1을 선택해주세요.");
		edt_CutValveNum01.SetFocus();
		return;
	}
	
	if(edt_CutValveNum02.Text == "")
	{
		alert("차단밸브번호2를 선택해주세요.");
		edt_CutValveNum02.SetFocus();
		return;
	}
	
	if(edt_CutValveNum03.Text == "")
	{
		alert("차단밸브번호3을 선택해주세요.");
		edt_CutValveNum03.SetFocus();
		return;
	}
	
	if(edt_CutValveNum04.Text == "")
	{
		alert("차단밸브번호4를 선택해주세요.");
		edt_CutValveNum04.SetFocus();
		return;
	}
	
	if(edt_TakerEmpID.Text == "")
	{
		alert("담당자를 선택해주세요.");
		edt_TakerEmpID.SetFocus();
		return;
	}
	*/

	//-------------------------------------------------------------------------------
	//GetFaciNum() : 채번 ds_GetNum으로 시설물 번호를 넣음 
	//-------------------------------------------------------------------------------
	
	var kind = "";
	if(fv_saveMode=="insertImport")
	{
	    GetFaciNum();
		ds_ImportDetail.AddColumn("CRT_EMPID", "STRING", 256);
		ds_ImportDetail.AddColumn("CRT_IP", "STRING", 256);
		ds_ImportDetail.AddColumn("FACI_STS_CD", "STRING", 256);
		ds_ImportDetail.AddColumn("FACI_NUM", "STRING", 256);
		ds_ImportDetail.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_ImportDetail.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_ImportDetail.SetColumn(0, "FACI_STS_CD", "20");
		ds_ImportDetail.SetColumn(0, "FACI_NUM", ds_GetNum.GetColumn(0,"FACI_NUM"));
		kind = ds_GetNum.GetColumn(0,"FACI_NUM");
    }else if(fv_saveMode=="updateImport")
    {
		ds_ImportDetail.AddColumn("UPD_EMPID", "STRING", 256);
		ds_ImportDetail.AddColumn("UPD_IP", "STRING", 256);
		ds_ImportDetail.AddColumn("FACI_STS_CD", "STRING", 256);
		ds_ImportDetail.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_ImportDetail.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_ImportDetail.SetColumn(0, "FACI_STS_CD", "20");
		kind = fv_faciNum;
    }
	
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------

	var Transition     = fv_saveMode;
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service";
    var InputDsList     = "ds_ImportDetail=ds_ImportDetail:u";
    var OutputDsList = "ds_ImportDetail=ds_ImportDetail";
    var Argument      = fv_saveMode;
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
	if(ErrorCode<0)
	{
		return false;
	}
	
    //-----------------------------------------
	//  배관 1, 2, 3 저장
	//-----------------------------------------
	if(update_flag=="Y")
	{
		fv_saveMode="insertImport";
	}
	if(fv_saveMode=="insertImport")
	{
	    if(update_flag=="Y")
		{
			pipeSave(fv_faciNum, "insertPipe1", "001", ds_ImportPipe);
			pipeSave(fv_faciNum, "insertPipe2", "002", ds_ImportPipe2);
			pipeSave(fv_faciNum, "insertPipe3", "003", ds_ImportPipe3);
		}
		else
		{
			pipeSave(ds_GetNum.GetColumn(0,"FACI_NUM"), "insertPipe1", "001", ds_ImportPipe);
			pipeSave(ds_GetNum.GetColumn(0,"FACI_NUM"), "insertPipe2", "002", ds_ImportPipe2);
			pipeSave(ds_GetNum.GetColumn(0,"FACI_NUM"), "insertPipe3", "003", ds_ImportPipe3);

		}
	}else if(fv_saveMode=="updateImport")
    {
		pipeSave(fv_faciNum, "updatePipe1", "001", ds_ImportPipe);
		pipeSave(fv_faciNum, "updatePipe2", "002", ds_ImportPipe2);
		pipeSave(fv_faciNum, "updatePipe3", "003", ds_ImportPipe3);
    }
    
    gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	if(fv_saveMode=="insertGovRoom")
	{
		// 팀-전체
		cob_Team.Value = "";     // 전체
		cob_PBlockCd.Value = ""; // 전체
	}
		
	fn_D1_Search();
}


//------------------------------------------------------------------------------- 
// 함수명: pipeSave()
//   설명: 중점관리시설  배관 상세내용를 저장 하는 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//          Sid  : 서비스 아이디 
//          pipeManageNum  : 배관관리번호
//          OutDs  : 데이터셋
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function pipeSave(FACI_NUM, Sid, pipeManageNum, OutDs)
{
	if(SubStr(Sid,0,10) =="insertPipe")
	{
		OutDs.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		OutDs.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		OutDs.SetColumn(0, "PIPE_MANAGE_NUM", pipeManageNum);
		OutDs.SetColumn(0, "FACI_NUM", FACI_NUM);
	}else if(SubStr(Sid,0,10) =="updatePipe")
    {
		OutDs.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		OutDs.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		OutDs.SetColumn(0, "PIPE_MANAGE_NUM", pipeManageNum);
    }
    
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var ServiceId       = Sid;
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service";
    var InputDsList     = OutDs.ID +"="+OutDs.ID+":u";
    var OutputDsList    = OutDs.ID +"="+OutDs.ID;
    var Argument        = Sid; 
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
	if(ErrorCode<0)
	{
		return false;
	}
	
	OutDs.ClearData();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
//   설명: 중점관리시설를 삭제 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonDeleteRow()
{
	
    if(!Confirm(gfn_GetMessage("M000016","%AAA%","삭제")))	//"삭제하시겠습니까?" 
     {
        return false;
     };
        
    if(ds_Import.GetRowCount()==0){ 
        alert(gfn_GetMessage("M000008","%AAA%","삭제대상"));	//"삭제하실 내역이 없습니다."
		return;
    }
    
    var str_FaciNum = ds_Import.GetColumn(ds_Import.row, "FACI_NUM");
	fn_D1_FileDelete(str_FaciNum); 
        
    ds_Import.DeleteRow(ds_Import.row);	
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "deleteImport";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service&FACI_NUM="+fv_faciNum;
    var InputDsList     = "ds_Import=ds_Import:u";
    var OutputDsList = "ds_Import=ds_Import";
    var Argument      = "deleteImport";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    
	if(ErrorCode<0){
	    if(ErrorMsg!=""){
	       alert(ErrorMsg);
	    } else {   
	       
	    }
		ds_Import.Reset();
		return false;
	}
    fn_CommonSearch();
}

