// 함수명: fn_ZipSearch()
//   설명: 우편번호 조회클릭시 우편번호 조회 popup화면 로딩
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_ZipSearch(obj)
{
    //if(strFaciNum != "")
	//{
		var arrList = gfn_Dialog("A10", "A1101020010U", "", 1100, 600, true, -1, -1);
		
		if(arrList == 0){
			return;
		}
		
		if (arrList != "" && arrList != null) {
			
			if (cob_ZipNo1.Value != arrList[2]) {
				 alert("해당 시/구를 선택해주세요.");
				 return;
			}
			
			if(obj.id == "btn_DZip"){   
			
				edt_lot_num_union = arrList[17];  		 // 지번주소조합
				edt_new_addr_union = arrList[23]; 		 // 새주소조합
				edt_zip_seq = arrList[0];         		 // 우편일련번호     
				edt_hill = arrList[10];           		 // 산번지
				edt_addr_m = arrList[11];         		 // 본번
				edt_addr_s = arrList[12];         		 // 부번  
				edt_second_addr = arrList[13];    		 // 보조주소
				edt_bld_dong = arrList[14];       		 // 동
				edt_hosu = arrList[15];           		 // 호수
				// edt_new_addr_bld_seq = arrList[18]; 	 // 새주소일련번호
				// edt_new_addr_ungrnoud_yn = arrList[25];  // 새주소지하여부
				// edt_new_hosu = arrList[21];              // 새주소호수 
				edt_zip_no1 = arrList[2];                // 우편번호1
				edt_zip_no2 = arrList[3];                // 우편번호2
			}
		}	
		
		G_ACTIVEWIN = parent.id;
	//}	
}

// ===============================================================================
// 함수명: D1101010010F_T09_D01_OnLoadCompleted()
//   설명: 화면 로드 후 조회 조건 기본 셋팅
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function D1101010010F_T09_D01_OnLoadCompleted()
{
    //gfn_LoadForm(obj);
    
	//-----------------------------------------
	//  콤보박스의 내용을 셋팅한다.
	//-----------------------------------------
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branc"));
	//gfn_SetComboBox_Comcode("B10037",gfn_setDsObject_Comcode("cob_Branc"));	
	gfn_SetComboBox_Comcode("B10004",gfn_setDsObject_Comcode("cob_ZipNo1"));
	//gfn_SetComboBox_Comcode("B10037",gfn_setDsObject_Comcode("cob_BranchCd"));	
	gfn_SetComboBox_Comcode("D10186",gfn_setDsObject_Comcode("cob_FaciStsCd"));
	
	// 2013.04.25. 이건민 - 주철민 계장 추가요청사항 (조정기 구분)
	gfn_SetComboBox_Comcode("D10087",gfn_setDsObject_Comcode("cob_RegtrFlag"));
	
	//-----------------------------------------
	//  콤보박스의 초기 설정
	//-----------------------------------------
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") {
	    cob_Branc.value = '';
	} else {
	    cob_Branc.Value = gfn_GetGlobalValue("G_BRANCH_CD");
	    cob_Branc.Enable = false;
	}
	
	cob_BranchCd.Enable = false;
	
	cob_BranchCd_OnChanged();
	gfn_D1_SetGrid_Sort(grd_Dire);
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_ExcelExport()
//  설명: 특별관리시설 엑셀로 저장
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_ExcelExport()
{	
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branc.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo1.Text," ",20);
    SearchCondition = SearchCondition + "팀 :" + RPad(cob_Team.Text," ",20);
    SearchCondition = SearchCondition + "구간 :" + RPad(cob_PBlockCd.Text," ",20);  
    SearchCondition = SearchCondition + "건물명 :" + RPad(edt_StructureNm.Text," ",20);
	gfn_ExportExcel("중압직공급 조회 ", 5, SearchCondition, "grd_Excel"); 
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Search()
//  설명: 특별관리시설   등록 화면 리스트를 조회 하는 함수
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Search()
{	
	//-------------------------------------------------------------------------------
	//  Kind   :  현재 화면 종류
	//  ServiceId   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
	ds_SpeciDetail.ClearData();
    var ServiceId    = "select10";
    var ServicePart  = "D10";
    var strURL 		 = "handle.do?ServiceName=D1_FacilityInfoMgr-service"
					 + "&BRANCH_CD=" + cob_Branc.Value
					 + "&ZIP_NO1="   + cob_ZipNo1.Value
					 + "&FACI_NM="   + edt_StructureNm.Text
					 + "&TEAM_CD="   + cob_Team.Value
					 + "&FACI_NUM="  + edt_FaciNumSearch.Text
					 + "&BLOCK_CD="  + cob_PBlockCd.Value
					 ;					 
    var InputDsList  = "";
    var OutputDsList = "ds_Speci=ds_Speci ds_SpeciExcel=ds_SpeciExcel";
    var Argument     = "select10";
    gfn_SyncCall(ServiceId, ServicePart, strURL, InputDsList, OutputDsList, Argument);
   
    if(ErrorCode < 0)
	{    
		return;
	}
	if(ds_Speci.GetRowCount()==0)
        grd_Dire.NoDataText="조회결과가 없습니다.";
	
	st_Reg.Text = ds_Speci.GetRowCount();
	strFaciNum = "";
	edtFileList2.Text="";
	grd_speci_OnCellClick("",0,"","","","");
	
	
}

//------------------------------------------------------------------------------- 
// 함수명: grd_speci_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
//   설명: 특별관리시설 상세내용를 조회 하는 fn_D1_DetailSearch()을 부르는 이벤트용 함수
//         그리드 클릭시 이벤트 처리
// arguments   
//          obj: Object 이 Event가 발생된 Component.
//          nRow: Integer Event가 발생한 Cell의 Row Index.
//          nCell: Integer Event가 발생한 Cell의 Column Index.
//          nX: Integer Grid 내에서 Click 된 마우스 포인트의 X 좌표.
//          nY Integer Grid 내에서 Click 된 마우스 포인트의 Y 좌표.
//          nPivotIndex CrossTabGrid에서 Event가 발생한 Cell의 Pivot Index.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function grd_speci_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if (nCell == 9) {
        var args = "BUNGI='01'";
            args += " GU_NM=" + quote(ds_Speci.GetColumn(nRow, "ZIP_NO1"))
                 + " DONG_NM_H=" + quote(ds_Speci.GetColumn(nRow, "TOWN"))
                 + " FNU=" + quote(ds_Speci.GetColumn(nRow, "ADDR_M"))  
                 + " SNU=" + quote(ds_Speci.GetColumn(nRow, "ADDR_S"))
                 ;
	
		Dialog("D10::D1402060011U.xml", args, 1208, 840, "Resize=true Scroll=true", -1, -1);
    } else {
		strFaciNum = ds_Speci.getcolumn(nRow, "FACI_NUM");
		fn_D1_SetComboInit();
		fn_D1_DetailSearch(strFaciNum);
		cob_BranchCd_OnChanged1();
		//fv_saveMode = "updateSpeci";
	}	
}

