//------------------------------------------------------------------------------- 
// 함수명: fn_CommonAddRow()
//   설명: 특별관리시설 내용를 신규 추가 하는 모드로 변경 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonAddRow()
{	
	fv_faciNum = "";

    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
        alert("지사를 선택해주세요.");
        cob_BranchCd.Value = "";
        cob_Gu.Value = "";
        return;
    }
    
    edtFileList.Text = "";
    edtFileList2.Text = "";
	ds_SpeciDetail.ClearData();
    ds_SpeciDetail.AddRow();
    cob_BranchCd.Value = cob_Branc.Value;    
    cob_BranchCd_OnChanged1();
    cob_Gu.Value = cob_ZipNo1.Value;    
    fn_D1_SetComboInit();
    //strFaciNum = GetFaciNum();
    cob_FaciStsCd.Index = 1;
    //fv_saveMode     = "insertSpeci";
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonSave()
//   설명: 특별관리시설 상세 내용를 저장 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSave()
{   
	if(edt_FaciNm == "")
	{
		alert("건물명을 입력해주세요.");
		return;
	}
	
	/* 20090810
	if(edt_WorkFirmCd == "")
	{
		alert("시공사를 선택해주세요.");
		return;
	}
	*/
	
	// 2013.04.25. 이건민- 주철민 계장 요청으로 공사번호 필수항목 체크추가
	if(edt_WorkNum == "")
	{
		alert("공사번호를 선택해주세요.");
		return;
	}
	
		
	if(edt_lot_num_union.Text == "")
	{
		alert("주소를 선택해주세요.");
		return;
	}
	
	// 2013.04.25. 이건민 - 주철민 계장 요청사항 (조정기 구분이 '구역'일경우에는 구역 조정기 명칭 필수)
	var strRegtrNm = replace(edt_RegtrNm.text, ' ', '');
	
	if(cob_RegtrFlag.Value == "20")
	{
		if(strRegtrNm == null || strRegtrNm == "")
		{
			alert("조정기 구분이 '구역'일 경우 '구역 조정기 명칭'은 필수입니다.");
			return;
		}
	}
			
	// 2013.04.25. 이건민 - 주철민 계장 요청사항 (구역 조정기 명칭에 '압력' 명칭 제약)
	for(var i=0; i < length(strRegtrNm); i++)
	{
		if(charAt(strRegtrNm, i) == "압")
		{
			i++;
			if(charAt(strRegtrNm, i) == "력")
			{
				alert("구역 조정기 명칭에 '압력'을 빼주세요.");
				
				return;
			}
		}
	}
	
	var kind = "";
	
	//중앙직공급 채번해오기
	if(ds_SpeciDetail.GetRowType(0) == "insert")
	{
	    strFaciNum = GetFaciNum();
		ds_SpeciDetail.SetColumn(0,"FACI_NUM",strFaciNum);
		kind = strFaciNum;
	} else {
	    kind = ds_SpeciDetail.GetColumn(0,"FACI_NUM");
	} 
	
	ds_SpeciDetail.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
	ds_SpeciDetail.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
	ds_SpeciDetail.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
	ds_SpeciDetail.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
	ds_SpeciDetail.SetColumn(0, "REGTR_NM", strRegtrNm);
    
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------

	var Transition      = "fn_D10_Save";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service";
    var InputDsList     = "ds_SpeciDetail=ds_SpeciDetail:u";
    var OutputDsList    = "";
    var Argument        = "saveDire";
   
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
	
	if(ErrorCode < 0)
	{    
		return;
	}
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	if(fv_saveMode=="insertGovRoom")
	{
		// 팀-전체
		cob_Team.Value = "";     // 전체
		cob_PBlockCd.Value = ""; // 전체
	}
		
	fn_D1_Search();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
//   설명: 특별관리시설를 삭제 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonDeleteRow()
{
	
    if(!Confirm(gfn_GetMessage("M000016","%AAA%","삭제")))	//"삭제하시겠습니까?" 
     {
        return false;
     };
        
    if(ds_Speci.GetRowCount()==0){ 
        alert(gfn_GetMessage("M000008","%AAA%","삭제대상"));	//"삭제하실 내역이 없습니다."
		return;
    }
    
    var str_FaciNum = ds_Speci.GetColumn(ds_Speci.row, "FACI_NUM");
	fn_D1_FileDelete(str_FaciNum);      
        
    ds_Speci.DeleteRow(ds_Speci.row);	
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition    = "deleteSpeci";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service&FACI_NUM=";
    var InputDsList   = "ds_Speci=ds_Speci:u";
    var OutputDsList  = "ds_Speci=ds_Speci";
    var Argument      = "deleteSpeci1";
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
        
	if(ErrorCode<0){
	    if(ErrorMsg!=""){
	       alert(ErrorMsg);
	    } else {   
	       
	    }
		ds_Speci.Reset();
		return false;
	}
    fn_CommonSearch();
}

