//------------------------------------------------------------------------------- 
// 함수명: fn_CommonAddRow()
//   설명: 특별관리시설 내용를 신규 추가 하는 모드로 변경 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonAddRow()
{	
	fv_faciNum = "";

    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
        alert("지사를 선택해주세요.");
        cob_BranchCd.Value = "";
        cob_Gu.Value = "";
        return;
    }
    edtFileList.Text = "";
    edtFileList2.Text = "";
	ds_SpeciDetail.ClearData();
    ds_SpeciDetail.AddRow();  
    cob_BranchCd.Value = cob_Branc.Value;  
    cob_BranchCd_OnChanged1();
    cob_Gu.Value = cob_ZipNo1.Value;    
    sp_InstYmd.Value   = substr(today(), 0, 4);    
    GetFaciNum();
    cob_FaciStsCd.Index = 1;
    fv_saveMode     = "insertSpeci";
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonSave()
//   설명: 특별관리시설 상세 내용를 저장 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSave()
{
	// if(edt_WorkNum.Text == "")
	// {
		// alert("공사번호를 선택해주세요.");
		// return;
	// }
	
	if(edt_lot_num_union.Text == "")
	{
		alert("주소를 선택해주세요.");
		return;
	}
	
	// 20090810
	// if(sp_InstYmd.Text == "")
	// {
		// alert("시공년도를 입력해주세요.");
		// return;
	// }
	
	/* 20090810	
	if(cob_PipeDiamCd.Text == "")
	{
		alert("관경을 선택해주세요.");
		return;
	}
	
	if(cob_PressFlag.Text == "")
	{
		alert("압력를 선택해주세요.");
		return;
	}
	
	if(edt_Leng.Text == "")
	{
		alert("연장을 입력해주세요.");
		return;
	}
	
	if(edt_PipeNum.Text == "")
	{
		alert("배관번호를 입력해주세요.");
		return;
	}
	
	if(cob_CathodicFlag.Text == "")
	{
		alert("방식구분을 선택해주세요.");
		return;
	}
	
	if(cob_RoadWidth.Text == "")
	{
		alert("도로폭을 선택해주세요.");
		return;
	}
	*/
		
	//-------------------------------------------------------------------------------
	//GetFaciNum() : 채번 ds_GetNum으로 시설물 번호를 넣음 
	//-------------------------------------------------------------------------------
	
    var kind = "";
    if(fv_saveMode=="insertSpeci"){
		ds_SpeciDetail.AddColumn("CRT_EMPID", "STRING", 256);
		ds_SpeciDetail.AddColumn("CRT_IP", "STRING", 256);
		ds_SpeciDetail.AddColumn("FACI_FLAG", "STRING", 256);
		ds_SpeciDetail.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_SpeciDetail.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_SpeciDetail.SetColumn(0, "FACI_STS_CD", "20");
		ds_SpeciDetail.SetColumn(0, "FACI_NUM", ds_GetNum.GetColumn(0,"FACI_NUM"));
		kind = ds_GetNum.GetColumn(0,"FACI_NUM");
    }else if(fv_saveMode=="updateSpeci"){
		ds_SpeciDetail.AddColumn("UPD_EMPID", "STRING", 256);
		ds_SpeciDetail.AddColumn("UPD_IP", "STRING", 256);
		ds_SpeciDetail.AddColumn("FACI_FLAG", "STRING", 256);
		ds_SpeciDetail.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_SpeciDetail.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_SpeciDetail.SetColumn(0, "FACI_STS_CD", "20");
		kind = fv_faciNum;
    }
    
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------

	var Transition     = fv_saveMode;
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service";
    var InputDsList     = "ds_SpeciDetail=ds_SpeciDetail:u";
    var OutputDsList = "ds_SpeciDetail=ds_SpeciDetail";
    var Argument      = fv_saveMode;
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
	if(ErrorCode<0)
	{
		return false;
	}
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	
	if(fv_saveMode=="insertGovRoom")
	{
		// 팀-전체
		cob_Team.Value = "";     // 전체
		cob_PBlockCd.Value = ""; // 전체
	}
		
	fn_D1_Search();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
//   설명: 특별관리시설를 삭제 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonDeleteRow()
{
	
    if(!Confirm(gfn_GetMessage("M000016","%AAA%","삭제")))	//"삭제하시겠습니까?" 
     {
        return false;
     };
        
    if(ds_Speci.GetRowCount()==0){ 
        alert(gfn_GetMessage("M000008","%AAA%","삭제대상"));	//"삭제하실 내역이 없습니다."
		return;
    }
    
    var str_FaciNum = ds_Speci.GetColumn(ds_Speci.row, "FACI_NUM");
	fn_D1_FileDelete(str_FaciNum);  
        
    ds_Speci.DeleteRow(ds_Speci.row);	
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition   = "deleteSpeci";
    var ServicePart  = "D10";
    var Url          = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service&FACI_NUM="+fv_faciNum;
    var InputDsList  = "ds_Speci=ds_Speci:u";
    var OutputDsList = "ds_Speci=ds_Speci";
    var Argument     = "deleteSpeci";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    
	if(ErrorCode<0){
	    if(ErrorMsg!=""){
	       alert(ErrorMsg);
	    } else {   
	       
	    }
		ds_Speci.Reset();
		return false;
	}
    fn_CommonSearch();
}

