//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Search()
//  설명: 특별관리시설   등록 화면 리스트를 조회 하는 함수
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Search()
{	
    var branch = "";
    if (cob_Branc.Value != "" || cob_Branc.Value != null) {
        branch = cob_Branc.Text;   
    }
	ds_SpeciDetail.ClearData();
	//-------------------------------------------------------------------------------
	//  Kind   :  현재 화면 종류
	//  ServiceId   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    var ServiceId     = "selectLD";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_FacilityInfoMgr-service" 
					  + "&BRANCH_CD=" + branch
					  + "&Zip1="	  + decode(cob_ZipNo1.Text, '전체', '', cob_ZipNo1.Text)
					  + "&TEAM_CD="   + cob_Team.Value
					  + "&BLOCK_CD="  + cob_PBlockCd.Value
					  ;
    var InputDsList   = "";
    var OutputDsList  = "ds_Speci=ds_Speci";
    var Argument      = "selectLD";
   
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ds_Speci.GetRowCount()==0)
        grd_speci.NoDataText="조회결과가 없습니다.";
    
    st_Count.Text = ds_Speci.RowCount();
    fv_faciNum = "";
    edtFileList.text = "";
    edtFileList2.text = "";
    
    //-------------------------------------------------------------------------------
	//  EXCEL
	//-------------------------------------------------------------------------------
    ds_excel.ClearData();
    var Transition     = "find_sp";
    var ServicePart    = "D10";
    var Url            = "handle.do?ServiceName="+"D1_FacilityInfoMgr_Excel-service"
					   + "&BRANCH_CD="   + cob_Branc.Value
					   + "&STR_ZIP_NO1=" + cob_ZipNo1.Value
					   + "&STR_ZIP_NO2=" + ""
					   + "&FACI_NM="     + ""
					   + "&FACI_FLAG="   + fv_Kind 
					   + "&TEAM_CD="     + cob_Team.Value+"&FACI_NUM="+""
					   + "&BLOCK_CD="    + cob_PBlockCd.Value
					   ;
    var InputDsList    = "";
    var OutputDsList   = "ds_excel=ds_excel";
    var Argument       = "Search_Sp";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    grd_speci_OnCellClick("",0,"","","","");
}

//------------------------------------------------------------------------------- 
// 함수명: grd_speci_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
//   설명: 특별관리시설 상세내용를 조회 하는 fn_D1_DetailSearch()을 부르는 이벤트용 함수
//         그리드 클릭시 이벤트 처리
// arguments   
//          obj: Object 이 Event가 발생된 Component.
//          nRow: Integer Event가 발생한 Cell의 Row Index.
//          nCell: Integer Event가 발생한 Cell의 Column Index.
//          nX: Integer Grid 내에서 Click 된 마우스 포인트의 X 좌표.
//          nY Integer Grid 내에서 Click 된 마우스 포인트의 Y 좌표.
//          nPivotIndex CrossTabGrid에서 Event가 발생한 Cell의 Pivot Index.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function grd_speci_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if (nCell == 10) {
        var args = "BUNGI='01'";
            args += " GU_NM=" + quote(ds_Speci.GetColumn(nRow, "STR_ZIP_NO1"))
                 + " DONG_NM_H=" + quote(ds_Speci.GetColumn(nRow, "TOWN"))
                 + " FNU=" + quote(ds_Speci.GetColumn(nRow, "STR_ADDR_M"))  
                 + " SNU=" + quote(ds_Speci.GetColumn(nRow, "STR_ADDR_S"))
                 ;
	
		Dialog("D10::D1402060011U.xml", args, 1208, 840, "Resize=true Scroll=true", -1, -1);
    } else {
        if (ds_Speci.GetColumn(nRow, "FACI_NUM") == "" || ds_Speci.GetColumn(nRow, "FACI_NUM") == null) {    
			edtFileList.Text = "";
			edtFileList2.Text = "";
			ds_SpeciDetail.ClearData();
			fv_faciNum = "";
			ds_SpeciDetail.AddRow();    
			cob_BranchCd.Value = cob_Branc.Value; 
			cob_BranchCd_OnChanged1();
			cob_Gu.Value = cob_ZipNo1.Value;    
			cob_FaciStsCd.Index = 1;			
			GetFaciNum();
			fv_saveMode     = "insertSpeci";
			//ds_SpeciDetail.ClearData();
			fv_pipeNum = grd_speci.GetCellValue(nRow,4);
        } else {   
			//ds_SpeciDetail.ClearData();
			fv_faciNum = grd_speci.GetCellValue(nRow,1);
			fn_D1_DetailSearch(fv_faciNum);
			cob_BranchCd_OnChanged1();
			fn_D1_FileListNm(fv_faciNum);
			fn_D1_FileListNmImg(fv_faciNum);
			fv_saveMode = "updateSpeci";
		}	
	}	
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FileListNm(strFaciNum)
//   설명: 시설물 파일명을 조회 하는 함수
// arguments   
//          strFaciNum  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FileListNm(strFaciNum)
{	
    var ServiceId     = "search";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+ strFaciNum;
    var InputDsList     = "";
    var OutputDsList = "ds_FileList=ds_FileList";
    var Argument      = "findFile";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	edtFileList.text = "";
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		edtFileList.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
	}
}

