//------------------------------------------------------------------------------- 
// 함수명: fn_CommonSave()
//   설명: 특별관리시설 상세 내용를 저장 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSave()
{
	/* 20090810
	if(cal_LowDenConfYmd.value == "")
	{
		alert("확인일자를 선택해주세요.");
		return;
	}
	
	if(cob_LowDenConfMth.Text == "")
	{
		alert("확인방법을 선택해주세요.");
		return;
	}
	
	if(cob_LowDenPipeSts.Text == "")
	{
		alert("배관상태를 선택해주세요.");
		return;
	}
	*/
	
	if(edt_lot_num_union.Text == "")
	{
		alert("주소를 선택해주세요.");
		return;
	}
	
	//-------------------------------------------------------------------------------
	//GetFaciNum() : 채번 ds_GetNum으로 시설물 번호를 넣음 
	//-------------------------------------------------------------------------------
    
    var kind = "";
    if(fv_saveMode=="insertSpeci"){
        ds_SpeciDetail.AddColumn("CRT_EMPID", "STRING", 256);
		ds_SpeciDetail.AddColumn("CRT_IP", "STRING", 256);
		ds_SpeciDetail.AddColumn("FACI_STS_CD", "STRING", 256);
		ds_SpeciDetail.AddColumn("PIPE_NUM", "STRING", 256);
		ds_SpeciDetail.AddColumn("FACI_NUM", "STRING", 256);
		ds_SpeciDetail.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_SpeciDetail.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_SpeciDetail.SetColumn(0, "FACI_STS_CD", "20");
		ds_SpeciDetail.SetColumn(0, "FACI_NUM", fv_faciNum);
		ds_SpeciDetail.SetColumn(0, "PIPE_NUM", fv_pipeNum);
				
		kind = ds_GetNum.GetColumn(0,"FACI_NUM");
    }else if(fv_saveMode=="updateSpeci"){
		ds_SpeciDetail.AddColumn("UPD_EMPID", "STRING", 256);
		ds_SpeciDetail.AddColumn("UPD_IP", "STRING", 256);
		ds_SpeciDetail.AddColumn("FACI_STS_CD", "STRING", 256);
		ds_SpeciDetail.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
		ds_SpeciDetail.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		ds_SpeciDetail.SetColumn(0, "FACI_FLAG", "20");
		kind = fv_faciNum;
    }
    
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
    
	var Transition     = fv_saveMode;
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service";
    var InputDsList     = "ds_SpeciDetail=ds_SpeciDetail:u";
    var OutputDsList = "ds_SpeciDetail=ds_SpeciDetail";
    var Argument      = fv_saveMode;
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
	if(ErrorCode<0)
	{
		return false;
	}
	
	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 

	fn_D1_Search();

}


//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
//   설명: 특별관리시설를 삭제 하는 함수
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonDeleteRow()
{
	
    if(!Confirm(gfn_GetMessage("M000016","%AAA%","삭제")))	//"삭제하시겠습니까?" 
     {
        return false;
     };
        
    if(ds_Speci.GetRowCount()==0){ 
        alert(gfn_GetMessage("M000008","%AAA%","삭제대상"));	//"삭제하실 내역이 없습니다."
		return;
    }
    
    var str_FaciNum = ds_Speci.GetColumn(ds_Speci.row, "FACI_NUM");
	fn_D1_FileDelete(str_FaciNum);      
        
    ds_Speci.DeleteRow(ds_Speci.row);	
    //-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "deleteSpeci";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service&FACI_NUM="+fv_faciNum;
    var InputDsList     = "ds_Speci=ds_Speci:u";
    var OutputDsList = "ds_Speci=ds_Speci";
    var Argument      = "deleteSpeci";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    
	if(ErrorCode<0){
	    if(ErrorMsg!=""){
	       alert(ErrorMsg);
	    } else {   
	      
	    }
		ds_Speci.Reset();
		return false;
	}
    fn_CommonSearch();
}

function fn_D1_FileDelete(str_FACI_NUM){
  	    
	ds_FaciFile.ClearData();
	ds_FaciFile.AddRow();
	
	ds_FaciFile.SetColumn(0, "FACI_NUM", str_FACI_NUM);
	
	ds_FaciFile.UpdateControl = false;
	ds_FaciFile.SetRowType(0, "delete");
	
	// 첨부파일 삭제
	var Transition    = "deleteGov";
	var ServicePart   = "D10";
	var Url           = "handle.do?ServiceName=D1_FacilityFileInfoMgr-service";    
	var InputDsList   = "ds_FaciFile=ds_FaciFile:u";
	var OutputDsList  = "";
	var Argument      = "deleteFaciFile";
	
	gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
} 

//------------------------------------------------------------------------------- 
// 함수명: GetFaciNum()
//   설명: 채번 함수
// arguments   
//          FACI_NUM  : 시설물 번호
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function GetFaciNum()
{	
	var FaciNum = "";
	ds_GetNum.ClearData();
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------
	
    var Transition     = "selectNum";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoMgr-service"+"&FACI_FLAG="+fv_Kind;
    var InputDsList     = "";
    var OutputDsList = "ds_GetNum=ds_GetNum";
    var Argument      = "selectSpeciNum";
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    var num =ParseInt(ds_GetNum.GetColumn(0,"FACI_NUM"))+1;
    
    var numAfter = "" ;
    var i = 0;
    for(;(i+Length(ToString(num)))<6;i++){
		numAfter = "0"+numAfter; 
    }
    numAfter = numAfter + num;    
    ds_GetNum.SetColumn(0,"FACI_NUM", fv_Kind+numAfter);
    fv_faciNum = ds_GetNum.GetColumn(0,"FACI_NUM");
}



