//-------------------------------------------------------------------------------
// 함수명: fn_E2_Close
// 설명: 창 닫기 함수
// arguments
// 		   없음
// return: 없음
//-------------------------------------------------------------------------------
function fn_D1_Close()
{
	var arrList = array();

	for(var i=0; i<ds_FileList2.RowCount(); i++)
	{
	    if(ds_FileList2.GetRowType(i)=="normal")
	    {
			arrList[i] = ds_FileList2.GetColumn(i,"ORG_FILE_NAME");
		}
	}
	
	Close(arrList);
}
