//-------------------------------------------------------------------------------
//   함수명: fn_CommonExcelExport()
//   설명  :  엑셀다운 
//   arguments   
//   obj
//   return 내용: 없음
//------------------------------------------------------------------------------- 

function fn_CommonExcelExport()
{
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :" + RPad(cob_Branc.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_Gu.Text," ",20);
    SearchCondition = SearchCondition + "구간 :" + RPad(cob_BlockCd.Text," ",20);
    SearchCondition = SearchCondition + "이름 :" + RPad(edt_UserNm.Text," ",20);
	gfn_ExportExcel("구간시설 조회 ", 5, SearchCondition, "grd_Excel"); 
}

