function fn_SumLeng()
{
	edt_TotLeng.Text = ParseInt(edt_KWalkLeng.Text) + ParseInt(edt_KFidLeng.Text) + ParseInt(edt_PWalkLeng.Text) + ParseInt(edt_PFidLeng.Text);
