/********************************************************************************
* 기      능   :  동 구간 선택
********************************************************************************/      
function Button2_OnClick(obj)
{	
	if(strBlockCd != "")
	{
		var strBranchText = cob_Branch.Text;
		if(strBranchText == "강북지사")
		{
			strBranchText = "10";
		}else if(strBranchText == "남부지사")
		{
			strBranchText = "20";
		}else if(strBranchText == "서부지사")
		{
			strBranchText = "30";
		}else if(strBranchText == "경기지사")
		{
			strBranchText = "40";
		}
		
		var args = "LOW_CODE_ITEM=" + strBranchText
				 + " ITEM_KNAME=" + cob_ZipNo1.Text;
		
		var rArgs = Dialog("D10::D1101020012U.xml", args, 688, 336, true, -1, -1);
		
		if (rArgs == 0 || rArgs == "" || rArgs == null) {
			return;
		}
		
		var all = split(rArgs, "#");
		var name = "";
		var value = "";
		 var value2 = "";
		
		for (var i = 0; i < all.length; i++) {
		
			if (i % 3 == 0) {
				name += all[i] + "#";
			} else if (i % 3 == 1) {
				value += all[i] + "#";
			} else if (i % 3 == 2) {
				value2 += all[i] + "#";
			} 
		}    
		
		var num = split(name, "#");
		var num1 = split(value, "#");
		var num2 = split(value2, "#");
		
		ds_Dong.ClearData();
		for (var j = 0; j < num.length; j++) {
		  
			ds_Dong.AddRow(); 
			ds_Dong.UpdateControl = false;
			ds_Dong.SetRowType(j,"insert");
			ds_Dong.SetColumn(j, "TOWN", num2[j]);
			ds_Dong.SetColumn(j, "ZIP_NO2", num1[j]);
			ds_Dong.SetColumn(j, "ZIP_NO1", num[j]);
			//저장시 등록할 사용자 정보
			ds_Dong.SetColumn(j, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Dong.SetColumn(j, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
			ds_Dong.SetColumn(j, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Dong.SetColumn(j, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
		} 
	}
}

/********************************************************************************
* 기      능   :  정압기 선택
********************************************************************************/      
function Button3_OnClick(obj)
{
	if(strBlockCd != "")
	{
		var args  = "BRANCH_CD=" + cob_Branch.Value;
		    args += " ZIP_N01=" + cob_ZipNo1.Value;
		    
		var rArgs = Dialog("D10::D1101020013U.xml", args, 688, 288, true, -1, -1);
		
		if (rArgs == 0 || rArgs == "" || rArgs == null) {
			return;
		}
		
		var all = split(rArgs, "#");
		var strFaciNum = "";
		var strGovRoomNm = "";
		var strGovNum = "";
		var strGovModelNm = "";
		
		ds_Gov.ClearData();
		for (var i = 0; i < all.length; i++) {
		
			if (i % 4 == 0) {
				strFaciNum += all[i] + "#";
			} else if (i % 4 == 1) {
				strGovRoomNm += all[i] + "#";
			} else if (i % 4 == 2) {
				strGovNum += all[i] + "#";
			} else if (i % 4 == 3) {
				strGovModelNm += all[i] + "#";
			} 
		}    
		
		var strFaciNumArray = split(strFaciNum, "#");
		var strGovRoomNmArray = split(strGovRoomNm, "#");
		var strGovNumArray = split(strGovNum, "#");
		var strGovModelNmArray = split(strGovModelNm, "#");
		
		for (var j = 0; j < strFaciNumArray.length; j++) {
		  
			ds_Gov.AddRow(); 
			ds_Gov.UpdateControl = false;
			ds_Gov.SetRowType(j,"insert");
			ds_Gov.SetColumn(j,"BLOCK_CD",ds_FaciDetail.GetColumn(0,"BLOCK_CD"));
			ds_Gov.SetColumn(j, "FACI_NUM", strFaciNumArray[j]);
			ds_Gov.SetColumn(j, "GOV_ROOM_NM",strGovRoomNmArray[j]);
			ds_Gov.SetColumn(j, "GOV_NUM", strGovNumArray[j]);
			ds_Gov.SetColumn(j, "GOV_MODEL_NM", strGovModelNmArray[j]);
			//저장시 등록할 사용자 정보
			ds_Gov.SetColumn(j, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Gov.SetColumn(j, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));
			ds_Gov.SetColumn(j, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Gov.SetColumn(j, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));
	
		} 
	}
}

