function D1101020011F_T07_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);

    fn_Init();	
}

function fn_Init() {
    // 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", parent.cob_Branc.value, fn_setDsObject_Comcode("1", "cob_Gu"));
    cob_Gu.Index = 0;
    
    gfn_D1_SetComboBox_Comcode1("D10001",gfn_setDsObject_Comcode("cob_FaciGubun"),"AP#DR#BH#GR#GV#OP#RE#RO#TB#UC#VB#VT#VV#WD#LD#DU#ZZ");
	cob_FaciGubun.Index = 10;
	
	//2012.01.07 이건민 - 권기철 과장님 요청사항 (동삭제 요청으로 주석처리)
	//cob_Gu_OnChanged();
}

// 시/구별 동조회
function cob_Gu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var ServiceId 		= "";
    var ServicePart 	= "D10";
    var Url 			= "handle.do?ServiceName=D1101020011F-service"
                        + "&ZIP_NO1="   + cob_Gu.Value
                        ;

    var InputDsList 	= "";
    var OutputDsList 	= "ds_Dong=ds_Dong";
    var Argument 		= "findDongSearch";
	
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if (ErrorCode < 0) {
        
		return;
	}
	
	ds_Dong.InsertRow(0);
	ds_Dong.SetColumn(0, "NAME" , "전체");
	ds_Dong.SetColumn(0, "VALUE", ""    );
	
	cob_Dong.InnerDataset = "ds_Dong";
	
	cob_Dong.Value = '';
	if(cob_Gu.Value=="413"||cob_Gu.Value=="415")
	{
		cob_Dong.Enable = false; 
	}
	else
	{
		cob_Dong.Enable = true; 
	}
}


function fn_Clear() {
    
    ds_faci.ClearData();
    ds_faci1.ClearData();
    st_Reg.Text = 0;
    st_Reg1.Text = 0;
}

function btn_search_OnClick(obj) {
		
	G_BLOCK_CD = parent.edt_BlockCd.Text;
	
	if (parent.edt_BlockCd.Text == "" || parent.edt_BlockCd.Text == null) {
	    
	    alert("데이터를 먼저 조회하여 하나의 구간정보를 선택하여 주십시오. ");
		return;
	}
	
	var ServiceId 		= "";
    var ServicePart 	= "D10";
    var Url 			= "handle.do?ServiceName=D1101020011F-service"
                        + "&BRANCH_CD=" + parent.cob_Branc.value 
                        + "&ZIP_NO1="   + cob_Gu.Value
                        + "&ZIP_NO2="   + substr(cob_Dong.Value,0,3)
                        + "&S2_ZIP_NO2=" +trim(substr(cob_Dong.Value,3,3))
                        + "&TOWN="      + edt_Dong.Text
                        + "&BLOCK_CD="  + G_BLOCK_CD
                        + "&FACI_GUBUN=" + cob_FaciGubun.Value
                        ;

    var InputDsList 	= "";
    var OutputDsList 	= "ds_faci=ds_faci";
    var Argument 		= "findSC1";
	
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    st_Reg.Text = ds_faci.GetRowCount();
    G_FACI_COUNT = ds_faci.GetRowCount();
    
    if (ErrorCode < 0) {
        
		return;
	}
	
	fn_Search();
}

function fn_Search() {
	
	var ServiceId 		= "";
    var ServicePart 	= "D10";
    var Url 			= "handle.do?ServiceName=D1101020011F-service"
                        + "&BRANCH_CD="  + parent.cob_Branc.value 
                        + "&BLOCK_CD="   + G_BLOCK_CD
                        + "&FACI_GUBUN=" + cob_FaciGubun.Value
                        ;

    var InputDsList 	= "";
    var OutputDsList 	= "ds_faci1=ds_faci1";
    var Argument 		= "findSC2";
	
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    st_Reg1.Text = ds_faci1.GetRowCount();
    G_FACI1_COUNT = ds_faci1.GetRowCount();
   
    if (ErrorCode < 0) {
        
		return;
	}
}

function btn_save_OnClick(obj) {

    var str_TeamCD = trim(parent.edt_TeamCd.Text);

    if (parent.edt_BlockCd.Text == "" || parent.edt_BlockCd.Text == null) {
	    
	    alert("구간을 선택하거나 등록해 주세요.");
	    return;
	}
   
    if (str_TeamCD == "" || str_TeamCD == null) {
	    alert("구간에 팀 매칭이 되어 있지 않습니다. ");
	    return;
	}
   	
    ds_sc.ClearData();
    ds_sd.ClearData();

    if (G_FACI1_COUNT < ds_faci1.GetRowCount()) {
   
		ds_sc.UpdateControl = false;
		ds_sd.UpdateControl = false;
		
		var sd_count = 0;
		var sc_count = 0;
		
		for (var i = 0; i < ds_faci1.GetRowCount(); i++) {
		
			if (substr(ds_faci1.GetColumn(i, "FACI_NUM"), 0, 2) == "MD") {
				
				ds_sd.AddRow();
				ds_sd.SetColumn(sd_count, "FACI_NUM", ds_faci1.GetColumn(i, "FACI_NUM"));
				ds_sd.SetColumn(sd_count, "BLOCK_CD", ds_faci1.GetColumn(i, "BLOCK_CD"));
				ds_sd.SetColumn(sd_count, "TEAM_CD", parent.edt_team.text);
				
				sd_count = sd_count + 1;
			} else {
				
				ds_sc.AddRow();
				ds_sc.SetColumn(sc_count, "FACI_NUM", ds_faci1.GetColumn(i, "FACI_NUM"));
				ds_sc.SetColumn(sc_count, "BLOCK_CD", ds_faci1.GetColumn(i, "BLOCK_CD"));
				ds_sc.SetColumn(sc_count, "TEAM_CD", parent.edt_team.text);
				
				sc_count = sc_count + 1;
			}
		}
		
		for (var i = 0; i < ds_sc.GetRowCount(); i++) {
		
			if (ds_sc.GetRowType(i) == "normal") {
				
				ds_sc.SetRowType(i, "update");
			}
		}		
		
		for (var i = 0; i < ds_sd.GetRowCount(); i++) {
		
			if (ds_sd.GetRowType(i) == "normal") {
				
				ds_sd.SetRowType(i, "update");
			}
		}	
		
		var ServiceId 		= "";
		var ServicePart 	= "D10";
		var Url 			= "handle.do?ServiceName=D1101020011F-service";
	
		var InputDsList 	= "ds_sd=ds_sd:u";
		var OutputDsList 	= "";
		var Argument 		= "insertSD1";
		
		gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
	
		if (ErrorCode < 0) {
	
			return;
		}
		
		var ServiceId 		= "";
		var ServicePart 	= "D10";
		var Url 			= "handle.do?ServiceName=D1101020011F-service";
	
		var InputDsList 	= "ds_sc=ds_sc:u";
		var OutputDsList 	= "";
		var Argument 		= "insertSC1";
		
		gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
	
		if (ErrorCode < 0) {
	
			return;
		}
		
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true);
		
	} else if (G_FACI_COUNT < ds_faci.GetRowCount()) {
 
		ds_sd.UpdateControl = false;
		ds_sc.UpdateControl = false;
		
		var sd_count = 0;
		var sc_count = 0;
		
		for (var i = 0; i < ds_faci.GetRowCount(); i++) {
		
			if (substr(ds_faci.GetColumn(i, "FACI_NUM"), 0, 2) == "MD") {
				
				ds_sd.AddRow();
				ds_sd.SetColumn(sd_count, "FACI_NUM", ds_faci.GetColumn(i, "FACI_NUM"));
				ds_sd.SetColumn(sd_count, "BLOCK_CD", ds_faci.GetColumn(i, "BLOCK_CD"));
				ds_sd.SetColumn(sd_count, "TEAM_CD", " ");
				
				sd_count = sd_count + 1;
			} else {
			
				ds_sc.AddRow();
				ds_sc.SetColumn(sc_count, "FACI_NUM", ds_faci.GetColumn(i, "FACI_NUM"));
				ds_sc.SetColumn(sc_count, "BLOCK_CD", ds_faci.GetColumn(i, "BLOCK_CD"));
				ds_sc.SetColumn(sc_count, "TEAM_CD", " ");
				
				sc_count = sc_count + 1;
			}
		}
		
		for (var i = 0; i < ds_sd.GetRowCount(); i++) {
		
			if (ds_sd.GetRowType(i) == "normal") {
				
				ds_sd.SetRowType(i, "update");
			}
		}		
		
		for (var i = 0; i < ds_sc.GetRowCount(); i++) {
		
			if (ds_sc.GetRowType(i) == "normal") {
				
				ds_sc.SetRowType(i, "update");
			}
		}			    
		
		var ServiceId 		= "";
		var ServicePart 	= "D10";
		var Url 			= "handle.do?ServiceName=D1101020011F-service";
	
		var InputDsList 	= "ds_sd=ds_sd:u";
		var OutputDsList 	= "";
		var Argument 		= "insertSD2";
		
		gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
			
		if (ErrorCode < 0) {
			
			return;
		}	
			
		var ServiceId 		= "";
		var ServicePart 	= "D10";
		var Url 			= "handle.do?ServiceName=D1101020011F-service";
	
		var InputDsList 	= "ds_sc=ds_sc:u";
		var OutputDsList 	= "";
		var Argument 		= "insertSC2";
				
		gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
			
		if (ErrorCode < 0) {
			
			return;
		}
		
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true);
		
	} else {
 	
	    return;
	}	
    
    btn_search_OnClick(obj);
    fn_Search();
    
    parent.fn_serach_count();
}

function btn_insert_OnClick(obj) {
   
    var count = ds_faci.RowCount();
    
    for (var i = count; count + 1 > 0; i--) {
    
		if (ds_faci.GetColumn(i, "CHK") == "1") {
		    
		    ds_faci1.AddRow();
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "STR_LOT_NUM_UNION", ds_faci.GetColumn(i, "STR_LOT_NUM_UNION"));
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "BLOCK_CD"         , G_BLOCK_CD);
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "BLOCK_NM"         , ds_faci.GetColumn(i, "BLOCK_NM"));
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "INST_YMD"		   , ds_faci.GetColumn(i, "INST_YMD"));
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "FACI_NUM"		   , ds_faci.GetColumn(i, "FACI_NUM"));
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "FACI_NM"		   , ds_faci.GetColumn(i, "FACI_NM"));
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "END_LOT_NUM_UNION", ds_faci.GetColumn(i, "END_LOT_NUM_UNION"));
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "CHK"              , "1");
		    ds_faci1.SetColumn(ds_faci1.RowCount() - 1, "TEAM_CD"          , parent.edt_team.text);
		    ds_faci.DeleteRow(i);
		}
		
		count = count - 1;
	}	
}

function btn_delete_OnClick(obj) {

    var count = ds_faci1.RowCount();
   
    for (var i = count; count + 1 > 0; i--) {
    
		if (ds_faci1.GetColumn(i, "CHK") == "1") {
		    
		    ds_faci.AddRow();
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "STR_LOT_NUM_UNION", ds_faci1.GetColumn(i, "STR_LOT_NUM_UNION"));
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "BLOCK_CD", " ");
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "ROAD_FLAG", ds_faci1.GetColumn(i, "ROAD_FLAG"));
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "INST_YMD", ds_faci1.GetColumn(i, "INST_YMD"));
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "FACI_NUM", ds_faci1.GetColumn(i, "FACI_NUM"));
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "FACI_NM", ds_faci1.GetColumn(i, "FACI_NM"));
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "END_LOT_NUM_UNION", ds_faci1.GetColumn(i, "END_LOT_NUM_UNION"));
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "CHK", "1");
		    ds_faci.SetColumn(ds_faci.RowCount() - 1, "TEAM_CD", " ");
		    ds_faci1.DeleteRow(i);
		}
		
		count = count - 1;
	}
}
function grd_faci_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if("CHK" == grd_faci.GetCellProp("body",nCell,"ColId")){
	
		var str_BLOCK_CD = trim(ds_faci.GetColumn(nRow, "BLOCK_CD"));
		
		if(str_BLOCK_CD != '' && str_BLOCK_CD != null){
				ds_faci.SetColumn(nRow, "CHK", "1");
		}
	}
}

function cob_Dong_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cob_Dong.Value == '' || cob_Dong.Value == null){
		edt_Dong.Enable = true;
	}else{
		edt_Dong.Text = "";
		edt_Dong.Enable = false;
	}
}

function edt_Dong_OnChanged(obj,strText)
{
	if(edt_Dong.Text != '' && edt_Dong.Text != null){
		cob_Dong.Value = "";
		cob_Dong.Enable = false;
	}else{
		cob_Dong.Enable = true;
	}
}

function grd_faci_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if("CHK" == grd_faci.GetCellProp("body",nCell,"ColId")){
	if(grd_faci.GetCellProp("head",0,"Text") == '0'){
	
		grd_faci.SetCellProp("head",0,"Text","1");
		
		for(var i=0; i<ds_faci.GetRowCount(); i++){
			
			var str_Blck = trim(ds_faci.GetColumn(i, "BLOCK_CD"));
						
			if(str_Blck == null || str_Blck == ''){
				ds_faci.SetColumn(i, "CHK", "1");
			}
		}
		
	}else{
		
		grd_faci.SetCellProp("head",0,"Text",'0');		
		
		for(var i=0; i<ds_faci.GetRowCount(); i++){
			
			var str_Blck = trim(ds_faci.GetColumn(i, "BLOCK_CD"));
			
			if(str_Blck == null || str_Blck == ''){
				ds_faci.SetColumn(i, "CHK", "0");
			}
		}
	}
	}
}

function grd_faci_OnHeadClick1(obj,nCell,nX,nY,nPivotIndex)
{
	if("CHK" == grd_faci.GetCellProp("body",nCell,"ColId")){
		gfn_GridSelChk(obj, "CHK");
	}
}

function grd_faci_OnMouseOver(obj,nPosX,nPosY,nRow,nCell,nPivotIndex)
{
	if(nCell == '3'){
		grd_faci.ToolTipText = ds_faci.GetColumn(nRow, "STR_LOT_NUM_UNION");
	}else if(nCell == '4'){
		grd_faci.ToolTipText = ds_faci.GetColumn(nRow, "END_LOT_NUM_UNION");
	}else{
		grd_faci.ToolTipText = '';
	}
}

function grd_faci1_OnMouseOver(obj,nPosX,nPosY,nRow,nCell,nPivotIndex)
{
	if(nCell == '3'){
		grd_faci1.ToolTipText = ds_faci1.GetColumn(nRow, "STR_LOT_NUM_UNION");
	}else if(nCell == '4'){
		grd_faci1.ToolTipText = ds_faci1.GetColumn(nRow, "END_LOT_NUM_UNION");
	}else{
		grd_faci1.ToolTipText = '';
	}
}
