function fn_CommonDeleteRow() 
{
    
    if (cob_jo.Value == "" || cob_jo.Value == null) 
    {
    
        alert("조를 선택해주세요.");
        return;
    }
    
    if(confirm("삭제하시겠습니까?"))
    {
		ds_DeleteNum.AddRow();
		ds_DeleteNum.UpdateControl = false;
		ds_DeleteNum.SetRowType(0,"delete");
		ds_DeleteNum.SetColumn(0,"BRANCH_CD",ds_num.getcolumn(Grid0.ClickedRow, "BRANCH_CD"));
		ds_DeleteNum.SetColumn(0,"TEAM_CD",ds_num.getcolumn(Grid0.ClickedRow, "TEAM_CD"));
		ds_DeleteNum.SetColumn(0,"SHIFT_WORK_CD",ds_num.getcolumn(Grid0.ClickedRow, "SHIFT_WORK_CD"));
		ds_DeleteNum.SetColumn(0,"SHIFT_WORK_SEQ",ds_num.getcolumn(Grid0.ClickedRow, "SHIFT_WORK_SEQ"));
		/*권기철11월15일
		ds_DeleteNum.SetColumn(0,"BRANCH_CD",cob_BranchCd.Value);
		ds_DeleteNum.SetColumn(0,"TEAM_CD",cob_team.Value);
		ds_DeleteNum.SetColumn(0,"SHIFT_WORK_CD",cob_jo.Value);
		ds_DeleteNum.SetColumn(0,"SHIFT_WORK_SEQ",cob_joGubun.Value);
		*/
		var strSvcID = "delete";
		var strPart = "D10";
		var strURL = "handle.do?ServiceName=D1101020013F-service";
		var strInDatasets  = "ds_DeleteNum=ds_DeleteNum:u";
		var strOutDatasets  = "";
		var strArgument  = "delete";
		
		gfn_SyncCall( 
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );
		 
		 if(ErrorCode < 0) 
		 {    
			return;
		 }	
		
		if (cob_block.Value != "") 
		{
			fn_delete_block();
		}
	}
	cob_jo_OnChanged();
	fn_CommonSearch();
}

