//------------------------------------------------------------------------------- 
//   함수명: fn_D1_SAVE
//   설명  : 정압기 순찰실적을 등록/업데이트한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Save()
{	
	var row = 0;
	var row1 = 0;
	ds_Plan.ClearData();
	ds_Plan.UpdateControl = false ;

	for(var i =0; i<ds_GovSave.GetRowCount();i++)
	{ 
		if ((ds_GovSave.GetRowType(i) == 'insert') || (ds_GovSave.GetRowType(i) == 'update'))
		{
			if(ds_GovSave.GetColumn(i,"PATROL_YMD") > today())
			{
				alert("점검일자가 현재일보다 큽니다.");
				return;
			}
	
			if(ds_GovSave.GetColumn(i,"CHK_FLAG") == "" || ds_GovSave.GetColumn(i,"CHK_FLAG") == null)
			{
				alert(gfn_GetMessage("M000011","%AAA%","순찰점검 구분을"));
				return;
			}	
					
			if(ds_GovSave.GetColumn(i,"GOV_DUTY_FLAG") == "" || ds_GovSave.GetColumn(i,"GOV_DUTY_FLAG") == null)
			{
				alert(gfn_GetMessage("M000011","%AAA%","분해점검 구분을"));
				return;
			}	
			if (ds_GovSave.GetRowType(i) == 'insert')
			{
				ds_GovSave.SetColumn(i, "CHK_YEAR", substr(ds_GovSave.GetColumn(i, "PATROL_YMD"),0,4));
				var chk_month = substr(ds_GovSave.GetColumn(i, "PATROL_YMD"),4,2);
				
				if(chk_month == "01"|| chk_month =="02"|| chk_month =="03")
				{
					ds_GovSave.SetColumn(i, "CHK_QUARTER", '1');
				}
				else if(chk_month == "04"|| chk_month =="05"|| chk_month =="06")
				{
					ds_GovSave.SetColumn(i, "CHK_QUARTER", '2');
				}
				else if(chk_month == "07"|| chk_month =="08"|| chk_month =="09")
				{
					ds_GovSave.SetColumn(i, "CHK_QUARTER", '3');
				}
				else if(chk_month == "10"|| chk_month =="11"|| chk_month =="12")
				{
					ds_GovSave.SetColumn(i, "CHK_QUARTER", '4');
				}
				else
				{
					alert("순찰일자 오류입니다..");
					return;
				}			
				
				//결재여부 확인 루틴//점검일에 결재 유무 확인
				if (fn_decide(ds_GovSave.GetColumn(i, "PATROL_YMD"), ds_GovSave.GetColumn(i, "BRANCH_CD"), ds_GovSave.GetColumn(i, "TEAM_CD")) == false)
				{
					alert("점검일자(" + ds_GovSave.GetColumn(i, "PATROL_YMD") +  ")는 결재 진행중입니다...등록 불가 합니다..");
					return;
				}
				
				if (ds_GovSave.GetColumn(i,"PLAN_FLAG") == "11")
				{
					if (substr(ds_GovSave.GetColumn(i, "PATROL_YMD"),0,4) != substr(ds_GovSave.GetColumn(i, "PLAN_YMD"),0,4))
					{
						alert("점검년도가 계획년도와 다릅니다. 등록 불가합니다..");
						return;
					}	
		
					if (fn_Quarter(ds_GovSave.GetColumn(i, "PATROL_YMD")) != fn_Quarter(ds_GovSave.GetColumn(i, "PLAN_YMD")))
					{
						alert("점검일자와 계획일자가 동일 분기가 아닙니다.. 등록 불가합니다..");
						return;
					}				
					
					row1 = ds_Plan.AddRow();
					ds_Plan.SetRowType(row1, "update");
					ds_Plan.SetColumn(row1, "CHK_YEAR", ds_GovSave.GetColumn(i, "CHK_YEAR"));		//순찰일자
					ds_Plan.SetColumn(row1, "CHK_QUARTER", ds_GovSave.GetColumn(i, "CHK_QUARTER"));		//순찰일자
					ds_Plan.SetColumn(row1, "BLOCK_CD", ds_GovSave.GetColumn(i, "BLOCK_CD"));		//순찰일자
					ds_Plan.SetColumn(row1, "FACI_NUM", ds_GovSave.GetColumn(i, "FACI_NUM"));		//정압실번호
					ds_Plan.SetColumn(row1, "DETAIL_NUM", ds_GovSave.GetColumn(i, "GOV_NUM"));		//정압기번호
					ds_Plan.SetColumn(row1, "PLAN_YMD", ds_GovSave.GetColumn(i, "PLAN_YMD"));		//순찰일자
					ds_Plan.SetColumn(row1, "PATROL_YMD", ds_GovSave.GetColumn(i, "PATROL_YMD"));		//순찰일자
					ds_Plan.SetColumn(row1, "CHK_FLAG", "Y");
					ds_Plan.SetColumn(row1, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
					ds_Plan.SetColumn(row1, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP				
				}

				ds_GovSave.SetColumn(i, "CRT_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
				ds_GovSave.SetColumn(i, "CRT_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP
				ds_GovSave.SetColumn(i, "UPD_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
				ds_GovSave.SetColumn(i, "UPD_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP			
			}
			else if(ds_GovSave.GetRowType(i) == 'update')
			{
				if(ds_GovSave.GetColumn(i,"DECIDE_PROC_STS") == '0' || ds_GovSave.GetColumn(i,"DECIDE_PROC_STS") == '1' || ds_GovSave.GetColumn(i, "DECIDE_PROC_STS") == '2')
				{
					alert("점검일자(" + ds_GovSave.GetColumn(i, "PATROL_YMD") +  ")는 결재(완료/진행중)으로 내역을 수정할 수 없습니다...");
					return;
				}
				else
				{
					ds_GovSave.SetColumn(i, "UPD_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
					ds_GovSave.SetColumn(i, "UPD_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP			
				}
			}
		}
	}

	var strSvcID         = "gov_check";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1202010010F-service";					
	var strInDatasets    = "ds_GovSave=ds_GovSave:u ds_Plan=ds_Plan:u";
	var strOutDatasets   = "";		
	var strArgument      = "gov_check=1";

	gfn_syncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				);
			   
	if(ErrorCode<0)
	{
		return;
	}

	alert("저장/수정 되었습니다.");
	
}

function fn_decide(PATROL_YMD, BRANCH_CD, TEAM_CD)
{
	ds_Decide.ClearData();
	
	var strSvcID        = "decide";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Decide=ds_Decide";
	var strArgument     = "decide=1";
		strArgument		+= " PATROL_YMD="  + PATROL_YMD;
		strArgument		+= " BRANCH_CD=" + BRANCH_CD;
		strArgument		+= " TEAM_CD="  + TEAM_CD;

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );


	if(ds_Decide.RowCount() == 0) return true;

	if(ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '0' || ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '1' || ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '2')
	{
		return false;
	}	
	else
	{
		return true;
	}	
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Addrowm()
//   설명  :  구간 행추가 
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Addrowm()
{

	if(ds_GovRoom.GetRowCount()==0)
	{
		return;
	}

    if((gfn_GetGlobalValue("G_BRANCH_CD") == "999999") AND (gfn_GetGlobalValue("G_USER_NO") != '700004008'))
    {
		return;
	}
	else if(ds_GovRoom.GetColumn(ds_GovRoom.row, "FACI_NUM") == '' || ds_GovRoom.GetColumn(ds_GovRoom.row, "FACI_NUM") == null)	
	{
        alert("실적 등록할 시설물을 선택하세요");
        return;
    }    
    else if(ds_GovRoom.GetRowType(0)=="insert") 
    {
		return;
    }

    //계획 및 분기계획건의 경우 점검일자가 등록되어 있는 경우는 실적 추가등록 불가
    if(cob_Gubun.value=="10" || cob_Gubun.value=="40") // 계획,분기계획
    {
    	if(length(trim(ds_GovRoom.GetColumn(ds_GovRoom.row, "PATROL_YMD"))) > 0)            
		{
			alert("해당 계획일자의 점검 실적이 등록되어 있습니다...");
			return;
		}
	}	    

	ds_GovSave.InsertRow(0);			//insert시 맨위에 행추가
	ds_GovSave.SetColumn(0, "FACI_NUM"	    ,ds_GovRoom.GetColumn(ds_GovRoom.Row,"FACI_NUM") );
	ds_GovSave.SetColumn(0, "GOV_NUM"	    ,ds_GovRoom.GetColumn(ds_GovRoom.Row,"GOV_NUM") );
	ds_GovSave.SetColumn(0, "FACI_GOV_NUM"  ,ds_GovRoom.GetColumn(ds_GovRoom.Row,"FACI_NUM") + '_' + ds_GovRoom.GetColumn(ds_GovRoom.Row,"GOV_NUM"));
	grd_MultiSave.SetCellProp("body",3,"Edit"   ,"expr:decode(rowtype,'insert','date','none')"); 
	ds_GovSave.SetColumn(0, "PATROL_YMD", Today());  
	ds_GovSave.SetColumn(0, "GOV_FLAG"	    ,ds_GovRoom.GetColumn(ds_GovRoom.Row,"GOV_FLAG") );
	ds_GovSave.SetColumn(0, "BRANCH_CD"     ,ds_GovRoom.GetColumn(ds_GovRoom.row, "BRANCH_CD"));
	ds_GovSave.SetColumn(0, "TEAM_CD"       ,ds_GovRoom.GetColumn(ds_GovRoom.row, "TEAM_CD"));
	ds_GovSave.SetColumn(0, "SHIFT_WORK_CD" ,cob_PShiftWorkCd.Value );
	ds_GovSave.SetColumn(0, "ZIP_NO1"       ,ds_GovRoom.GetColumn(ds_GovRoom.row, "ZIP_NO1"));
	ds_GovSave.SetColumn(0, "ZIP_NO2"       ,ds_GovRoom.GetColumn(ds_GovRoom.row, "ZIP_NO2"));
	ds_GovSave.SetColumn(0, "BLOCK_CD"      ,ds_GovRoom.GetColumn(ds_GovRoom.row, "BLOCK_CD"));
	ds_GovSave.SetColumn(0, "PLAN_FLAG"     ,ds_GovRoom.GetColumn(ds_GovRoom.row, "PLAN_FLAG"));
	ds_GovSave.SetColumn(0,"TAKER_EMPID1",edt_empid1.text);
	ds_GovSave.SetColumn(0,"TAKER_EMPID2",edt_empid2.text);
	ds_GovSave.SetColumn(0,"TAKER_EMPID3",edt_empid3.text);
	ds_GovSave.SetColumn(0,"TAKER_EMPID1_NM",edt_empnm1.text);
	ds_GovSave.SetColumn(0,"TAKER_EMPID2_NM",edt_empnm2.text);
	ds_GovSave.SetColumn(0,"TAKER_EMPID3_NM",edt_empnm3.text);

	//비계획인 경우는 계획일자가 없다.--권기철 2010/01/06
    if(cob_Gubun.value == '30')
    {
		ds_GovSave.SetColumn(0,"PLAN_YMD",'');
    }
    else{
		ds_GovSave.SetColumn(0,"PLAN_YMD",ds_GovRoom.GetColumn(ds_GovRoom.Row,"PLAN_YMD"));
    }	   
}


function fn_D1_Delete()
{   
	if (ds_GovSave.GetRowCount() == 0)
	{
		return;
	}
		
	if(ds_GovSave.GetRowType(ds_GovSave.row) == "insert")
	{
		ds_GovSave.DeleteRow(ds_GovSave.row);
		return;
	}

	if (ds_GovSave.GetColumn(ds_GovSave.row, "DECIDE_PROC_STS") == '0' || ds_GovSave.GetColumn(ds_GovSave.row, "DECIDE_PROC_STS") == '1'  || ds_GovSave.GetColumn(ds_GovSave.row, "DECIDE_PROC_STS") == '2') 
	{	
		alert("결재(진행/완료)중으로 삭제가 불가합니다...");
		return;
    }else if(confirm("삭제하시겠습니까?"))
    {
		ds_Plan.ClearData();
		ds_Plan.UpdateControl = false ;

		ds_Plan.AddRow();
		ds_Plan.SetRowType(0, "update");		
		ds_Plan.SetColumn(0, "CHK_YEAR", ds_GovSave.GetColumn(ds_GovSave.row, "CHK_YEAR"));		//순찰일자
		ds_Plan.SetColumn(0, "CHK_QUARTER", ds_GovSave.GetColumn(ds_GovSave.row, "CHK_QUARTER"));		//순찰일자
		ds_Plan.SetColumn(0, "BLOCK_CD", ds_GovSave.GetColumn(ds_GovSave.row, "BLOCK_CD"));		//순찰일자
		ds_Plan.SetColumn(0, "FACI_NUM", ds_GovSave.GetColumn(ds_GovSave.row, "FACI_NUM"));		//순찰일자
		ds_Plan.SetColumn(0, "DETAIL_NUM", ds_GovSave.GetColumn(ds_GovSave.row, "GOV_NUM"));		//정압기번호		
		ds_Plan.SetColumn(0, "PLAN_YMD", ds_GovSave.GetColumn(ds_GovSave.row, "PLAN_YMD"));		//순찰일자
		ds_Plan.SetColumn(0, "PATROL_YMD", '');		//순찰일자초기화
		ds_Plan.SetColumn(0, "CHK_FLAG", "N");
		ds_Plan.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_Plan.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP						

		ds_GovSave.DeleteRow(ds_GovSave.row);

		var strSvcID         = "gov_check";
		var strPart          = "D10";	
		var strURL           = "handle.do?ServiceName=D1202010010F-service";					
		var strInDatasets    = "ds_GovSave=ds_GovSave:u ds_Plan=ds_Plan:u";
		var strOutDatasets   = "";		
		var strArgument      = "gov_check=1";
	
		gfn_SyncCall(
						 strSvcID
					   , strPart  	 
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );
	
		if(ErrorCode < 0)
		{
			return;
		}
		else
		{
			alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"));
		}		
			
	}
}

