function fn_D1_PortletFind(str_FaciNum, str_GoveNum, str_PlanYmd){
		
		var strSvcID = "fn_D1_Find";
		var strPart = "D10";				
		var strURL = "handle.do?ServiceName=D1_PatrolCheckResultMgr-service"
				   + "&BRANCH_CD="		+ ""		  //지사코드
				   + "&BLOCK_CD=" 		+ ""       	  //구코드
				   + "&TEAM_CD=" 		+ ""		  //팀코드
				   + "&SHIFT_WORK_CD="  + ""		  //조코드
				   + "&PATROL_YMD=" 	+ str_PlanYmd //순찰일자
				   + "&FACI_NUM="	    + str_FaciNum //시설물번호
				   ; 
		var strInDatasets   = "";
		var strOutDatasets  = "ds_Multi_T02=ds_Multi_T02";
		var strArgument     = "find2";
			
		gfn_SyncCall(strSvcID,
					  strPart,
					  strURL,
					  strInDatasets,
					  strOutDatasets,
					  strArgument
		);
		
		if(ErrorCode < 0)
		{
			return;
		}
				 
		if(ds_Multi_T02.GetRowCount()==0)
		{
			grd_Multi.NoDataText=gfn_GetMessage("M000022","%AAA%","조회결과가");
			return;
		}
		
		grd_Multi_OnCellClick ("",0,"","","","");
		
		for(var i=0; i<ds_GovRoom.GetRowCount(); i++){
			if(ds_GovRoom.GetColumn(i, "GOV_NUM") == str_GoveNum){
			   ds_GovRoom.SelectRow(i);
			   ds_GovRoom.row = i;
			}	
		}				
}


function grd_MultiSave_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	grd_MultiSave.SetCellProp("body",5,"Edit","none");		//결재상태
	grd_MultiSave.SetCellProp("body",6,"Edit","none");		//정압기구분
	
	if(ds_GovSave.GetRowType(nRow) == "insert")
	{
		grd_MultiSave.SetCellProp("body",3,"Edit","date");		//점검일자
	}
	else
	{
		grd_MultiSave.SetCellProp("body",3,"Edit","none");		//점검일자		
	}	
	
	if(ds_GovSave.GetColumn(nRow,"DECIDE_PROC_STS") == '0' || ds_GovSave.GetColumn(nRow,"DECIDE_PROC_STS") == '1' || ds_GovSave.GetColumn(nRow,"DECIDE_PROC_STS") == '2')
	{
		grd_MultiSave.SetCellProp("body",7,"Edit","none");		//순찰점검구분
		grd_MultiSave.SetCellProp("body",8,"Edit","none");		//분해점검
		grd_MultiSave.SetCellProp("body",9,"Edit","none");		//1차압력
		grd_MultiSave.SetCellProp("body",10,"Edit","none");		//2차압력
		grd_MultiSave.SetCellProp("body",11,"Edit","none");	//누출여부
		grd_MultiSave.SetCellProp("body",12,"Edit","none");	//작동상태
		grd_MultiSave.SetCellProp("body",13,"Edit","none");	//차압계
		grd_MultiSave.SetCellProp("body",14,"Edit","none");	//경보기
		grd_MultiSave.SetCellProp("body",15,"Edit","none");	//SSV차단
		grd_MultiSave.SetCellProp("body",16,"Edit","none");	//SSV작동
		grd_MultiSave.SetCellProp("body",17,"Edit","none");		//특기사항
	}
	else
	{
		grd_MultiSave.SetCellProp("body",7,"Edit","combo");		//순찰점검구분
		grd_MultiSave.SetCellProp("body",8,"Edit","combo");		//분해점검
		grd_MultiSave.SetCellProp("body",9,"Edit","number");		//1차압력
		grd_MultiSave.SetCellProp("body",10,"Edit","number");		//2차압력
		grd_MultiSave.SetCellProp("body",11,"Edit","combo");	//누출여부
		grd_MultiSave.SetCellProp("body",12,"Edit","combo");	//작동상태
		grd_MultiSave.SetCellProp("body",13,"Edit","combo");	//차압계
		grd_MultiSave.SetCellProp("body",14,"Edit","combo");	//경보기
		grd_MultiSave.SetCellProp("body",15,"Edit","combo");	//SSV차단
		grd_MultiSave.SetCellProp("body",16,"Edit","combo");	//SSV작동
		grd_MultiSave.SetCellProp("body",17,"Edit","normal");		//특기사항
	}			
}

