var Quarter = '';
var Patrol_Str_ymd = '';

function D1202010010F_T04_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	gfn_D1_SetGrid_Sort(grd_Multi1);
	gfn_D1_SetGrid_Sort(grd_MultiSave);
	
	gfn_SetGrdComboBox_Comcode("grd_MultiSave",5,"A10411");	
	grd_MultiSave.SetCellProp("body",5,"Edit","none");
	gfn_SetGrdComboBox_Comcode("grd_MultiSave",6,"D10209");		
	
	if (IsExistVar("PORTLET_FLAG", true)){
		if (PORTLET_FLAG == "p"){
			fn_D1_PortletFind(FACI_NUM, PLAN_YMD);
		}
	}
	
	fn_chkQuarter();
}


function fn_chkQuarter()
{
	if(SubStr(cal_PPatrolYmd.Value,4,2)=="01"||SubStr(cal_PPatrolYmd.Value,4,2)=="02"||SubStr(cal_PPatrolYmd.Value,4,2)=="03")
	{
		Quarter = '1';
		Patrol_Str_ymd = SubStr(cal_PPatrolYmd.Value,0,4) + "0101";
	}
	else if(SubStr(cal_PPatrolYmd.Value,4,2)=="04"||SubStr(cal_PPatrolYmd.Value,4,2)=="05"||SubStr(cal_PPatrolYmd.Value,4,2)=="06")
	{
		Quarter = '2';
		Patrol_Str_ymd = SubStr(cal_PPatrolYmd.Value,0,4) + "0401";
	}
	else if(SubStr(cal_PPatrolYmd.Value,4,2)=="07"||SubStr(cal_PPatrolYmd.Value,4,2)=="08"||SubStr(cal_PPatrolYmd.Value,4,2)=="09")
	{
		Quarter = '3';
		Patrol_Str_ymd = SubStr(cal_PPatrolYmd.Value,0,4) + "0701";
	}
	else if(SubStr(cal_PPatrolYmd.Value,4,2)=="10"||SubStr(cal_PPatrolYmd.Value,4,2)=="11"||SubStr(cal_PPatrolYmd.Value,4,2)=="12")
	{
		Quarter = '4';
		Patrol_Str_ymd = SubStr(cal_PPatrolYmd.Value,0,4) + "1001";
	}    
}   

function fn_Quarter(chk_ymd)
{
	if(SubStr(chk_ymd,4,2)=="01"||SubStr(chk_ymd,4,2)=="02"||SubStr(chk_ymd,4,2)=="03")
	{
		return '1';
	}
	else if(SubStr(chk_ymd,4,2)=="04"||SubStr(chk_ymd,4,2)=="05"||SubStr(chk_ymd,4,2)=="06")
	{
		return '2';
	}
	else if(SubStr(chk_ymd,4,2)=="07"||SubStr(chk_ymd,4,2)=="08"||SubStr(chk_ymd,4,2)=="09")
	{
		return '3';
	}
	else if(SubStr(chk_ymd,4,2)=="10"||SubStr(chk_ymd,4,2)=="11"||SubStr(chk_ymd,4,2)=="12")
	{
		return '4';
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_ExcelExport()
//  설명: 특별관리시설 엑셀로 저장
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_ExcelExport()
{ 
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :"     + RPad(cob_PBranchCd.Text," ",20);
    SearchCondition = SearchCondition + "팀 :"       + RPad(cob_PTeamCd.Text," ",20);
    SearchCondition = SearchCondition + "조 :"       + RPad(cob_PShiftWorkCd.Text," ",20);
    SearchCondition = SearchCondition + "구간코드 :" + RPad(cob_block.Text," ",20);
    SearchCondition = SearchCondition + "순찰일자 :" + RPad(cal_PPatrolYmd.Text," ",20);
    SearchCondition = SearchCondition + "시설물번호 :" + RPad(edt_PFaciNum.Text," ",20);
    
	gfn_ExportExcel("테스트박스 순찰점검등록", 5, SearchCondition, "grd_Multi1");
}

//-------------------------------------------------------------------------------
// 함수명   : fn_D1_Find()
// 설명     : 순찰점검 주 계획을 조회한다.
// return 내용 : 없음
//-------------------------------------------------------------------------------
function fn_D1_Find()
{
	fn_chkQuarter();
	
	ds_TestBox.ClearData();
	 
	ds_Search.ClearData();
	var row = ds_Search.AddRow();

	ds_Search.SetColumn(row,"CHK_YEAR",SubStr(cal_PPatrolYmd.Value,0,4));
	ds_Search.SetColumn(row,"CHK_QUARTER",Quarter);	
	ds_Search.SetColumn(row,"BRANCH_CD",cob_PBranchCd.Value);
	ds_Search.SetColumn(row,"BLOCK_CD",cob_block.Value);
	ds_Search.SetColumn(row,"STR_YMD",Patrol_Str_ymd);
	ds_Search.SetColumn(row,"PLAN_YMD",cal_PPatrolYmd.Value);
	ds_Search.SetColumn(row,"FACI_NUM",edt_PFaciNum.Text);	
	
	
	if(cob_PShiftWorkCd.value=="10"||cob_PShiftWorkCd.value=="20")
	{
		ds_Search.SetColumn(row,"TEAM_CD",cob_PTeamCd.Value);
	}
	else
	{
		ds_Search.SetColumn(row,"TEAM_CD",'');
	}
	if(cob_PShiftWorkCd.value=="10")
	{
		ds_Search.SetColumn(row,"SHIFT_WORK_CD",cob_PShiftWorkCd.Value);
	}
	else
	{
		ds_Search.SetColumn(row,"SHIFT_WORK_CD",'');
	}	

	grd_Multi1.SetCellProp("head",9,"text"   ,"최근점검일자");

	ds_TBoxSave.ClearData();
	
	if(cob_Gubun.value=="10" || cob_Gubun.value=="40") // 계획,분기계획
	{
		grd_Multi1.SetCellProp("head",9,"text"   ,"점검일자");
		if (cob_Gubun.value=="40")
		{
			ds_Search.SetColumn(row,"PLAN_YMD",'');
		}	    
	
		var strSvcID = "plan_testbox";
		var strPart  = "D10";
		var strURL   = "handle.do?ServiceName=D1202010010F-service";
		var strInDatasets  = "ds_Search=ds_Search";
		var strOutDatasets  = "ds_TestBox=ds_Multi";
		var strArgument  = "plan_testbox=1";
					
		gfn_syncCall( 
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );					  
    }
    else if(cob_Gubun.value=="20") // 미점검 
    {
		var strSvcID = "nocheck_testbox";
		var strPart  = "D10";
		var strURL   = "handle.do?ServiceName=D1202010010F-service";
		var strInDatasets  = "ds_Search=ds_Search";
		var strOutDatasets  = "ds_TestBox=ds_Multi";
		var strArgument  = "nocheck_testbox=1";
					
		gfn_syncCall( 
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );					  
    }
    else if(cob_Gubun.value=="30") //  비계획
    {
		var strSvcID = "noplan_testbox";
		var strPart  = "D10";
		var strURL   = "handle.do?ServiceName=D1202010010F-service";
		var strInDatasets  = "ds_Search=ds_Search";
		var strOutDatasets  = "ds_TestBox=ds_Multi";
		var strArgument  = "noplan_testbox=1";
					
		gfn_syncCall( 
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );						   			      
    }

	if(ErrorCode < 0)
	{
		return;
	}		
	
	fn_D1_Cnt();
}

// 미점검 , 금일점검, 점검대상 카운트 
function fn_D1_Cnt()
{
	ds_Search_cnt.ClearData();
	ds_Search_cnt.AddRow(); 

	ds_Search_cnt.SetColumn(0,"CHK_YEAR",SubStr(cal_PPatrolYmd.Value,0,4));
	ds_Search_cnt.SetColumn(0,"CHK_QUARTER",Quarter);	
	ds_Search_cnt.SetColumn(0,"BRANCH_CD",cob_PBranchCd.Value);
	ds_Search_cnt.SetColumn(0,"BLOCK_CD",cob_block.Value);
	ds_Search_cnt.SetColumn(0,"FACI_FLAG","TB");
	ds_Search_cnt.SetColumn(0,"PATROL_YMD",cal_PPatrolYmd.Value);
	
	ds_Count.ClearData();
	ds_Tbcnt.ClearData();

	var strSvcID = "plan_cnt";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets  = "ds_Search_cnt=ds_Search_cnt";
	var strOutDatasets  = "ds_Tbcnt=ds_Count";
	var strArgument  = "plan_cnt=1";
				
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );						   			      
				   	
}


function grd_Multi1_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_TestBox.rowcount() == 0)
	{
		return;
	}	
	
	if("FACI_CARD" == grd_Multi1.GetCellProp("body",nCell,"ColId"))
	{
		fn_Card(nRow);											//시설물카드
		return;
	}

	if("GIS_POSITION" == grd_Multi1.GetCellProp("body",nCell,"ColId"))
	{
		var args = "BUNGI='10' FACI_NUM="+ds_TestBox.GetColumn(nRow,"FACI_NUM");
		gfn_Dialog("D10","D1402060011U",args,1208,840,true,-1,-1);	//GIS 불러오기
		return;
	}
	
	if("WORK_INFO" == grd_Multi1.GetCellProp("body",nCell,"ColId"))		//공사정보조회
	{
		if(ds_TestBox.GetColumn(ds_TestBox.row,"WORK_NUM")==""||ds_TestBox.GetColumn(ds_TestBox.row,"WORK_NUM")==null)
		{
			alert("공사번호가 없습니다..");
			return;
		}
		var strPrefix = "B20";
		var strFormid = "B2204010010F";
		var strArg    = "fa_BranchCd=" + ds_TestBox.GetColumn(ds_TestBox.row,"BRANCH_CD"); 
		    strArg   += "  fa_WorkNum="+ ds_TestBox.GetColumn(ds_TestBox.row,"WORK_NUM"); 
		    
		gfn_NewWindow(strPrefix,strFormid,strArg);
		return;
	}	
}

//------------------------------------------------------------------------------- 
//   함수명: fn_Card(obj)
//   설명  :  밸브 시설물카드조회 팝업
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_Card(nRow)
{
	// 함수 호출용 파라미터를 설정한다
	var strTitle ="T/B시설물카드조회"; 
	var strPart ="D10";           
	var strReport="D1202010036P"; 
	var strParameter="FACI_NUM=" 	+ds_TestBox.GetColumn(nRow,"FACI_NUM")    
					+",PATROL_YMD=" +ds_TestBox.GetColumn(nRow,"PATROL_YMD")
					;
	//gfn_bt_d2_Report
	// 공통 레포트 팝업 함수 호출 
	gfn_ReportPopup(strTitle,strPart,strReport,strParameter);
} 

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_SAVE
//   설명  : 테스트박스를 등록/업데이트한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Save()
{
	var row = 0;
	var row1 = 0;
	ds_SaveBad.ClearData();
	ds_SaveBad.UpdateControl = false ;
	ds_Plan.ClearData();
	ds_Plan.UpdateControl = false ;
		
	for(var i =0; i<ds_TBoxSave.GetRowCount();i++)
	{ 
		if(ds_TBoxSave.GetColumn(i,"PATROL_YMD") > today())
		{
			alert("점검일자가 현재일보다 큽니다.");
			return;
		}
		
		if(ds_TBoxSave.GetColumn(i,"OPERAT_STS_CD") == "" || ds_TBoxSave.GetColumn(i,"OPERAT_STS_CD") == null)
		{
			alert(gfn_GetMessage("M000011","%AAA%","외관상태를"));
			return;
		}
		if(ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT") == "" || ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT") == null)
		{
			alert(gfn_GetMessage("M000011","%AAA%","측정전위를"));
			return;
		}
		else if(ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT") < 0) 
		{
			alert(gfn_GetMessage("M000011","%AAA%","측정전위는 -값이 이미적용되어 +값 또는 0이상의 값으로 입력바랍니다."));
			return;		
		}
		 
		if(ds_TBoxSave.GetColumn(i,"MG_ELECT_POT") < 0)
		{
			alert(gfn_GetMessage("M000011","%AAA%","MG전위는 -값이 이미적용되어 +값 또는 0이상의 값으로 입력바랍니다."));
			return;		
		}
					
		if (ds_TBoxSave.GetRowType(i) == 'insert')
		{
			ds_TBoxSave.SetColumn(i, "CHK_YEAR", substr(ds_TBoxSave.GetColumn(i, "PATROL_YMD"),0,4));
			var chk_month = substr(ds_TBoxSave.GetColumn(i, "PATROL_YMD"),4,2);
			
			if(chk_month == "01"|| chk_month =="02"|| chk_month =="03")
			{
				ds_TBoxSave.SetColumn(i, "CHK_QUARTER", '1');
			}
			else if(chk_month == "04"|| chk_month =="05"|| chk_month =="06")
			{
				ds_TBoxSave.SetColumn(i, "CHK_QUARTER", '2');
			}
			else if(chk_month == "07"|| chk_month =="08"|| chk_month =="09")
			{
				ds_TBoxSave.SetColumn(i, "CHK_QUARTER", '3');
			}
			else if(chk_month == "10"|| chk_month =="11"|| chk_month =="12")
			{
				ds_TBoxSave.SetColumn(i, "CHK_QUARTER", '4');
			}
			else
			{
				alert("순찰일자 오류입니다..");
				return;
			}			
			
			//결재여부 확인 루틴//점검일에 결재 유무 확인
			if (fn_decide(ds_TBoxSave.GetColumn(i, "PATROL_YMD"), ds_TBoxSave.GetColumn(i, "BRANCH_CD"), ds_TBoxSave.GetColumn(i, "TEAM_CD")) == false)
			{
				alert("점검일자(" + ds_TBoxSave.GetColumn(i, "PATROL_YMD") +  ")는 결재 진행중입니다...등록 불가 합니다..");
				return;
			}
			
			if (ds_TBoxSave.GetColumn(i,"PLAN_FLAG") == "11")
			{
				if (substr(ds_TBoxSave.GetColumn(i, "PATROL_YMD"),0,4) != substr(ds_TBoxSave.GetColumn(i, "PLAN_YMD"),0,4))
				{
					alert("점검년도가 계획년도와 다릅니다. 등록 불가합니다..");
					return;
				}	
	
				if (fn_Quarter(ds_TBoxSave.GetColumn(i, "PATROL_YMD")) != fn_Quarter(ds_TBoxSave.GetColumn(i, "PLAN_YMD")))
				{
					alert("점검일자와 계획일자가 동일 분기가 아닙니다.. 등록 불가합니다..");
					return;
				}				
				
				row1 = ds_Plan.AddRow();
				ds_Plan.SetRowType(row1, "update");
				ds_Plan.SetColumn(row1, "CHK_YEAR", ds_TBoxSave.GetColumn(i, "CHK_YEAR"));		//순찰일자
				ds_Plan.SetColumn(row1, "CHK_QUARTER", ds_TBoxSave.GetColumn(i, "CHK_QUARTER"));		//순찰일자
				ds_Plan.SetColumn(row1, "BLOCK_CD", ds_TBoxSave.GetColumn(i, "BLOCK_CD"));		//순찰일자
				ds_Plan.SetColumn(row1, "FACI_NUM", ds_TBoxSave.GetColumn(i, "FACI_NUM"));		//순찰일자
				ds_Plan.SetColumn(row1, "DETAIL_NUM", '0001');		//상세번호
				ds_Plan.SetColumn(row1, "PLAN_YMD", ds_TBoxSave.GetColumn(i, "PLAN_YMD"));		//순찰일자
				ds_Plan.SetColumn(row1, "PATROL_YMD", ds_TBoxSave.GetColumn(i, "PATROL_YMD"));		//순찰일자
				ds_Plan.SetColumn(row1, "CHK_FLAG", "Y");
				ds_Plan.SetColumn(row1, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
				ds_Plan.SetColumn(row1, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP				
			}
			//alert(toNumber(ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT")));
			
			
			// 2012.05.23 권기철 과장님 추가요청 사항_이건민 측정전위 값 100 ~ 849이면 불량으로 인한 if문 주석처리
			//if(toNumber(ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT") != 0) && toNumber(ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT") <= 850))
			
			// 2012.05.23 권기철 과장님 추가요청 사항_이건민 측정전위 값 100 ~ 849이면 불량
			if(toNumber(ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT") != 0) && toNumber(ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT") >= 100) && toNumber(ds_TBoxSave.GetColumn(i,"MEAS_ELECT_POT") <= 849))
			{ 
				row = ds_SaveBad.AddRow();
				ds_SaveBad.SetRowType(row, "insert");
				
				ds_TBoxSave.SetColumn(i, "CHK_RSLT", "N");	//점검결과
				ds_SaveBad.SetColumn(row, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
				ds_SaveBad.SetColumn(row, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP						
				ds_SaveBad.SetColumn(row, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
				ds_SaveBad.SetColumn(row, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP
				ds_SaveBad.SetColumn(row, "FACI_NUM", ds_TBoxSave.GetColumn(i, "FACI_NUM")); //시설물번호
				ds_SaveBad.SetColumn(row, "DETAIL_NUM", "0001");							//상세번호(TB는 상세번호가 없기 때문에 TB00으로 무조건 셋팅)
				ds_SaveBad.SetColumn(row, "PATROL_YMD", ds_TBoxSave.GetColumn(i, "PATROL_YMD"));		//순찰일자
				ds_SaveBad.SetColumn(row, "ELECT_POT_MEAS", ds_TBoxSave.GetColumn(i, "MEAS_ELECT_POT")); //측정전위(특별관리)
				
				ds_SaveBad.SetColumn(row, "BRANCH_CD", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "BRANCH_CD"));	//지사코드
				ds_SaveBad.SetColumn(row, "TEAM_CD",  ds_TBoxSave.GetColumn(ds_TBoxSave.row, "TEAM_CD"));   //팀코드
				ds_SaveBad.SetColumn(row, "SHIFT_WORK_CD",  ds_TBoxSave.GetColumn(ds_TBoxSave.row, "SHIFT_WORK_CD"));	//팀코드
				ds_SaveBad.SetColumn(row, "ZIP_NO1", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "ZIP_NO1"));	//조코드
				ds_SaveBad.SetColumn(row, "ZIP_NO2", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "ZIP_NO2"));	//조코드
				ds_SaveBad.SetColumn(row, "CHK_YEAR",  ds_TBoxSave.GetColumn(ds_TBoxSave.row, "CHK_YEAR"));	//지사코드
				ds_SaveBad.SetColumn(row, "CHK_QUARTER", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "CHK_QUARTER")); //구코드
				ds_SaveBad.SetColumn(row, "BLOCK_CD", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "BLOCK_CD"));						//팀코드

				ds_SaveBad.SetColumn(row, "PLAN_YMD", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "PLAN_YMD"));			//계획일자								
				ds_SaveBad.SetColumn(row, "PLAN_FLAG", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "PLAN_FLAG"));			//계획구분				
			}
			else
			{
				ds_TBoxSave.SetColumn(i, "CHK_RSLT", "Y");	//점검결과
			}	
			ds_TBoxSave.SetColumn(i, "CRT_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
			ds_TBoxSave.SetColumn(i, "CRT_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP
			ds_TBoxSave.SetColumn(i, "UPD_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
			ds_TBoxSave.SetColumn(i, "UPD_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP			
		}
		else if(ds_TBoxSave.GetRowType(i) == 'update')
		{
			if(ds_TBoxSave.GetColumn(i,"DECIDE_PROC_STS") == '0' || ds_TBoxSave.GetColumn(i, "DECIDE_PROC_STS") == '1' || ds_TBoxSave.GetColumn(i, "DECIDE_PROC_STS") == '2')
			{
				alert("점검일자(" + ds_TBoxSave.GetColumn(i, "PATROL_YMD") +  ")는 결재(완료/진행중)으로 내역을 수정할 수 없습니다...");
				return;
			}
			else
			{
				ds_TBoxSave.SetColumn(i, "UPD_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
				ds_TBoxSave.SetColumn(i, "UPD_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP			
			}
		}
	}
	
	var strSvcID         = "testbox_chk_ins";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1202010010F-service";					
	var strInDatasets    = "ds_TBoxSave=ds_TBoxSave:u ds_SaveBad=ds_SaveBad:u ds_Plan=ds_Plan:u";
	var strOutDatasets   = "";		
	var strArgument      = "testbox_chk_ins=1";

	gfn_syncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				);
			   
	if(ErrorCode<0)
	{
		return;
	}

	alert("저장/수정 되었습니다.");
	
	ds_TestBox_OnRowPosChanged();	
}
function fn_decide(PATROL_YMD, BRANCH_CD, TEAM_CD)
{
	ds_Decide.ClearData();
	
	var strSvcID        = "decide";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Decide=ds_Decide";
	var strArgument     = "decide=1";
		strArgument		+= " PATROL_YMD="  + PATROL_YMD;
		strArgument		+= " BRANCH_CD=" + BRANCH_CD;
		strArgument		+= " TEAM_CD="  + TEAM_CD;

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );

	if(ds_Decide.RowCount() == 0) return true;
	
	if(ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '0' || ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '1' || ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '2')
	{
		return false;
	}	
	else
	{
		return true;
	}	
}

