//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Addrowm()
//   설명  :  구간 행추가 
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Addrowm()
{
	if(ds_TestBox.GetRowCount()==0)
	{
		return;
	}
	/*
	if(gfn_GetGlobalValue("G_USER_NO") != "199443297" && gfn_GetGlobalValue("G_USER_NO") != "700004008")
    {
		return;
	}
	*/
	else if(ds_TestBox.GetColumn(ds_TestBox.row, "FACI_NUM") == '' || ds_TestBox.GetColumn(ds_TestBox.row, "FACI_NUM") == null)	
	{
        alert("실적 등록할 시설물을 선택하세요");
        return;
    }else if(ds_TBoxSave.GetRowType(0)=="insert") {
		return;
    }
    
    //계획 및 분기계획건의 경우 점검일자가 등록되어 있는 경우는 실적 추가등록 불가
    if(cob_Gubun.value=="10" || cob_Gubun.value=="40") // 계획,분기계획
    {
    	if(length(trim(ds_TestBox.GetColumn(ds_TestBox.row, "PATROL_YMD"))) > 0)            
		{
			alert("해당 계획일자의 점검 실적이 등록되어 있습니다...");
			return;
		}
	}	

	ds_TBoxSave.InsertRow(0);		//insert시 맨위에 행추가
	ds_TBoxSave.SetColumn(0,"FACI_NUM", ds_TestBox.GetColumn(ds_TestBox.row, "FACI_NUM"));
	ds_TBoxSave.SetColumn(0,"PATROL_YMD",Today());
	grd_MultiSave.SetCellProp("body",3,"Edit"   ,"expr:decode(rowtype,'insert','date','none')"); 
	grd_MultiSave.SetCellProp("body",8,"Edit"   ,"expr:decode(rowtype,'insert','number','none')"); 
	
	ds_TBoxSave.SetColumn(0, "BRANCH_CD"     ,ds_TestBox.GetColumn(ds_TestBox.row, "BRANCH_CD"));
	ds_TBoxSave.SetColumn(0, "TEAM_CD"       ,ds_TestBox.GetColumn(ds_TestBox.row, "TEAM_CD"));
	ds_TBoxSave.SetColumn(0, "SHIFT_WORK_CD" ,cob_PShiftWorkCd.Value );
	ds_TBoxSave.SetColumn(0, "ZIP_NO1"       ,ds_TestBox.GetColumn(ds_TestBox.row, "ZIP_NO1"));
	ds_TBoxSave.SetColumn(0, "ZIP_NO2"       ,ds_TestBox.GetColumn(ds_TestBox.row, "ZIP_NO2"));
	ds_TBoxSave.SetColumn(0, "BLOCK_CD"      ,ds_TestBox.GetColumn(ds_TestBox.row, "BLOCK_CD"));
	ds_TBoxSave.SetColumn(0, "PRESS_FLAG"    ,ds_TestBox.GetColumn(ds_TestBox.row, "PRESS_FLAG"));
	ds_TBoxSave.SetColumn(0, "PLAN_FLAG"     ,ds_TestBox.GetColumn(ds_TestBox.row, "PLAN_FLAG"));
	ds_TBoxSave.SetColumn(0, "TAKER_EMPID1_NM", edt_empnm1.text);	
	ds_TBoxSave.SetColumn(0, "TAKER_EMPID2_NM", edt_empnm2.text);
	ds_TBoxSave.SetColumn(0, "TAKER_EMPID3_NM", edt_empnm3.text);
	ds_TBoxSave.SetColumn(0, "TAKER_EMPID1",edt_empid1.text );	
	ds_TBoxSave.SetColumn(0, "TAKER_EMPID2",edt_empid2.text );
	ds_TBoxSave.SetColumn(0, "TAKER_EMPID3",edt_empid3.text );

	//비계획인 경우는 계획일자가 없다.--권기철 2010/01/06
    if(cob_Gubun.value == '30')
    {
		ds_TBoxSave.SetColumn(0,"PLAN_YMD",'');
    }
    else{
		ds_TBoxSave.SetColumn(0,"PLAN_YMD",ds_TestBox.GetColumn(ds_TestBox.row,"PLAN_YMD"));
    }				
}


