function fn_D1_Delete()
{
	if (ds_TBoxSave.GetRowCount() == 0)
	{
		return;
	}
		
	if(ds_TBoxSave.GetRowType(ds_TBoxSave.row) == "insert")
	{
		ds_TBoxSave.DeleteRow(ds_TBoxSave.row);
		return;
	}

	if (ds_TBoxSave.GetColumn(ds_TBoxSave.row, "DECIDE_PROC_STS") == '0' || ds_TBoxSave.GetColumn(ds_TBoxSave.row, "DECIDE_PROC_STS") == '1' || ds_TBoxSave.GetColumn(ds_TBoxSave.row, "DECIDE_PROC_STS") == '2') 
	{	
		alert("결재(진행/완료)중으로 삭제가 불가합니다...");
		return;
    }else if(confirm("삭제하시겠습니까?"))
    {
		ds_SaveBad.ClearData();
		ds_SaveBad.UpdateControl = false ;

		ds_SaveBad.AddRow();
		ds_SaveBad.SetRowType(0, "delete");
		
		ds_SaveBad.SetColumn(0, "FACI_NUM", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "FACI_NUM")); //시설물번호
		ds_SaveBad.SetColumn(0, "DETAIL_NUM", "0001");		
		ds_SaveBad.SetColumn(0, "PATROL_YMD", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "PATROL_YMD")); //순찰일자

		ds_Plan.ClearData();
		ds_Plan.UpdateControl = false ;

		ds_Plan.AddRow();
		ds_Plan.SetRowType(0, "update");		
		ds_Plan.SetColumn(0, "CHK_YEAR", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "CHK_YEAR"));		//순찰일자
		ds_Plan.SetColumn(0, "CHK_QUARTER", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "CHK_QUARTER"));		//순찰일자
		ds_Plan.SetColumn(0, "BLOCK_CD", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "BLOCK_CD"));		//순찰일자
		ds_Plan.SetColumn(0, "FACI_NUM", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "FACI_NUM"));		//순찰일자
		ds_Plan.SetColumn(0, "DETAIL_NUM", '0001');		//상세번호
		ds_Plan.SetColumn(0, "PLAN_YMD", ds_TBoxSave.GetColumn(ds_TBoxSave.row, "PLAN_YMD"));		//순찰일자
		ds_Plan.SetColumn(0, "PATROL_YMD", '');		//순찰일자초기화
		ds_Plan.SetColumn(0, "CHK_FLAG", "N");
		ds_Plan.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_Plan.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP						

		ds_TBoxSave.DeleteRow(ds_TBoxSave.row);

		var strSvcID         = "testbox_chk_del";
		var strPart          = "D10";	
		var strURL           = "handle.do?ServiceName=D1202010010F-service";					
		var strInDatasets    = "ds_TBoxSave=ds_TBoxSave:u ds_SaveBad=ds_SaveBad:u ds_Plan=ds_Plan:u";
		var strOutDatasets   = "";		
		var strArgument      = "testbox_chk_del=1";
	
		gfn_SyncCall(
						 strSvcID
					   , strPart  	 
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );
	
		if(ErrorCode < 0)
		{
			return;
		}
		else
		{
			alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"));
		}		
			
	}
}

function ds_TestBox_OnRowPosChanged(obj,nOldRow,nRow)
{
	ds_TBoxSave.ClearData();
    var strSvcID        = "rst_testbox";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_TBoxSave=ds_TBoxSave";
	var strArgument     = "rst_testbox=1";
		strArgument		+= " FACI_NUM="    + ds_TestBox.GetColumn(ds_TestBox.row,"FACI_NUM");
								 	 
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	
}


function ds_TestBox_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(nReason == 5)
	{
		ds_TestBox_OnRowPosChanged();
	}
}
