function fn_D1_PortletFind(str_FaciNum, str_PlanYmd){

	var strSvcID = "fn_D1_Find";
	var strPart  = "D10";				
	var strURL   = "handle.do?ServiceName=D1_PatrolCheckResultMgr-service"
	             + "&BRANCH_CD="	 + "" 
	             + "&BLOCK_CD="  	 + ""
	             + "&TEAM_CD=" 		 + ""
	             + "&SHIFT_WORK_CD=" + ""
	             + "&PATROL_YMD=" 	 + str_PlanYmd
	             + "&FACI_NUM="	     + str_FaciNum
	             ;
	
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Multi_T05=ds_Multi";
	var strArgument     = "find5";
	
	gfn_SyncCall(strSvcID,
	              strPart,
	              strURL,
	              strInDatasets,
	              strOutDatasets,
	              strArgument);
	              
	if(ErrorCode < 0)
	{
		return;
	}			              
	
	if(ds_Multi_T05.GetRowCount()==0)
	{
        grd_Multi.NoDataText=gfn_GetMessage("M000009","%AAA%","조회를");
    }
    
    grd_Multi_OnCellClick ("",0,"","","","");
}

//-------------------------------------------------------------------------------------
// 함수명 :  fn_D1_Delete()
// 내용   :  순찰 실적을 삭제한다.
// args   : 
// return : 없음
//-------------------------------------------------------------------------------------
function fn_D1_Delete()
{
	if (ds_RectiSave.GetRowCount() == 0)
	{
		return;
	}
		
	if(ds_RectiSave.GetRowType(ds_RectiSave.row) == "insert")
	{
		ds_RectiSave.DeleteRow(ds_RectiSave.row);
		return;
	}

	if (ds_RectiSave.GetColumn(ds_RectiSave.row, "DECIDE_PROC_STS") == '0' || ds_RectiSave.GetColumn(ds_RectiSave.row, "DECIDE_PROC_STS") == '1' || ds_RectiSave.GetColumn(ds_RectiSave.row, "DECIDE_PROC_STS") == '2') 
	{	
		alert("결재(진행/완료)중으로 삭제가 불가합니다...");
		return;
    }else if(confirm("삭제하시겠습니까?"))
    {
		ds_List.ClearData();
		ds_List.UpdateControl = false ;

		ds_List.AddRow();
		ds_List.SetRowType(0, "delete");
		
		ds_List.SetColumn(0, "FACI_NUM", ds_RectiSave.GetColumn(ds_RectiSave.row, "FACI_NUM")); //시설물번호
		ds_List.SetColumn(0, "PATROL_YMD", ds_RectiSave.GetColumn(ds_RectiSave.row, "PATROL_YMD")); //순찰일자

		ds_Plan.ClearData();
		ds_Plan.UpdateControl = false ;

		ds_Plan.AddRow();
		ds_Plan.SetRowType(0, "update");		
		ds_Plan.SetColumn(0, "CHK_YEAR", ds_RectiSave.GetColumn(ds_RectiSave.row, "CHK_YEAR"));		//순찰일자
		ds_Plan.SetColumn(0, "CHK_QUARTER", ds_RectiSave.GetColumn(ds_RectiSave.row, "CHK_QUARTER"));		//순찰일자
		ds_Plan.SetColumn(0, "BLOCK_CD", ds_RectiSave.GetColumn(ds_RectiSave.row, "BLOCK_CD"));		//순찰일자
		ds_Plan.SetColumn(0, "FACI_NUM", ds_RectiSave.GetColumn(ds_RectiSave.row, "FACI_NUM"));		//순찰일자
		ds_Plan.SetColumn(0, "DETAIL_NUM", '0001');		//상세번호
		ds_Plan.SetColumn(0, "PLAN_YMD", ds_RectiSave.GetColumn(ds_RectiSave.row, "PLAN_YMD"));		//순찰일자
		ds_Plan.SetColumn(0, "PATROL_YMD", '');		//순찰일자초기화
		ds_Plan.SetColumn(0, "CHK_FLAG", "N");
		ds_Plan.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_Plan.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP						

		ds_RectiSave.DeleteRow(ds_RectiSave.row);

		var strSvcID         = "re_dr_check";
		var strPart          = "D10";	
		var strURL           = "handle.do?ServiceName=D1202010010F-service";					
		var strInDatasets    = "ds_ReDrSave=ds_RectiSave:u ds_List=ds_List:u ds_Plan=ds_Plan:u";
		var strOutDatasets   = "";		
		var strArgument      = "re_dr_check=1";
	
		gfn_SyncCall(
						 strSvcID
					   , strPart  	 
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );
	
		if(ErrorCode < 0)
		{
			return;
		}
		else
		{
			alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"));
		}		
			
	}
}

function grd_MultiSave_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if("CHK_YN" == grd_MultiSave.GetCellProp("body",nCell,"ColId"))	//점검대장 등록여부
	{	
		if(ds_RectiSave.GetRowType(ds_RectiSave.Row)=="insert")
		{
			alert("순찰점검등록을 먼저 하십시오.");
			return;
		}
			
		var args = "";

		args  = "FACI_NUM="    + quote(ds_RectiSave.GetColumn(nRow,"FACI_NUM"));										//시설물번호
		args += " PATROL_YMD=" + quote(ds_RectiSave.GetColumn(nRow,"PATROL_YMD"));	//순찰일자
		args += " VOLTAGE="    + quote(ds_RectiSave.GetColumn(nRow,"VOLTAGE"));
		args += " VOLTAIC="    + quote(ds_RectiSave.GetColumn(nRow,"VOLTAIC"));
		args += " RECTIFER_ELECT_POT="    + quote(ds_RectiSave.GetColumn(nRow,"RECTIFER_ELECT_POT"));
		var retValue = gfn_Dialog("D10","D1202010021U",args,660,620,true,-1,-1);
		
		ds_Recti_OnRowPosChanged();		
	}	
}

function ds_Recti_OnRowPosChanged(obj,nOldRow,nRow)
{
	ds_RectiSave.ClearData();
    var strSvcID        = "rst_re_dr";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_RectiSave=ds_ReDrSave";
	var strArgument     = "rst_re_dr=1";
		strArgument		+= " FACI_NUM="    + ds_Recti.GetColumn(ds_Recti.row,"FACI_NUM");
								 	 
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	
}

function ds_Recti_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(nReason == 5)
	{
		ds_Recti_OnRowPosChanged();
	}
}
