//------------------------------------------------------------------------------- 
//   함수명: fn_D1_SAVE()
//   설명  : 수취기기 실적을 등록한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Save()
{	
	var row1 = 0;
	ds_Plan.ClearData();
	ds_Plan.UpdateControl = false ;
		
	for(var i =0; i<ds_WDrainSave.GetRowCount();i++)
	{ 
		if(ds_WDrainSave.GetColumn(i,"PATROL_YMD") > today())
		{
			alert("점검일자가 현재일보다 큽니다.");
			return;
		}
		
		if(ds_WDrainSave.GetColumn(0,"LEAK_YN") == ""||ds_WDrainSave.GetColumn(0,"LEAK_YN") == null)
		{
			alert(gfn_GetMessage("M000011","%AAA%","누출여부를"));	
			return;
		}
	
		if(ds_WDrainSave.GetColumn(0,"OUTWARD_STS_CD") == ""||ds_WDrainSave.GetColumn(0,"OUTWARD_STS_CD") == null)
		{
			alert(gfn_GetMessage("M000011","%AAA%","외관상태를"));	
			return;
		}
		
		if (ds_WDrainSave.GetRowType(i) == 'insert')
		{
			ds_WDrainSave.SetColumn(i, "CHK_YEAR", substr(ds_WDrainSave.GetColumn(i, "PATROL_YMD"),0,4));
			var chk_month = substr(ds_WDrainSave.GetColumn(i, "PATROL_YMD"),4,2);
			
			if(chk_month == "01"|| chk_month =="02"|| chk_month =="03")
			{
				ds_WDrainSave.SetColumn(i, "CHK_QUARTER", '1');
			}
			else if(chk_month == "04"|| chk_month =="05"|| chk_month =="06")
			{
				ds_WDrainSave.SetColumn(i, "CHK_QUARTER", '2');
			}
			else if(chk_month == "07"|| chk_month =="08"|| chk_month =="09")
			{
				ds_WDrainSave.SetColumn(i, "CHK_QUARTER", '3');
			}
			else if(chk_month == "10"|| chk_month =="11"|| chk_month =="12")
			{
				ds_WDrainSave.SetColumn(i, "CHK_QUARTER", '4');
			}
			else
			{
				alert("순찰일자 오류입니다..");
				return;
			}			
			
			//결재여부 확인 루틴//점검일에 결재 유무 확인
			if (fn_decide(ds_WDrainSave.GetColumn(i, "PATROL_YMD"), ds_WDrainSave.GetColumn(i, "BRANCH_CD"), ds_WDrainSave.GetColumn(i, "TEAM_CD")) == false)
			{
				alert("점검일자(" + ds_WDrainSave.GetColumn(i, "PATROL_YMD") +  ")는 결재 진행중입니다...등록 불가 합니다..");
				return;
			}
			
			if (ds_WDrainSave.GetColumn(i,"PLAN_FLAG") == "11")
			{
				if (substr(ds_WDrainSave.GetColumn(i, "PATROL_YMD"),0,4) != substr(ds_WDrainSave.GetColumn(i, "PLAN_YMD"),0,4))
				{
					alert("점검년도가 계획년도와 다릅니다. 등록 불가합니다..");
					return;
				}	
	
				if (fn_Quarter(ds_WDrainSave.GetColumn(i, "PATROL_YMD")) != fn_Quarter(ds_WDrainSave.GetColumn(i, "PLAN_YMD")))
				{
					alert("점검일자와 계획일자가 동일 분기가 아닙니다.. 등록 불가합니다..");
					return;
				}				
				
				row1 = ds_Plan.AddRow();
				ds_Plan.SetRowType(row1, "update");
				ds_Plan.SetColumn(row1, "CHK_YEAR", ds_WDrainSave.GetColumn(i, "CHK_YEAR"));		//순찰일자
				ds_Plan.SetColumn(row1, "CHK_QUARTER", ds_WDrainSave.GetColumn(i, "CHK_QUARTER"));		//순찰일자
				ds_Plan.SetColumn(row1, "BLOCK_CD", ds_WDrainSave.GetColumn(i, "BLOCK_CD"));		//순찰일자
				ds_Plan.SetColumn(row1, "FACI_NUM", ds_WDrainSave.GetColumn(i, "FACI_NUM"));		//순찰일자
				ds_Plan.SetColumn(row1, "DETAIL_NUM", '0001');		//상세번호
				ds_Plan.SetColumn(row1, "PLAN_YMD", ds_WDrainSave.GetColumn(i, "PLAN_YMD"));		//순찰일자
				ds_Plan.SetColumn(row1, "PATROL_YMD", ds_WDrainSave.GetColumn(i, "PATROL_YMD"));		//순찰일자
				ds_Plan.SetColumn(row1, "CHK_FLAG", "Y");
				ds_Plan.SetColumn(row1, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
				ds_Plan.SetColumn(row1, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP				
			}

			ds_WDrainSave.SetColumn(i, "CRT_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
			ds_WDrainSave.SetColumn(i, "CRT_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP
			ds_WDrainSave.SetColumn(i, "UPD_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
			ds_WDrainSave.SetColumn(i, "UPD_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP			
		}
		else if(ds_WDrainSave.GetRowType(i) == 'update')
		{
			if(ds_WDrainSave.GetColumn(i,"DECIDE_PROC_STS") == '0' || ds_WDrainSave.GetColumn(i, "DECIDE_PROC_STS") == '1' || ds_WDrainSave.GetColumn(i, "DECIDE_PROC_STS") == '2')
			{
				alert("점검일자(" + ds_WDrainSave.GetColumn(i, "PATROL_YMD") +  ")는 결재(완료/진행중)으로 내역을 수정할 수 없습니다...");
				return;
			}
			else
			{
				ds_WDrainSave.SetColumn(i, "UPD_EMPID"     ,gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
				ds_WDrainSave.SetColumn(i, "UPD_IP"        ,gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP			
			}
		}
	}
	
	var strSvcID         = "wdrain_check";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1202010010F-service";					
	var strInDatasets    = "ds_WDrainSave=ds_WDrainSave:u ds_Plan=ds_Plan:u";
	var strOutDatasets   = "";		
	var strArgument      = "wdrain_check=1";

	gfn_syncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				);
			   
	if(ErrorCode<0)
	{
		return;
	}

	alert("저장/수정 되었습니다.");
	
	ds_WDrain_OnRowPosChanged();		
}

//결재유무 check
function fn_decide(PATROL_YMD, BRANCH_CD, TEAM_CD)
{
	ds_Decide.ClearData();
	
	var strSvcID        = "decide";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Decide=ds_Decide";
	var strArgument     = "decide=1";
		strArgument		+= " PATROL_YMD="  + PATROL_YMD;
		strArgument		+= " BRANCH_CD=" + BRANCH_CD;
		strArgument		+= " TEAM_CD="  + TEAM_CD;

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );

	if(ds_Decide.RowCount() == 0) return true;
	
	if(ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '0' || ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '1' || ds_Decide.GetColumn(0,"DECIDE_PROC_STS") == '2')
	{
		return false;
	}	
	else
	{
		return true;
	}	
}

//------------------------------------------------------------------------------- 
//  함수명 : grd_MultiSave_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
//  설 명  : 그리드의 타입에 따라 순찰일자의 활성화 여부
//  args   :  
//  return : 없음
//-------------------------------------------------------------------------------
function grd_MultiSave_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	
	if(ds_WDrainSave.GetRowType(nRow) == "insert")
	{
		grd_MultiSave.SetCellProp("body",3,"Edit","date");		//점검일자
	}
	else
	{
		grd_MultiSave.SetCellProp("body",3,"Edit","none");		//점검일자		
	}	
	
	if(ds_WDrainSave.GetColumn(nRow,"DECIDE_PROC_STS") == '0' || ds_WDrainSave.GetColumn(nRow,"DECIDE_PROC_STS") == '1' || ds_WDrainSave.GetColumn(nRow,"DECIDE_PROC_STS") == '2')
	{
		grd_MultiSave.SetCellProp("body",6,"Edit","none");		//누출여부
		grd_MultiSave.SetCellProp("body",7,"Edit","none");		//외관상태
		grd_MultiSave.SetCellProp("body",8,"Edit","none");		//점검사항
		grd_MultiSave.SetCellProp("body",9,"Edit","none");	    //특이사항
	}
	else
	{
		grd_MultiSave.SetCellProp("body",6,"Edit","combo");		//누출여부
		grd_MultiSave.SetCellProp("body",7,"Edit","combo");		//외관상태
		grd_MultiSave.SetCellProp("body",8,"Edit","combo");	    //점검사항
		grd_MultiSave.SetCellProp("body",9,"Edit","normal");	//특이사항
	}
}


//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Addrowm()
//   설명  :  구간 행추가 
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Addrowm()
{
	if(ds_WDrain.GetRowCount()==0)
	{
		return;
	}
	
    if((gfn_GetGlobalValue("G_BRANCH_CD") == "999999") AND (gfn_GetGlobalValue("G_USER_NO") != '199443297'))
    {
		return;
	}
	else if(ds_WDrain.GetColumn(ds_WDrain.row, "FACI_NUM") == '' || ds_WDrain.GetColumn(ds_WDrain.row, "FACI_NUM") == null)	
	{
        alert("실적 등록할 시설물을 선택하세요");
        return;
    }else if(ds_WDrainSave.GetRowType(0)=="insert") {
		return;
    }
    
    //계획 및 분기계획건의 경우 점검일자가 등록되어 있는 경우는 실적 추가등록 불가
    if(cob_Gubun.value=="10" || cob_Gubun.value=="40") // 계획,분기계획
    {
		if(length(trim(ds_WDrain.GetColumn(ds_WDrain.row, "PATROL_YMD"))) > 0)
		{
			alert("해당 계획일자의 점검 실적이 등록되어 있습니다...");
			return;
		}
	}	
	
	ds_WDrainSave.InsertRow(0);		//insert시 맨위에 행추가
	ds_WDrainSave.SetColumn(0,"FACI_NUM", ds_WDrain.GetColumn(ds_WDrain.row, "FACI_NUM"));
	ds_WDrainSave.SetColumn(0,"PATROL_YMD",Today());
	grd_MultiSave.SetCellProp("body",3,"Edit"   ,"expr:decode(rowtype,'insert','date','none')"); 
	
	ds_WDrainSave.SetColumn(0, "BRANCH_CD"     ,ds_WDrain.GetColumn(ds_WDrain.row, "BRANCH_CD"));
	ds_WDrainSave.SetColumn(0, "TEAM_CD"       ,ds_WDrain.GetColumn(ds_WDrain.row, "TEAM_CD"));
	ds_WDrainSave.SetColumn(0, "ZIP_NO1"       ,ds_WDrain.GetColumn(ds_WDrain.row, "ZIP_NO1"));
	ds_WDrainSave.SetColumn(0, "ZIP_NO2"       ,ds_WDrain.GetColumn(ds_WDrain.row, "ZIP_NO2"));
	ds_WDrainSave.SetColumn(0, "BLOCK_CD"      ,ds_WDrain.GetColumn(ds_WDrain.row, "BLOCK_CD"));
	ds_WDrainSave.SetColumn(0, "SHIFT_WORK_CD" ,cob_PShiftWorkCd.Value);
	ds_WDrainSave.SetColumn(0, "PLAN_FLAG"     ,ds_WDrain.GetColumn(ds_WDrain.row, "PLAN_FLAG"));
	ds_WDrainSave.SetColumn(0, "TAKER_EMPID1_NM", edt_empnm1.text);	
	ds_WDrainSave.SetColumn(0, "TAKER_EMPID2_NM", edt_empnm2.text);
	ds_WDrainSave.SetColumn(0, "TAKER_EMPID3_NM", edt_empnm3.text);
	ds_WDrainSave.SetColumn(0, "TAKER_EMPID1",edt_empid1.text );	
	ds_WDrainSave.SetColumn(0, "TAKER_EMPID2",edt_empid2.text );
	ds_WDrainSave.SetColumn(0, "TAKER_EMPID3",edt_empid3.text );

	//비계획인 경우는 계획일자가 없다.--권기철 2010/01/06
    if(cob_Gubun.value == '30')
    {
		ds_WDrainSave.SetColumn(0,"PLAN_YMD",'');
    }
    else{
		ds_WDrainSave.SetColumn(0,"PLAN_YMD",ds_WDrain.GetColumn(ds_WDrain.row,"PLAN_YMD"));
    }				    
}




