function fn_D1_PortletFind(str_FaciNum, str_PlanYmd){

		var strSvcID = "fn_D1_Find";
	var strPart = "D10";				
	var strURL  = "handle.do?ServiceName=D1_PatrolCheckResultMgr-service"
	            + "&BRANCH_CD="		 + ""
	            + "&BLOCK_CD=" 		 + ""
	            + "&TEAM_CD=" 		 + ""
	            + "&SHIFT_WORK_CD="  + ""
	            + "&PATROL_YMD=" 	 + str_PlanYmd
	            + "&FACI_NUM="	     + str_FaciNum
	            ;
	var strInDatasets  = "";
	var strOutDatasets  = "ds_Multi_T07=ds_Multi";
	var strArgument  = "find7";
	
	gfn_SyncCall(strSvcID,
	              strPart,
	              strURL,
	              strInDatasets,
	              strOutDatasets,
	              strArgument);
	              
	if(ErrorCode < 0)
	{
		return;
	}			              
	              	
	if(ds_Multi_T07.GetRowCount()==0)
	{
        grd_Multi.NoDataText=gfn_GetMessage("M000009","%AAA%","조회를");
    }
    
    grd_Multi_OnCellClick ("",0,"","","","");
}

//-------------------------------------------------------------------------------------
// 함수명 :  fn_D1_Delete()
// 내용   :  순찰 실적을 삭제한다.
// args   : 
// return : 없음
//-------------------------------------------------------------------------------------
function fn_D1_Delete()
{
	if (ds_WDrainSave.GetRowCount() == 0)
	{
		return;
	}
		
	if(ds_WDrainSave.GetRowType(ds_WDrainSave.row) == "insert")
	{
		ds_WDrainSave.DeleteRow(ds_WDrainSave.row);
		return;
	}

	if (ds_WDrainSave.GetColumn(ds_WDrainSave.row, "DECIDE_PROC_STS") == '0' || ds_WDrainSave.GetColumn(ds_WDrainSave.row, "DECIDE_PROC_STS") == '1' || ds_WDrainSave.GetColumn(ds_WDrainSave.row, "DECIDE_PROC_STS") == '2') 
	{	
		alert("결재(진행/완료)중으로 삭제가 불가합니다...");
		return;
    }else if(confirm("삭제하시겠습니까?"))
    {
		ds_Plan.ClearData();
		ds_Plan.UpdateControl = false ;

		ds_Plan.AddRow();
		ds_Plan.SetRowType(0, "update");		
		ds_Plan.SetColumn(0, "CHK_YEAR", ds_WDrainSave.GetColumn(ds_WDrainSave.row, "CHK_YEAR"));		//순찰일자
		ds_Plan.SetColumn(0, "CHK_QUARTER", ds_WDrainSave.GetColumn(ds_WDrainSave.row, "CHK_QUARTER"));		//순찰일자
		ds_Plan.SetColumn(0, "BLOCK_CD", ds_WDrainSave.GetColumn(ds_WDrainSave.row, "BLOCK_CD"));		//순찰일자
		ds_Plan.SetColumn(0, "FACI_NUM", ds_WDrainSave.GetColumn(ds_WDrainSave.row, "FACI_NUM"));		//순찰일자
		ds_Plan.SetColumn(0, "DETAIL_NUM", '0001');		//상세번호
		ds_Plan.SetColumn(0, "PLAN_YMD", ds_WDrainSave.GetColumn(ds_WDrainSave.row, "PLAN_YMD"));		//순찰일자
		ds_Plan.SetColumn(0, "PATROL_YMD", '');		//순찰일자초기화
		ds_Plan.SetColumn(0, "CHK_FLAG", "N");
		ds_Plan.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_Plan.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP						

		ds_WDrainSave.DeleteRow(ds_WDrainSave.row);

		var strSvcID         = "wdrain_check";
		var strPart          = "D10";	
		var strURL           = "handle.do?ServiceName=D1202010010F-service";					
		var strInDatasets    = "ds_WDrainSave=ds_WDrainSave:u ds_Plan=ds_Plan:u";
		var strOutDatasets   = "";		
		var strArgument      = "wdrain_check=1";
	
		gfn_SyncCall(
						 strSvcID
					   , strPart  	 
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );
	
		if(ErrorCode < 0)
		{
			return;
		}
		else
		{
			alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"));
		}		
			
	}
}
function ds_WDrain_OnRowPosChanged(obj,nOldRow,nRow)
{
	ds_WDrainSave.ClearData();
    var strSvcID        = "rst_wdrain";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_WDrainSave=ds_WDrainSave";
	var strArgument     = "rst_wdrain=1";
		strArgument		+= " FACI_NUM="    + ds_WDrain.GetColumn(nRow,"FACI_NUM");
								 	 
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );			
}
function ds_WDrain_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(nReason == 5)
	{
		ds_WDrain_OnRowPosChanged();
	}
}
