	ds_Search_cnt.SetColumn(0,"BLOCK_CD",cob_block.Value);
	ds_Search_cnt.SetColumn(0,"PATROL_YMD",cal_PPatrolYmd.Value);
	
	ds_Count.ClearData();
	ds_Imporcnt.ClearData();

	var strSvcID = "imporplan_cnt";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets  = "ds_Search_cnt=ds_Search_cnt";
	var strOutDatasets  = "ds_Imporcnt=ds_Count";
	var strArgument  = "imporplan_cnt=1";
				
	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );						   			      
}


function grd_Multi1_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(ds_Impor.rowcount() == 0)
	{
		return;
	}	
	
	if("FACI_CARD" == grd_Multi1.GetCellProp("body",nCell,"ColId"))
	{
		fn_Card(nRow);											//시설물카드
		return;
	}

	if("GIS_POSITION" == grd_Multi1.GetCellProp("body",nCell,"ColId"))
	{
		var args = "BUNGI='10' FACI_NUM="+ds_Impor.GetColumn(nRow,"FACI_NUM");
		gfn_Dialog("D10","D1402060011U",args,1208,840,true,-1,-1);	//GIS 불러오기
		return;
	}
	
	if("WORK_INFO" == grd_Multi1.GetCellProp("body",nCell,"ColId"))		//공사정보조회
	{
		if(trim(ds_Impor.GetColumn(ds_Impor.row,"WORK_NUM"))==""||ds_Impor.GetColumn(ds_Impor.row,"WORK_NUM")==null)
		{
			alert("공사번호가 없습니다..");
			return;
		}
		
		var strPrefix = "B20";
		var strFormid = "B2204010010F";
		var strArg    = "fa_BranchCd=" + ds_Impor.GetColumn(ds_Impor.row,"BRANCH_CD"); 
		    strArg   += "  fa_WorkNum="+ ds_Impor.GetColumn(ds_Impor.row,"WORK_NUM"); 
		    
		gfn_NewWindow(strPrefix,strFormid,strArg);
		return;
	}		
	
}

