//-------------------------------------------------------------------------------------
// 함수명 :  fn_D1_Delete()
// 내용   :  순찰 실적을 삭제한다.
// args   : 
// return : 없음
//-------------------------------------------------------------------------------------
function fn_D1_Delete()
{
	if (ds_ImporSave.GetRowCount() == 0)
	{
		return;
	}
		
	if(ds_ImporSave.GetRowType(ds_ImporSave.row) == "insert")
	{
		ds_ImporSave.DeleteRow(ds_ImporSave.row);
		return;
	}

	if (ds_ImporSave.GetColumn(ds_ImporSave.row, "DECIDE_PROC_STS") == '0' || ds_ImporSave.GetColumn(ds_ImporSave.row, "DECIDE_PROC_STS") == '1' || ds_ImporSave.GetColumn(ds_ImporSave.row, "DECIDE_PROC_STS") == '2') 
	{	
		alert("결재(진행/완료)중으로 삭제가 불가합니다...");
		return;
    }else if(confirm("삭제시 점검표 등록내역도 삭제가 됩니다...삭제하시겠습니까?"))
    {
		ds_List.ClearData();
		ds_List.UpdateControl = false ;

		ds_List.AddRow();
		ds_List.SetRowType(0, "delete");
		
		ds_List.SetColumn(0, "FACI_NUM", ds_ImporSave.GetColumn(ds_ImporSave.row, "FACI_NUM")); //시설물번호
		ds_List.SetColumn(0, "PATROL_YMD", ds_ImporSave.GetColumn(ds_ImporSave.row, "PATROL_YMD")); //순찰일자

		ds_Plan.ClearData();
		ds_Plan.UpdateControl = false ;

		ds_Plan.AddRow();
		ds_Plan.SetRowType(0, "update");		
		ds_Plan.SetColumn(0, "CHK_YEAR", ds_ImporSave.GetColumn(ds_ImporSave.row, "CHK_YEAR"));		//순찰일자
		ds_Plan.SetColumn(0, "CHK_QUARTER", ds_ImporSave.GetColumn(ds_ImporSave.row, "CHK_QUARTER"));		//순찰일자
		ds_Plan.SetColumn(0, "BLOCK_CD", ds_ImporSave.GetColumn(ds_ImporSave.row, "BLOCK_CD"));		//순찰일자
		ds_Plan.SetColumn(0, "FACI_NUM", ds_ImporSave.GetColumn(ds_ImporSave.row, "FACI_NUM"));		//순찰일자
		ds_Plan.SetColumn(0, "DETAIL_NUM", '0001');		//상세번호
		ds_Plan.SetColumn(0, "PLAN_YMD", ds_ImporSave.GetColumn(ds_ImporSave.row, "PLAN_YMD"));		//순찰일자
		ds_Plan.SetColumn(0, "PATROL_YMD", '');		//순찰일자초기화
		ds_Plan.SetColumn(0, "CHK_FLAG", "N");
		ds_Plan.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_Plan.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP						

		ds_ImporSave.DeleteRow(ds_ImporSave.row);

		var strSvcID         = "impor_check";
		var strPart          = "D10";	
		var strURL           = "handle.do?ServiceName=D1202010010F-service";					
		var strInDatasets    = "ds_ImporSave=ds_ImporSave:u ds_List=ds_List:u ds_Plan=ds_Plan:u";
		var strOutDatasets   = "";		
		var strArgument      = "impor_check=1";
	
		gfn_SyncCall(
						 strSvcID
					   , strPart  	 
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
				   );
	
		if(ErrorCode < 0)
		{
			return;
		}
		else
		{
			alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"));
		}		
			
	}
}

function grd_MultiSave_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var retValue = '';

	if("CHK_YN" == grd_MultiSave.GetCellProp("body",nCell,"ColId"))	//점검대장 등록여부
	{	
		if(ds_ImporSave.GetRowType(nRow)=="insert")
		{
			alert("순찰점검등록을 먼저 하십시오.");
			return;
		}
			
		var args = "";

		args  = "FACI_NUM="    + quote(ds_ImporSave.GetColumn(nRow,"FACI_NUM"));										//시설물번호
		args += " PATROL_YMD=" + quote(ds_ImporSave.GetColumn(nRow,"PATROL_YMD"));	//순찰일자
		if(substr(ds_ImporSave.GetColumn(nRow,"FACI_NUM"),0,2) == 'BH')
		{
			retValue = gfn_Dialog("D10","D1202010028U",args,660,400,true,-1,-1);
		}	
		else if(substr(ds_ImporSave.GetColumn(nRow,"FACI_NUM"),0,2) == 'UC') 
		{
			retValue = gfn_Dialog("D10","D1202010029U",args,660,365,true,-1,-1);
			
		}
		else if(substr(ds_ImporSave.GetColumn(nRow,"FACI_NUM"),0,2) == 'RO') 
		{
			retValue = gfn_Dialog("D10","D1202010030U",args,660,329,true,-1,-1);
		}
		else
		{
			alert("시설물번호를 확인 하세요...");
			return;
		}	

		if (retValue == null)
		{
			return;
		}	
		else if(retValue == "saveOk")
		{
			ds_ImporSave.SetColumn(nRow, "CHK_YN" ,'10'); //점검표등록 여부 10 등록, 20 미등록
		}
		else
		{
			return;
		}
	}		
}

function ds_Impor_OnRowPosChanged(obj,nOldRow,nRow)
{
	ds_ImporSave.ClearData();
    var strSvcID        = "rst_impor";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1202010010F-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_ImporSave=ds_ImporSave";
	var strArgument     = "rst_impor=1";
		strArgument		+= " FACI_NUM="    + ds_Impor.GetColumn(nRow,"FACI_NUM");
								 	 
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );		
}
function ds_Impor_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(nReason == 5)
	{
		ds_Impor_OnRowPosChanged();
	}
}
