var strRowType = "";
var strFaciNum = "";		//특별관리시설번호
var strDetailNum = "MD00";		//상세번호
var strPatrolYmd = "";		//순찰일자
var fv_FaciStsCd = "MD";	//임시

var fv_BranchCd    = "";
var fv_TeamCd      = "";
var fv_ShiftWorkCd = "";
