//---------------------------------------------------------
//	화면 OnLoadCompleted 이벤트처리 발생하는 함수( 화면ID_OnLoadCompleted )
//---------------------------------------------------------
function D1202010011F_T09_D01_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	gfn_D1_SetGrid_Sort(grd_Multi);
	gfn_D1_SetGrid_Sort(grd_MultiSave);
	fn_D1_Combo_Set();
	
	if (IsExistVar("PORTLET_FLAG", true)){
		if (PORTLET_FLAG == "p"){
			fn_D1_PortletFind(FACI_NUM, PLAN_YMD);
		}
	}
}


function fn_D1_Combo_Set()
{
	gfn_SetGrdComboBox_Comcode("grd_MultiSave",3,"D10189");		//점검대장등록여부
	grd_MultiSave.SetCellProp("body",3,"Edit","none");
}


//------------------------------------------------------------------------------- 
// 함수명: fn_D1_ExcelExport()
//  설명: 특별관리시설 엑셀로 저장
// arguments   
//  return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_ExcelExport()
{ 
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 :"     		+ RPad(cob_PBranchCd.Text," ",20);
    SearchCondition = SearchCondition + "팀 :"       		+ RPad(cob_PTeamCd.Text," ",20);
    SearchCondition = SearchCondition + "조 :"       		+ RPad(cob_PShiftWorkCd.Text," ",20);
    SearchCondition = SearchCondition + "구간코드 :" + RPad(cob_block.Text," ",20);
    SearchCondition = SearchCondition + "순찰일자 :" 		+ RPad(cal_PPatrolYmd.Text," ",20);
    SearchCondition = SearchCondition + "시설물번호 :" 		+ RPad(edt_PFaciNum.Text," ",20);
	gfn_ExportExcel("특별관리시설 중압직공급 순찰점검등록", 5, SearchCondition, "grd_Excel");
}


//-------------------------------------------------------------------------------
// 함수명   : fn_D1_Find()
// 설명     : 순찰점검 주 계획을 조회한다.
// return 내용 : 없음
//-------------------------------------------------------------------------------

function fn_D1_Find()
{
	//입력한 날짜가 오늘 보다 클 경우
	if(!fn_DateCheckToday(cal_PPatrolYmd))
	{
		return;
	}

	ds_DetailFind.ClearData();
	ds_Save.ClearData();

	var strSvcID = "fn_D1_Find";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_PatrolCheckResultMgr-service"
	           + "&BRANCH_CD="		+cob_PBranchCd.Value		//지사코드
	           + "&BLOCK_CD=" 		+cob_block.Value        	//구코드
	           + "&TEAM_CD=" 		+cob_PTeamCd.Value			//팀코드
	           + "&SHIFT_WORK_CD="  +cob_PShiftWorkCd.Value		//조코드
	           + "&PATROL_YMD=" 	+cal_PPatrolYmd.Value		//순찰일자
	           + "&FACI_NUM="	    +edt_PFaciNum.Text			//시설물번호
	           ;
	var strInDatasets  = "";
	var strOutDatasets  = "ds_Multi=ds_Multi ds_Excel=ds_Excel";
	var strArgument  = "find9_D3";
	
	gfn_SyncCall(strSvcID,
	              strPart,
	              strURL,
	              strInDatasets,
	              strOutDatasets,
	              strArgument);
	
	if(ErrorCode < 0)
	{
		alert(ErrorMsg);
		return;
	}			
	
	if(ds_Multi.GetRowCount()==0)
	{
        grd_Multi.NoDataText="조회결과가 없습니다.";
    }
	
	strFaciNum = "";	//담은 값을 지움 한 번 조회를 탄 후 값을 가지고 있으므로 null처리를 안해주면 
						//한번 조회 후 다시 조회 할때 '순찰계획을 선택해주세요' 라는 alert이 안뜨고 그냥 행추가가 되는것을 막기위한 
}


function grd_Multi_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if("FACI_CARD" == grd_Multi.GetCellProp("body",nCell,"ColId"))
	{
		fn_Card(nRow);
	}
	
	if("GIS_POSITION" == grd_Multi.GetCellProp("body",nCell,"ColId"))
	{
		var args = "BUNGI='17' FACI_NUM="+ds_Multi.GetColumn(nRow,"FACI_NUM");
		gfn_Dialog("D10","D1402060011U",args,1208,840,true,-1,-1);	//GIS 불러오기
	}

	strFaciNum      = ds_Multi.getColumn(nRow,"FACI_NUM");
	fv_BranchCd     = ds_Multi.GetColumn(nRow,"BRANCH_CD" );  
	fv_TeamCd       = ds_Multi.GetColumn(nRow,"TEAM_CD" );
	fv_ShiftWorkCd  = ds_Multi.GetColumn(nRow,"SHIFT_WORK_CD" );
	fv_ZipNo1       = ds_Multi.GetColumn(nRow,"ZIP_NO1" );
	fv_ChkYear      = ds_Multi.GetColumn(nRow,"CHK_YEAR" );
	fv_ChkQuarter   = ds_Multi.GetColumn(nRow,"CHK_QUARTER" );
	fv_BlockCd      = ds_Multi.GetColumn(nRow,"BLOCK_CD" );
	fv_PlanYmd      = ds_Multi.GetColumn(nRow,"PLAN_YMD" );		
	
	fn_D1_DetailFind(strFaciNum, strDetailNum);
	
	strRowType = "search";
}

//------------------------------------------------------------------------------- 
//   함수명: fn_Card(obj)
//   설명  :  밸브 시설물카드조회 팝업
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_Card(nRow)
{

	// 함수 호출용 파라미터를 설정한다
	var strTitle ="특별관리시설 시설물카드조회"; 
	var strPart ="D10";           
	var strReport="D1202010034P"; 
	var strParameter="FACI_NUM=" 	  +ds_Multi.GetColumn(nRow,"FACI_NUM")    		//시설물번호
					+",PATROL_YMD="   +ds_Multi.GetColumn(nRow,"PATROL_YMD")		//순찰일자
					;
	// 공통 레포트 팝업 함수 호출 
	gfn_ReportPopup(strTitle,strPart,strReport,strParameter);
} 

//-------------------------------------------------------------------------------
// 함수명   : fn_D1_Find()
// 설명     : 순찰점검 주 계획을 조회한다.
// return 내용 : 없음
//-------------------------------------------------------------------------------

function fn_D1_DetailFind(strFaciNum, strDetailNum)
{
	var strSvcID = "fn_D1_DetailFind";
	var strPart = "D10";				
	var strURL = "handle.do?ServiceName=D1_PatrolCheckResultMgr-service"
	           + "&FACI_NUM="	    +strFaciNum			//시설물번호
	           + "&DETAIL_NUM="		+strDetailNum		//상세번호
	           ; 
	var strInDatasets  = "";
	var strOutDatasets  = "ds_DetailFind=ds_DetailFind ds_Save=ds_Save";
	var strArgument  = "find9_Detail3";
	
	gfn_SyncCall(strSvcID,
	              strPart,
	              strURL,
	              strInDatasets,
	              strOutDatasets,
	              strArgument);

	if(ErrorCode < 0)
	{
		alert(ErrorMsg);
		return;
	}			              
	
	if(ds_Save.GetRowCount()==0)
	{
        grd_MultiSave.NoDataText="조회결과가 없습니다.";
    }
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_SAVE
//   설명  : 사유지 점용료 협의사항를 등록/업데이트한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Save()
{
	
	if(ds_Save.RowCount() == 0)
	{
		return;
	}
	
	if(strFaciNum != "")
	{
		if(ds_Save.GetRowType(0) == "insert")
		{
			var strChkFlag = fn_D1_CheckReg();
			
			if(!strChkFlag) 
			{
				alert('이미 등록된 순찰일자입니다.');
				return;
			}
		}
	}else{
		alert('상세화면 조회 후 가능합니다.');
		return;
	}
	
	if(ds_Save.GetColumn(0,"PATROL_YMD") == "" || ds_Save.GetColumn(0,"PATROL_YMD") == null)	//순찰일자
	{
		alert(gfn_GetMessage("M000011","%AAA%","순찰일자를"));	//순찰일자를 입력하세요
		return;
	}	
	
	if(ds_Save.GetColumn(0,"CHK_DOCU_REF_YN") == "20")	//점검대장 등록여부
	{
		alert('점검대장 등록여부를 등록해주세요.');
		return;
	}
	
	if(ds_Save.GetColumn(0,"CHK_DESC") == ""||ds_Save.GetColumn(0,"CHK_DESC") == null)
	{
		alert(gfn_GetMessage("M000011","%AAA%","점검사항을"));	//점검사항을 입력하세요
		return;
	}	
	
	//수정 등록시 셋팅할 사용자 정보
	ds_Save.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
	ds_Save.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자 IP
	ds_Save.SetColumn(0, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
	ds_Save.SetColumn(0, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자 IP
	ds_Save.SetColumn(0, "FACI_NUM" ,strFaciNum);						//시설물번호
	ds_Save.SetColumn(0, "DETAIL_NUM" ,strDetailNum);					//상세번호
	ds_Save.SetColumn(0, "BRANCH_CD"     ,fv_BranchCd );
	ds_Save.SetColumn(0, "TEAM_CD"       ,fv_TeamCd );
	ds_Save.SetColumn(0, "SHIFT_WORK_CD" ,fv_ShiftWorkCd );
	ds_Save.SetColumn(0, "ZIP_NO1"       ,fv_ZipNo1 );
	ds_Save.SetColumn(0, "CHK_YEAR"      ,fv_ChkYear );
	ds_Save.SetColumn(0, "CHK_QUARTER"   ,fv_ChkQuarter);
	ds_Save.SetColumn(0, "BLOCK_CD"      ,fv_BlockCd );
	ds_Save.SetColumn(0, "PLAN_YMD"      ,fv_PlanYmd);	
	
	
	var strSvcID         = "fn_D1_Save";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_PatrolCheckResultMgr-service" ;					
	var strInDatasets    = "ds_Save=ds_Save:u";
	var strOutDatasets   = "";		
	var strArgument      = "save_T09_3";
	
	//alert(strURL);
	
	gfn_AsyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );

	if(ErrorCode < 0)
	{
		alert(ErrorMsg);
		return;
	}
	else
	{					   
		alert(gfn_GetMessage("M000009","%AAA%","저장을"));
	}
}


//------------------------------------------------------------------------------- 
// 함수명: fn_D1_CheckReg()
//   설명: 중점관리시설점검 결과등록을 하기 전 기 등록 여부 확인
// arguments   
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_CheckReg()
{
    var strSvcID       = "fn_D1_CheckReg";
    var strPart        = "D10";
    var strURL         = "handle.do?ServiceName=D1_PatrolCheckResultMgr-service"
					   + "&FACI_NUM=" 	+strFaciNum			//시설물번호
					   + "&DETAIL_NUM="	+strDetailNum		//상세번호
					   + "&PATROL_YMD=" +ds_Save.GetColumn(0,"PATROL_YMD")	//순찰일자
					   ;
    var strInDatasets  = "";
    var strOutDatasets = "ds_Dub=ds_Dub";
    var strArgument    = "Dub_T09_3";   
    
    gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{
		alert(ErrorMsg);
		return;
	}					   

	if(ds_Dub.RowCount() != 0) return false;
	return true;
}

// ------------------------------------------------------------------------------- 
// 함수명: fn_D1_Addrowm()
// 설명  :  구간 행추가 
// arguments
// return 내용: 없음
// ------------------------------------------------------------------------------- 
function fn_D1_Addrowm()
{
	if(strFaciNum != "")
	{
		//행추가를 했을때 점검일자는 금일날짜를 뿌려줌, 점검일자가 키이기 때문에 행추가를 한번으로 막기위한 함수
		if(Today() != ds_Save.GetColumn(0,"PATROL_YMD"))	
		{
			ds_Save.InsertRow(0);										//insert시 맨위에 행추가
			ds_Save.SetColumn(0,"CHK_DOCU_REF_YN","20");				//점검표등록여부
			ds_Save.SetColumn(0,"PATROL_YMD",Today());					//순찰일자
			ds_Save.SetColumn(0, "TAKER_EMPID1_NM", edt_empnm1.text);	
			ds_Save.SetColumn(0, "TAKER_EMPID2_NM", edt_empnm2.text);
			ds_Save.SetColumn(0, "TAKER_EMPID3_NM", edt_empnm3.text);
			ds_Save.SetColumn(0, "TAKER_EMPID1",edt_empid1.text );	
			ds_Save.SetColumn(0, "TAKER_EMPID2",edt_empid2.text );
			ds_Save.SetColumn(0, "TAKER_EMPID3",edt_empid3.text );	
			strRowType = "insert";
		}
	}else{
		alert('순찰계획을 선택해주세요');
    }
}


// ---------------------------------------------------------
// 달력 팝업 및 점검대장 팝업
// ---------------------------------------------------------
function grd_MultiSave_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	


	if("CHK_DOCU_REF_YN" == grd_MultiSave.GetCellProp("body",nCell,"ColId"))	//점검대장 등록여부
	{
		if(ds_Save.GetColumn(nRow,"PATROL_YMD") == null)
		{
			alert('순찰일자를 먼저 등록해 주세요');
			return;
		}
		else if(strRowType == "insert")
		{
			var strChkFlag = fn_D1_CheckReg();
			//alert(strChkFlag);
			if(!strChkFlag)
			{
				alert('이미 등록된 순찰일자입니다.');
				return;
			}
		}
			
		var strCopyType = "";
		var args = "";
		
		strCopyType = strRowType;
		
		args  = "FACI_NUM="    +strFaciNum;								//시설물번호
		args += " DETAIL_NUM=" +strDetailNum;							//상세번호
		args += " PATROL_YMD=" +ds_Save.GetColumn(nRow,"PATROL_YMD");	//순찰일자
		args += " ROWTYPE="    +strRowType;
		
		strRowType = gfn_Dialog("D10","D1202010033U",args,700,395,true,-1,-1);
		
		if(strRowType == "savePass")
		{
			strRowType = "update";
			ds_Save.SetColumn(ds_Save.row,"CHK_DOCU_REF_YN","10");	//점검대장등록 여부
		}else if(strRowType == "saveNotPass")
		{
			strRowType = "update";
			ds_Save.SetColumn(ds_Save.row,"CHK_DOCU_REF_YN","10");	//점검대장등록 여부
		}else
		{
			strRowType = strCopyType;
		}
	}
	//이 화면은 등록만 되고 수정이 안됨, 수정을 하고 싶은면 삭제후 다시 등록 요함. 그래서 insert만 활성화 됨.
	if(ds_Save.GetRowType(nRow) == "insert")
	{
		grd_MultiSave.SetCellProp("body",4,"Edit","normal");	//점검사항
	}else
	{
		grd_MultiSave.SetCellProp("body",4,"Edit","none");		//점검사항
	}
}


