//-------------------------------------------------------------------------------
// 함수명   : fn_D1_Find()
// 설명     : 순찰점검 주 계획을 조회한다.
// return 내용 : 없음
//-------------------------------------------------------------------------------
function fn_D1_Find()
{
    
	if("RE" == substr(strFaciNum,0,2))		//정류기일때
	{	
		var strSvcID = "fn_D1_Find";
		var strPart = "D10";				
		var strURL = "handle.do?ServiceName=D1202010021U-service"
				   + "&FACI_NUM="		+strFaciNum				//시설물번호
				   + "&PATROL_YMD=" 	+strPatrolYmd			//순찰일자
				   ;
		var strInDatasets  = "";
		var strOutDatasets  = "ds_RecDraList=ds_RecDraList ds_RectDraSearch=ds_RectDraSearch";
		var strArgument  = "find";
		
		gfn_SyncCall(strSvcID,
					  strPart,
					  strURL,
					  strInDatasets,
					  strOutDatasets,
					  strArgument);

	}else if("DR" == substr(strFaciNum,0,2))		//배류기일때
	{	
		var strSvcID = "fn_D1_Find";
		var strPart = "D10";				
		var strURL = "handle.do?ServiceName=D1202010021U-service"
				   + "&FACI_NUM="		+strFaciNum				//시설물번호
				   + "&PATROL_YMD=" 	+strPatrolYmd			//순찰일자
				   ;
		var strInDatasets  = "";
		var strOutDatasets  = "ds_RecDraList=ds_RecDraList ds_RectDraSearch=ds_RectDraSearch";
		var strArgument  = "find2";
		
		gfn_SyncCall(strSvcID,
					  strPart,
					  strURL,
					  strInDatasets,
					  strOutDatasets,
					  strArgument);
	}
	fn_d1_Decide();
	fn_D1_Set();
}


//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Set()
//   설명:  순찰중점관리 교량첨가배관 점검대장 해당 내용을 셋팅한다.
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Set()
{	//alert("set");
	for(var i=0;i<ds_RecDraList.RowCount();i++)
	{	//alert("for");
		if(ds_RecDraList.GetColumn(i,"PASS_YN") == "Y")			//적합여부
		{
			ds_RecDra.SetColumn(i,"CHK_PASS",1);				//적합
		}else if(ds_RecDraList.GetColumn(i,"PASS_YN") == "N")	//적합여부
		{
			ds_RecDra.SetColumn(i,"CHK_NOT_PASS",1);			//부적합
		}
		ds_RecDra.SetColumn(i,"RMK",ds_RecDraList.GetColumn(i,"RMK"));					//비고
		
		edt_OutputVoltage.Text = ds_RecDraList.GetColumn(i,"OUTPUT_VOLTAGE");			//출력전압
		edt_OutputVoltaic.Text = ds_RecDraList.GetColumn(i,"OUTPUT_VOLTAIC");			//출력전류
		edt_RectiferElectPot.Value = ds_RecDraList.GetColumn(i,"RECTIFER_ELECT_POT");	//정,배류점전위
	}
}


//---------------------------------------------------------
// 검사 결과에 따른 체크값 설정
//---------------------------------------------------------
function grd_Multi_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if("CHK_PASS" == grd_Multi.GetCellProp("body",nCell,"ColId"))
	{	
		ds_RecDra.SetColumn(nRow,"CHK_NOT_PASS",0);
	}else if("CHK_NOT_PASS" == grd_Multi.GetCellProp("body",nCell,"ColId"))
	{	
		ds_RecDra.SetColumn(nRow,"CHK_PASS",0);
	}
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_Save()
//   설명  : 등록,수정을 한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Save()
{

	if(ds_RecDra.RowCount() == 0)
	{
		return;
	}

	var strType = "";
	//var strPassYn = "";
	//var strChkOpini = "";
	
	
	for(var i=0; i<ds_RecDra.RowCount(); i++)
	{
		if(edt_OutputVoltage.Text == "" || edt_OutputVoltage.Text == null)	//출력전압
		{
			alert(gfn_GetMessage("M000011","%AAA%","출력전압을"));	//출력전압을 입력하세요
			return;
		}
		
		if(edt_OutputVoltaic.Text == "" || edt_OutputVoltaic.Text == null)	//출력전류
		{
			alert(gfn_GetMessage("M000011","%AAA%","출력전류를"));	//출력전류를 입력하세요
			return;
		}
		
		if(edt_RectiferElectPot.Value == "" || edt_RectiferElectPot.Value == null)	//정,배류점 전위
		{
			alert(gfn_GetMessage("M000011","%AAA%","정,배류점전위를"));	//정,배류점전위를 입력하세요
			return;
		}
	
		var strRstCd = 0;
		
		if(ds_RecDra.GetColumn(i,"CHK_PASS") == 1)
		{
			strRstCd = "Y";
		}else if(ds_RecDra.GetColumn(i,"CHK_NOT_PASS") == 1)
		{
			strRstCd = "N";
		}else 
		{
			alert('적합여부를 모두 체크해 주세요');
			return;
		}
		ds_RecDra.UpdateControl = false;
		if(ds_RecDraList.GetRowCount()>0)
		{
			ds_RecDra.SetRowType(i, "update");
		}
		else
		{
			ds_RecDra.SetRowType(i, "insert");
		}
		// ds_RecDra.UpdateControl = false;
		// ds_RecDra.SetRowType(i, strType);
		
		//수정 등록시 셋팅할 사용자 정보
		ds_RecDra.SetColumn(i, "FACI_NUM", strFaciNum);								//정배류기번호
		ds_RecDra.SetColumn(i, "PATROL_YMD", strPatrolYmd);							//순찰일자
		ds_RecDra.SetColumn(i, "PASS_YN", strRstCd);								//적합여부
	//	ds_RecDra.SetColumn(i, "CHK_OPINI", ta_ChkOpini.Text);						//점검의견
		ds_RecDra.SetColumn(i, "OUTPUT_VOLTAGE", edt_OutputVoltage.Text);			//출력전압
		ds_RecDra.SetColumn(i, "OUTPUT_VOLTAIC", edt_OutputVoltaic.Text);			//출력전류
		ds_RecDra.SetColumn(i, "RECTIFER_ELECT_POT", edt_RectiferElectPot.Value);		//정,배류점전위
		
		ds_RecDra.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
		ds_RecDra.SetColumn(i, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자IP
		ds_RecDra.SetColumn(i, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_RecDra.SetColumn(i, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자IP
	}
	fn_D1_SaveOpni(); // 의견 저장 
	var strSvcID         = "fn_D1_Save";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1202010021U-service" ;					
	var strInDatasets    = "ds_RecDra=ds_RecDra:u";
	var strOutDatasets   = "";		
	var strArgument      = "save";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );		   

	if(ErrorCode<0)
	{
		arrList[1] = "saveNo";
		return false;
	}
	alert(gfn_GetMessage("M000009","%AAA%","저장을"));	
	
	var arrList = array();
	arrList[0] = edt_RectiferElectPot.Value;
	
	arrList[1] = "saveOk";
	Close(arrList);
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_SaveOpni()
//   설명  : 의견을 등록한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_SaveOpni()
{
    ds_RecDraOpni.AddRow();
	ds_RecDraOpni.UpdateControl=false;	
	//수정 등록시 셋팅할 사용자 정보
	ds_RecDraOpni.SetColumn(0, "FACI_NUM", strFaciNum);								//정배류기번호
	ds_RecDraOpni.SetColumn(0, "PATROL_YMD", strPatrolYmd);							//순찰일자
	ds_RecDraOpni.SetColumn(0, "CHK_OPINI", ta_ChkOpini.Text);	
	ds_RecDraOpni.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//수정자사번
	ds_RecDraOpni.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//수정자IP
    ds_RecDraOpni.SetRowType(0,"update");
	
	var strSvcID         = "fn_D1_Save";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1202010021U-service" ;					
	var strInDatasets    = "ds_RecDraOpni=ds_RecDraOpni:u";
	var strOutDatasets   = "";		
	var strArgument      = "save2";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );		   

	if(ErrorCode<0)
	{
		return false;
	}

}

