function fn_d1_Decide()
{
	var strSvcID = "fn_D1_Find";
	var strPart = "D10";				
	var strURL = "handle.do?ServiceName=D1202010021U-service"
			   + "&FACI_NUM="		+strFaciNum				//시설물번호
			   + "&PATROL_YMD=" 	+strPatrolYmd			//순찰일자
			   ;
	var strInDatasets  = "";
	var strOutDatasets  = "ds_decide=ds_decide";
	var strArgument  = "find3";
	
	gfn_SyncCall(    strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
					);
				  
	edt_decide.Text = ds_decide.GetColumn(0,"DECIDE_PROC_STS_NM");
	ta_ChkOpini.Text = ds_decide.GetColumn(0,"CHK_OPINI");						//점검의견
}

function edt_RectiferElectPot_OnCharChanged(obj,strPreText,strPostText)
{
	if(left(strPostText,1) == '-'){ obj.text = strPreText; }
}
