#include "JS::COMMONA10001.js";
#include "JS::COMMOND10001.js";


//---------------------------------------------------------
//	화면변수
//---------------------------------------------------------
var strFaciNum;               // 시설물명  

//---------------------------------------------------------
//	화면초기화함수
//---------------------------------------------------------
function D1202010025U_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	gfn_D1_SetGrid_Sort(grd_List);
	fn_Init();	
}

//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------
function fn_Init()
{
	// gfn_D1_GetCodeList("ds_WorkFlag","B10010");     // 공사코드 Lov 출력
    cal_PatrolYmd1.Text = parseInt(substr(today(), 0, 4)) - 1 + substr(today(), 4, 4);
    cal_PatrolYmd2.Text = today();
    
    fn_CommonSearch();
}

//-------------------------------------------------------------------------------
// 엑셀 버튼 실행
//-------------------------------------------------------------------------------
function fn_CommonExcelExport()
{
	grd_List.ExportExcelEx("sheet1");
}

//---------------------------------------------------------
//	공통 버튼 클릭 - Print
//---------------------------------------------------------
function fn_CommonPrint()
{
	PrintScreen(true);
}

//-------------------------------------------------------------------------------
// 조회 버튼 실행
//-------------------------------------------------------------------------------
function fn_CommonSearch()
{
	//입력한 날짜가 오늘 보다 클 경우
	if(!fn_DateCheckToday(cal_PatrolYmd1,cal_PatrolYmd2))
	{
		return;
	}
	//TO 날짜는 FROM날짜보다 클 경우
	if(!fn_DateCheck(cal_PatrolYmd1,cal_PatrolYmd2))
	{
		return;
	}
	// 그리드를 초기화 한다
	gfn_SetHeadClearAll(grd_List);
	strFaciNum =  FACI_NUM;      // 시설물명 
	// 데이타를 추출한다
	//gfn_D1_ReferDataSet("ds_codem","ds_codem",strServiceName1,checkOpt,strURL);
	
	var strSvcID = "";
	var strPart = "D10";
	var strURL  = "handle.do?ServiceName=D1202010025U-service&find=1"
	            + "&FACI_NUM ="  	+strFaciNum
	            + "&PATROL_STYMD =" +cal_PatrolYmd1.Value 
	            + "&PATROL_EDYMD =" +cal_PatrolYmd2.Value 
	            ;
	       
	var strInDatasets   = "";
	var strOutDatasets  = "ds_codem=ds_codem";
	var strArgument     = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  
			   );
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	if(ds_codem.GetRowCount()==0) trace("조회결과가 없습니다.");
	
	if(ds_codem.GetRowCount()==0)
	{
        grd_List.NoDataText="조회결과가 없습니다.";
    }

}
//-------------------------------------------------------------------------------
//  Call Back 함수 
//-------------------------------------------------------------------------------
function fn_CallBack(srvId,ErrorCode,ErrorMsg)
{
	if(ErrorCode < 0)
	{
		return;
	}

	if(srvId == "CommonSearch")
	{
		// 조회 후 처리

	}
}


//---------------------------------------------------------
//	공통 버튼 클릭 - 선택
//---------------------------------------------------------
function fn_CommonSelect()
{
	var arrList = array();
	var nRow = ds_codem.row;

	// 코드, 코드명

	arrList[0] = ds_codem.GetColumn(nRow,"PATROL_YMD");		// 순찰일자
	arrList[1] = ds_codem.GetColumn(nRow,"DRAINAGE_NM");		// 시설물명

	Close(arrList);
}
//---------------------------------------------------------
//	그리드 cell 더블 클릭 이벤트 
//--------------------------------------------------------- 
function grd_List_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	fn_CommonSelect();
}

