function D1202010040U_OnLoadCompleted(obj) {

	gfn_LoadForm(obj);

	fn_D1_Search();
	
	fn_initData();	
}   

//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------
function fn_initData() {

	//gfn_SetButton("0001000");
	
	ct01.ClearData("COD_Values");
	ct01.ClearData("COD_Colors");
	ct01.BackColor="#e5eaeb";
	
	ct02.ClearData("COD_Values");
	ct02.ClearData("COD_Colors");
	ct02.BackColor="#e5eaeb";
	
	ct03.ClearData("COD_Values");
	ct03.ClearData("COD_Colors");
	ct03.BackColor="#e5eaeb";

	fn_Chart_Display();	
}

/********************************************************************************
* 기      능   :  공통 조회 클릭시 조회 업무 수행 
********************************************************************************/
function fn_D1_Search() {
	
	var ServiceId       = "";
	var ServicePart     = "D10";
	var Url             = "handle.do?ServiceName=D1202010020U-service" + "&FACI_NUM=" + FACI_NUM
						   + "&PATROL_YMD1=" + fromCal.value + "&PATROL_YMD2=" + toCal.value
						   + "&VALVE_NUM=" + VALVE_NUM;
	var InputDsList     = "";
	var OutputDsList    = "ds_list=ds_list";
	var Argument        = "find2";
	
	gfn_SyncCall( // 동기화 가능한 펑션
				 ServiceId
				,ServicePart
				,Url
				,InputDsList
				,OutputDsList
				,Argument
				);   	
				
	var ServiceId       = "";
	var ServicePart     = "D10";
	var Url             = "handle.do?ServiceName=D1202010020U-service" + "&FACI_NUM=" + FACI_NUM
						   + "&PATROL_YMD1=" + fromCal.value + "&PATROL_YMD2=" + toCal.value
						   + "&VALVE_NUM=" + VALVE_NUM;
	var InputDsList     = "";
	var OutputDsList    = "ds_list1=ds_list1";
	var Argument        = "find3";
	
	gfn_SyncCall( // 동기화 가능한 펑션
				 ServiceId
				,ServicePart
				,Url
				,InputDsList
				,OutputDsList
				,Argument
				);   	
							
	var ServiceId       = "";
	var ServicePart     = "D10";
	var Url             = "handle.do?ServiceName=D1202010020U-service" + "&FACI_NUM=" + FACI_NUM
						   + "&PATROL_YMD1=" + fromCal.value + "&PATROL_YMD2=" + toCal.value
						   + "&VALVE_NUM=" + VALVE_NUM;
	var InputDsList     = "";
	var OutputDsList    = "ds_list2=ds_list2";
	var Argument        = "find4";
	
	gfn_SyncCall( // 동기화 가능한 펑션
				 ServiceId
				,ServicePart
				,Url
				,InputDsList
				,OutputDsList
				,Argument
				);   							
}	

/********************************************************************************
* 기      능   :  공통 조회 클릭시 조회 업무 수행 
********************************************************************************/
function fn_CommonSearch() {	

	fn_D1_Search(); 
	   
	fn_Chart_Display();	
}

function fn_Chart_Display() {

	ct01.OpenData("COD_Values", 2, ds_list.RowCount()); // 데이타를 Chart에 뿌려주는 메소드 
	ct01.OpenData("COD_Colors", 2, ds_list.RowCount()); // 데이타를 Chart에 뿌려주는 메소드
	//ct01.AxisY.Grid = 1;		//가로 그리드선 보여줄지 여부 (1, 0)
	
	ct01.AxisY.LabelsFormat.Decimals = 0; // 소수점이하 자리수 표시 1: 첫째자리, 2: 둘째자리 3: 셋째자리	
	ct01.AxisY.LabelsFormat.Format = "AxisFormat_Number";
	
	ct01.AxisY2.LabelsFormat.Decimals = 1;
	ct01.AxisY2.LabelsFormat.Format = "AxisFormat_Number";
	ct01.SerLegBoxObj.Docked = "Docked_Top"; // 범례위치 고정
        
	ct01.Series[0].Gallery = "Gallery_Bar"; // 1: Line Chart, 2:  Bar Chart 
	ct01.Series[0].cylsides = 30;
	ct01.SerLeg(0) = "측정전위";
    ct01.Volume = 20; // 그래프의 넓이
    
	ct01.Series[1].Gallery = "Gallery_Lines"; // 1: Line Chart, 2:  Bar Chart 
	ct01.Series[1].MarkerShape = "MarkerShape_Circle";
	ct01.SerLeg(1) = "측정전위";
	
	ct01.Series[0].Stacked = "Stacked_Normal";
	ct01.Series[1].Stacked = "Stacked_Normal";

	ct01.Series[0].Stacked = true;
	ct01.Series[1].Stacked = true;
	
	// 데이터및 라벨 표시 
	for(i = 0; i < ds_list.RowCount(); i++) {
	
		ct01.Axis[2].Label(i) = toString(ds_list.GetColumn(i, "DAY"), "@@@@.@@.@@");

		ct01.Value(0, i) = ds_list.GetColumn(i, "MV");
		ct01.Value(1, i) = ds_list.GetColumn(i, "MV");
		
		ct01.Series[3].YAxis = true;
	}
	
	ct01.AxisX.Title.Text = "날짜"; 
	
	ct01.AxisY.Title.Text = "전위값"; 
	
	// CloseData
	ct01.CloseData("COD_Values");
	ct01.CloseData("COD_Colors");
	
	ct01.Visible = true;
	
	ct02.OpenData("COD_Values", 2, ds_list1.RowCount()); // 데이타를 Chart에 뿌려주는 메소드 
	ct02.OpenData("COD_Colors", 2, ds_list1.RowCount()); // 데이타를 Chart에 뿌려주는 메소드
	//ct01.AxisY.Grid = 1;		//가로 그리드선 보여줄지 여부 (1, 0)
	
	ct02.AxisY.LabelsFormat.Decimals = 0; // 소수점이하 자리수 표시 1: 첫째자리, 2: 둘째자리 3: 셋째자리	
	ct02.AxisY.LabelsFormat.Format = "AxisFormat_Number";
	
	ct02.AxisY2.LabelsFormat.Decimals = 1;
	ct02.AxisY2.LabelsFormat.Format = "AxisFormat_Number";
	ct02.SerLegBoxObj.Docked = "Docked_Top"; // 범례위치 고정
        
	ct02.Series[0].Gallery = "Gallery_Bar"; // 1: Line Chart, 2:  Bar Chart 
	ct02.Series[0].cylsides = 30;
	ct02.SerLeg(0) = "측정전위";
    ct02.Volume = 20; // 그래프의 넓이
    
	ct02.Series[1].Gallery = "Gallery_Lines"; // 1: Line Chart, 2:  Bar Chart 
	ct02.Series[1].MarkerShape = "MarkerShape_Circle";
	ct02.SerLeg(1) = "측정전위";
	
	ct02.Series[0].Stacked = "Stacked_Normal";
	ct02.Series[1].Stacked = "Stacked_Normal";

	ct02.Series[0].Stacked = true;
	ct02.Series[1].Stacked = true;
	
	// 데이터및 라벨 표시 
	for(i = 0; i < ds_list1.RowCount(); i++) {
	
		ct02.Axis[2].Label(i) = toString(ds_list1.GetColumn(i, "DAY"), "@@@@.@@.@@");

		ct02.Value(0, i) = ds_list1.GetColumn(i, "MV1");
		ct02.Value(1, i) = ds_list1.GetColumn(i, "MV1");
		
		ct02.Series[3].YAxis = true;
	}
	
	ct02.AxisX.Title.Text = "날짜"; 
	
	ct02.AxisY.Title.Text = "전위값"; 
	
	// CloseData
	ct02.CloseData("COD_Values");
	ct02.CloseData("COD_Colors");
	
	ct02.Visible = true;
	
	ct03.OpenData("COD_Values", 2, ds_list2.RowCount()); // 데이타를 Chart에 뿌려주는 메소드 
	ct03.OpenData("COD_Colors", 2, ds_list2.RowCount()); // 데이타를 Chart에 뿌려주는 메소드
	//ct01.AxisY.Grid = 1;		//가로 그리드선 보여줄지 여부 (1, 0)
	
	ct03.AxisY.LabelsFormat.Decimals = 0; // 소수점이하 자리수 표시 1: 첫째자리, 2: 둘째자리 3: 셋째자리	
	ct03.AxisY.LabelsFormat.Format = "AxisFormat_Number";
	
	ct03.AxisY2.LabelsFormat.Decimals = 1;
	ct03.AxisY2.LabelsFormat.Format = "AxisFormat_Number";
	ct03.SerLegBoxObj.Docked = "Docked_Top"; // 범례위치 고정
        
	ct03.Series[0].Gallery = "Gallery_Bar"; // 1: Line Chart, 2:  Bar Chart 
	ct03.Series[0].cylsides = 30;
	ct03.SerLeg(0) = "측정전위";
    ct03.Volume = 20; // 그래프의 넓이
    
	ct03.Series[1].Gallery = "Gallery_Lines"; // 1: Line Chart, 2:  Bar Chart 
	ct03.Series[1].MarkerShape = "MarkerShape_Circle";
	ct03.SerLeg(1) = "측정전위";
	
	ct03.Series[0].Stacked = "Stacked_Normal";
	ct03.Series[1].Stacked = "Stacked_Normal";

	ct03.Series[0].Stacked = true;
	ct03.Series[1].Stacked = true;
	
	// 데이터및 라벨 표시 
	for(i = 0; i < ds_list2.RowCount(); i++) {
	
		ct03.Axis[2].Label(i) = toString(ds_list2.GetColumn(i, "DAY"), "@@@@.@@.@@");

		ct03.Value(0, i) = ds_list2.GetColumn(i, "MV2");
