var rec_dre_flag = 1;

function D1202010041U_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	gfn_SetButton("00100001");	
	fn_CommonSearch();
}


function fn_D1_Set()
{	
	var vMaxCnt = 11;
	var vCnt = ds_GridDataList.RowCount();
	
	
	for(var i=0;i<vMaxCnt;i++)
	{	
	    if(i < vCnt)
	    {		
			ds_GridData.SetColumn(i, "FACI_NUM", ds_GridDataList.GetColumn(i,"FACI_NUM"));
			ds_GridData.SetColumn(i, "PATROL_YMD", ds_GridDataList.GetColumn(i,"PATROL_YMD"));
		
			ds_GridData.SetColumn(i, "CHK_PASS", ds_GridDataList.GetColumn(i,"CHK_PASS"));
			ds_GridData.SetColumn(i, "CHK_NOT_PASS", ds_GridDataList.GetColumn(i,"CHK_NOT_PASS"));
			ds_GridData.SetColumn(i, "CHK_NULL", ds_GridDataList.GetColumn(i,"CHK_NULL"));
			ds_GridData.SetColumn(i,"RMK",ds_GridDataList.GetColumn(i,"RMK"));					
			ds_GridData.SetColumn(i,"CHK_ITEM",ds_GridDataList.GetColumn(i,"CHK_ITEM"));
			
			
		}
		else
		{
			ds_GridData.SetColumn(i, "FACI_NUM"  , FACI_NUM);
			ds_GridData.SetColumn(i, "PATROL_YMD", PATROL_YMD);		
		}
	}

    vCnt = ds_GridDataList2.RowCount();
    vMaxCnt = 8;

	for(var i=0;i<vMaxCnt;i++)
	{	
	    if(i < vCnt)
	    {
			ds_GridData2.SetColumn(i, "FACI_NUM"  , ds_GridDataList2.GetColumn(i,"FACI_NUM"));
			ds_GridData2.SetColumn(i, "PATROL_YMD", ds_GridDataList2.GetColumn(i,"PATROL_YMD"));					
			ds_GridData2.SetColumn(i,"CHK_ITEM"   , ds_GridDataList2.GetColumn(i,"CHK_ITEM"));
			ds_GridData2.SetColumn(i,"RMK",ds_GridDataList2.GetColumn(i,"RMK"));
			ds_GridData2.SetColumn(i,"MEAS_RSLT_VAL1",ds_GridDataList2.GetColumn(i,"MEAS_RSLT_VAL1"));
			ds_GridData2.SetColumn(i,"MEAS_RSLT_VAL2",ds_GridDataList2.GetColumn(i,"MEAS_RSLT_VAL2"));	    
	    }
	    else
	    {
			ds_GridData2.SetColumn(i, "FACI_NUM"  , FACI_NUM);
			ds_GridData2.SetColumn(i, "PATROL_YMD", PATROL_YMD);					
			ds_GridData2.SetColumn(i,"RMK","");
			ds_GridData2.SetColumn(i,"MEAS_RSLT_VAL1",0);
			ds_GridData2.SetColumn(i,"MEAS_RSLT_VAL2",0);	    	    
	    }
	}
}


function fn_CommonSearch()
{
	
	if("RE" == substr(FACI_NUM,0,2))		//정류기일때
	{	
		rec_dre_flag = 1;
	}else if("DR" == substr(FACI_NUM,0,2))		//배류기일때
	{	
		rec_dre_flag = 2;
	}

	ds_SearchTop.ClearData();
	ds_SearchTop.AddRow();
	ds_SearchTop.SetColumn(0,"FACI_NUM",FACI_NUM);
	ds_SearchTop.SetColumn(0,"PATROL_YMD",PATROL_YMD);
	ds_SearchTop.SetColumn(0,"REC_DRA_FG",rec_dre_flag);
	
	fn_TopSearch();
	fn_GridDataSearch();

	ds_GridData.FireEvent = false;
	fn_D1_Set();
	ds_GridData.FireEvent = true;
	
}


function fn_TopSearch()
{
	var strSvcID = "find";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1205010041U-service";
 	var strInDatasets  = "ds_SearchTop=ds_SearchTop";
	var strOutDatasets = "ds_TopData=ds_TopData";
	var strArgument  = "search";
				   	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	mae_Sum_PRESS_CATHODIC_LENG.Value = toNumber(mae_M_PRESS_CATHODIC_LENG.Value) + toNumber(mae_L_PRESS_CATHODIC_LENG.Value);
}


function fn_GridDataSearch()
{
	var strSvcID = "find";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1205010041U-service";
 	var strInDatasets  = "ds_SearchTop=ds_SearchTop";
	var strOutDatasets = "ds_GridDataList=ds_GridData ds_GridDataList2=ds_GridData";
	var strArgument  = "searchGrid";
				   	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

 
function fn_CommonSave()
{
	if(ds_GridData.RowCount() == 0 )
	{
		return;
	}

	for(var i=0; i<ds_GridData.RowCount(); i++)
	{
		var checkPass = 0;
		
		if(ds_GridData.GetColumn(i,"CHK_PASS") == 1)
		{
			checkPass = "Y";
		}else if(ds_GridData.GetColumn(i,"CHK_NOT_PASS") == 1)
		{
			checkPass = "N";
		}else if(ds_GridData.GetColumn(i,"CHK_NULL") == 1)
		{
			checkPass = "X";
		}else 
		{
			alert('적합여부를 모두 체크해 주세요');
			return;
		}	
		
		ds_GridData.SetColumn(i, "PASS_YN", checkPass);	
				
	}
	
	ds_GridData.AppendDataset(ds_GridData2); // 그리드 두개 통합	
	
	for(var i=0; i<ds_GridData.RowCount(); i++)
	{
		ds_GridData.SetColumn(i, "CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_GridData.SetColumn(i, "CRT_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자IP
		ds_GridData.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
		ds_GridData.SetColumn(i, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자IP
		ds_GridData.SetColumn(i, "RECTIFER_ELECT_POT", mae_RectiferElectPot.Value);
	}

	ds_TopData.SetColumn(0, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));	//등록자사번
	ds_TopData.SetColumn(0, "UPD_IP", gfn_GetGlobalValue("G_IPADDRESS"));	//등록자IP
	
	ds_SearchTop.SetColumn(0, "UPD_DTM", getDate());	//등록자IP

	var strSvcID         = "Save";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1205010041U-service" ;					
	var strInDatasets    = "ds_GridData=ds_GridData:u ds_TopData=ds_TopData:u ds_SearchTop=ds_SearchTop:u";
	var strOutDatasets   = "";		
	var strArgument      = "save";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );		   
	
	alert(gfn_GetMessage("M000009","%AAA%","저장을"));
	Reload();
}


function grd_Multi_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if("CHK_PASS" == grd_Multi.GetCellProp("body",nCell,"ColId"))
	{	
		ds_GridData.SetColumn(nRow,"CHK_NOT_PASS","0");
		ds_GridData.SetColumn(nRow,"CHK_NULL","0");
	}else if("CHK_NOT_PASS" == grd_Multi.GetCellProp("body",nCell,"ColId"))
	{	
		ds_GridData.SetColumn(nRow,"CHK_PASS","0");
		ds_GridData.SetColumn(nRow,"CHK_NULL","0");
	}else if("CHK_NULL" == grd_Multi.GetCellProp("body",nCell,"ColId"))
	{	
		ds_GridData.SetColumn(nRow,"CHK_PASS","0");
		ds_GridData.SetColumn(nRow,"CHK_NOT_PASS","0");
	}
}


function btn_Sanction_OnClick(obj) //전자결재
{
    if(ds_TopData.GetColumn(0,"DECIDE_PROC_STS")=="0"||ds_TopData.GetColumn(0,"DECIDE_PROC_STS")=="1"||ds_TopData.GetColumn(0,"DECIDE_PROC_STS")=="2")
    {
		return;
    }
	
	if(ds_GridData.GetRowCount()>0)
	{
		gfv_callGWInfo("fmD42","D10","D1202010019P",2,FACI_NUM+","+PATROL_YMD); // old
		// gfv_callGWInfo("","D10","D1202010019P",2,FaciNum+","+PatrolYmd); // new
		// 1번 : 화면명칭   2번 : 업무그룹 (b10,b20... OZ 인경우 필수), 3번: 프로그램ID (oz인경우 필수) 4: 아규먼트 갯수 (1....5)
		// 5번 : 아규먼트 목록 (형식 , 표시로 각 아규먼트 구분자 사용)
	}
}


function btn_OZ_OnClick(obj) //장표
{

	if(ds_GridData.GetRowCount()>0)
	{
		// 함수 호출용 파라미터를 설정한다
		var strTitle    ="정배류기 점검대장"; 
		var strPart     = "D10";           
		var strReport   = "D1202010019P";	//화면 아이디 따기 
		var strParameter= "FACI_NUM=" 	 + FACI_NUM		
						+ ",PATROL_YMD=" + PATROL_YMD		
						+ ",REC_DRA_FG=" + rec_dre_flag
						;
				
		gfn_ReportPopup(strTitle,strPart,strReport,strParameter);
	}
}

function mae_RectiferElectPot_OnCharChanged(obj,strPreText,strPostText)
{
	if(left(strPostText,1) == '-'){ obj.text = strPreText; }
}
