/*===============================================================================
 * 함수명: fn_CommonSearch()
   설명: 공통 조회 클릭시 조회 업무  수행 
   return 내용: 없음
===============================================================================*/ 
function fn_CommonSearch()
{   
	var str_PtrlYmd = cal_PPatrolYmd.Value;

	if(str_PtrlYmd == '' || str_PtrlYmd == null){
		alert("순찰점검일자를 입력하세요. ");
	}

	fn_chk_quarter();

    fn_D1_Find();    

    fn_D1_Set();
}

function fn_chk_quarter()
{
	if(SubStr(cal_PPatrolYmd.Value,4,2)=="01"||SubStr(cal_PPatrolYmd.Value,4,2)=="02"||SubStr(cal_PPatrolYmd.Value,4,2)=="03")
	{
		chk_quarter = '1';
		Patrol_Str_ymd = SubStr(cal_PPatrolYmd.Value,0,4) + "0101";
	}
	else if(SubStr(cal_PPatrolYmd.Value,4,2)=="04"||SubStr(cal_PPatrolYmd.Value,4,2)=="05"||SubStr(cal_PPatrolYmd.Value,4,2)=="06")
	{
		chk_quarter = '2';
		Patrol_Str_ymd = SubStr(cal_PPatrolYmd.Value,0,4) + "0401";
	}
	else if(SubStr(cal_PPatrolYmd.Value,4,2)=="07"||SubStr(cal_PPatrolYmd.Value,4,2)=="08"||SubStr(cal_PPatrolYmd.Value,4,2)=="09")
	{
		chk_quarter = '3';
		Patrol_Str_ymd = SubStr(cal_PPatrolYmd.Value,0,4) + "0701";
	}
	else if(SubStr(cal_PPatrolYmd.Value,4,2)=="10"||SubStr(cal_PPatrolYmd.Value,4,2)=="11"||SubStr(cal_PPatrolYmd.Value,4,2)=="12")
	{
		chk_quarter = '4';
		Patrol_Str_ymd = SubStr(cal_PPatrolYmd.Value,0,4) + "1001";
	}
}
//-------------------------------------------------------------------------------
// 함수명: fn_CommonPrint()
// 설명  : 순찰실적 장표를 출력한다. 
// arguments 없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonPrint()
{
	if(cal_PPatrolYmd.Value==""||cob_branch.Value==""||cob_Team.Value=="")
    {
		alert("지사,팀,순찰일자는 필수선택입니다.");
		return;
    }

    fn_chk_quarter();
    var Block_cd = '';
	if(cob_BLOCK.Value == '')
	{
		Block_cd = '%';
	}
	else
	{
		Block_cd = cob_BLOCK.Value;
	} 

   //	함수 호출용 파라미터를 설정한다
	var strTitle ="순찰실적조"; 
	var strPart ="D10" ;
	var strReport="D1203010010P_1";
	var strParameter="CHK_QUARTER="+chk_quarter
				   +",BRANCH_CD="+cob_branch.Value
		           +",TEAM_CD="+cob_Team.Value
		           +",BLOCK_CD="+Block_cd
		           +",PATROL_YMD="+cal_PPatrolYmd.Value 
		           +",PATROL_STR_YMD="+Patrol_Str_Ymd 
					;

    //	공통 레포트 팝업 함수 호출 
	gfn_ReportPopup(strTitle,strPart,strReport,strParameter);

}

