/********************************************************************************
* 기      능   :  지사별 구를 조회하여 콤보박스에 셋팅
********************************************************************************/
function cob_PBranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    gfn_D1_SetComboBox_Teamcd("ds_Team",cob_branch);


    // if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") {
		// cob_Team.value = "";
	// } else {
	    // cob_Team.value = gfn_GetGlobalValue("G_DEPT_CODE");
	// }
    cob_Team.value = gfn_GetGlobalValue("G_DEPT_CODE");
    gfn_D1_SetComboBox_Blockcd("ds_Block",cob_branch,cob_Team);
    cob_block.value = "";
}


function cob_Team_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	gfn_D1_SetComboBox_Blockcd("ds_Block",cob_branch,cob_Team);
    cob_block.value = "";
}

//-------------------------------------------------------------------------------
// 함수명   : fn_D1_Find_Set()
// 설명     : 순찰점검 e을 조회한다.
// return 내용 : 없음
//-------------------------------------------------------------------------------
function fn_D1_Find()
{	
	fn_chk_quarter();

	ds_Search_Req.ClearData();
	var row = ds_Search_Req.AddRow();
	ds_Search_Req.SetColumn(row,"CHK_YEAR",SubStr(cal_PPatrolYmd.Value,0,4));
	ds_Search_Req.SetColumn(row,"CHK_QUARTER",chk_quarter);	
	ds_Search_Req.SetColumn(row,"BRANCH_CD",cob_branch.Value);
	ds_Search_Req.SetColumn(row,"TEAM_CD",cob_Team.Value);
	ds_Search_Req.SetColumn(row,"BLOCK_CD",cob_block.Value);
	ds_Search_Req.SetColumn(row,"PATROL_YMD",cal_PPatrolYmd.Value);
	ds_Search_Req.SetColumn(row,"STR_PATROL_YMD",Patrol_Str_ymd);
	ds_Search_Req.SetColumn(row,"END_PATROL_YMD",cal_PPatrolYmd.Value);

	ds_Req1.ClearData();
	ds_Req2.ClearData();
	ds_Req3.ClearData();
	ds_Req4.ClearData();
	ds_Req5.ClearData();

    var strSvcID        = "find";
	var strPart         = "D10";	             
	var strURL          = "handle.do?ServiceName=D1_PatrolCheckDiaryMgr-service";
	var strInDatasets   = "ds_Search_Req=ds_Search_Req";
	var strOutDatasets  = "ds_Req1=ds_Req1 ds_Req2=ds_Req2 ds_Req3=ds_Req3 ds_Req4=ds_Req4 ds_Req5=ds_Req5";
	var strArgument     = "find=1";

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	

}

function fn_D1_Set()
{  
	edt_Rate_1.Value  =  round((parseInt(edt_Sum_1.Value)  / parseInt(edt_Plan_1.Value) * 100), 2);
	edt_Rate_2.Value  =  round((parseInt(edt_Sum_2.Value)  / parseInt(edt_Plan_2.Value) * 100), 2);
	edt_Rate_3.Value  =  round((parseInt(edt_Sum_3.Value)  / parseInt(edt_Plan_3.Value) * 100), 2);
	edt_Rate_4.Value  =  round((parseInt(edt_Sum_4.Value)  / parseInt(edt_Plan_4.Value) * 100), 2);
	edt_Rate_5.Value  =  round((parseInt(edt_Sum_5.Value)  / parseInt(edt_Plan_5.Value) * 100), 2);
	edt_Rate_6.Value  =  round((parseInt(edt_Sum_6.Value)  / parseInt(edt_Plan_6.Value) * 100), 2);
	edt_Rate_7.Value  =  round((parseInt(edt_Sum_7.Value)  / parseInt(edt_Plan_7.Value) * 100), 2);
	edt_Rate_8.Value  =  round((parseInt(edt_Sum_8.Value)  / parseInt(edt_Plan_8.Value) * 100), 2);
	edt_Rate_9.Value  =  round((parseInt(edt_Sum_9.Value)  / parseInt(edt_Plan_9.Value) * 100), 2);
	edt_Rate_10.Value =  round((parseInt(edt_Sum_10.Value) / parseInt(edt_Plan_10.Value) * 100), 2);

	edt_Rate_11.Value  =  round((parseInt(edt_Sum_11.Value)  / parseInt(edt_Plan_11.Value) * 100), 2);
	edt_Rate_12.Value  =  round((parseInt(edt_Sum_12.Value)  / parseInt(edt_Plan_12.Value) * 100), 2);
	edt_Rate_13.Value  =  round((parseInt(edt_Sum_13.Value)  / parseInt(edt_Plan_13.Value) * 100), 2);
	edt_Rate_14.Value  =  round((parseInt(edt_Sum_14.Value)  / parseInt(edt_Plan_14.Value) * 100), 2);
	edt_Rate_15.Value  =  round((parseInt(edt_Sum_15.Value)  / parseInt(edt_Plan_15.Value) * 100), 2);
	edt_Rate_16.Value  =  round((parseInt(edt_Sum_16.Value)  / parseInt(edt_Plan_16.Value) * 100), 2);
	edt_Rate_17.Value  =  round((parseInt(edt_Sum_17.Value)  / parseInt(edt_Plan_17.Value) * 100), 2);
	edt_Rate_18.Value  =  round((parseInt(edt_Sum_18.Value)  / parseInt(edt_Plan_18.Value) * 100), 2);

	edt_Rate_21.Value  =  round((parseInt(edt_Sum_21.Value)  / parseInt(edt_Plan_21.Value) * 100), 2);
	edt_Rate_22.Value  =  round((parseInt(edt_Sum_22.Value)  / parseInt(edt_Plan_22.Value) * 100), 2);
	edt_Rate_23.Value  =  round((parseInt(edt_Sum_23.Value)  / parseInt(edt_Plan_23.Value) * 100), 2);
	//중앙직공급
	edt_Rate_26.Value  =  round((parseInt(edt_Sum_26.Value)  / parseInt(edt_Plan_26.Value) * 100), 2);
}

