//-------------------------------------------------------------------------------
// 함수명   : btn_Sanction_OnClick(obj)
// 설명     : 전자결재
// return 내용 : 없음
//-------------------------------------------------------------------------------
function btn_Sanction_OnClick(obj)
{
    if(cal_PPatrolYmd.Value==""||cob_branch.Value==""||cob_Team.Value=="")
    {
		alert("지사,팀,순찰일자는 필수선택입니다.");
		return;
    }

    fn_chk_quarter();

    fn_d1_DecideSearch();

    if(ds_Req5.GetColumn(0,"DECIDE_PROC_STS") =="0"||ds_Req5.GetColumn(0,"DECIDE_PROC_STS")=="1"||ds_Req5.GetColumn(0,"DECIDE_PROC_STS")=="2")
    {
		alert("현재 "+ds_Req5.GetColumn(0,"DECIDE_PROC_STS_NM")+" 상태입니다.");
		return;
    }

    if(ds_Req5.GetRowCount() < 1)
    {
		var Result = fn_d1_DecideSave();
		if(Result == false)
		{
			return;
		}
    }

	gfv_callGWInfo("fmD07","D10","D1203010010P",5,chk_quarter+","+cob_branch.Value+","+cob_Team.Value+","+cal_PPatrolYmd.Value+","+Patrol_Str_ymd);


   // 1번 : 화면명칭   2번 : 업무그룹 (b10,b20... OZ 인경우 필수), 3번: 프로그램ID (oz인경우 필수) 4: 아규먼트 갯수 (1....5)
   // 5번 : 아규먼트 목록 (형식 , 표시로 각 아규먼트 구분자 사용)
}

function fn_d1_DecideSearch()
{
	ds_Search_Req.ClearData();
	var row = ds_Search_Req.AddRow();
	ds_Search_Req.SetColumn(row,"CHK_YEAR",SubStr(cal_PPatrolYmd.Value,0,4));
	ds_Search_Req.SetColumn(row,"CHK_QUARTER",chk_quarter);	
	ds_Search_Req.SetColumn(row,"BRANCH_CD",cob_branch.Value);
	ds_Search_Req.SetColumn(row,"TEAM_CD",cob_Team.Value);
	ds_Search_Req.SetColumn(row,"BLOCK_CD",cob_block.Value);
	ds_Search_Req.SetColumn(row,"PATROL_YMD",cal_PPatrolYmd.Value);
	ds_Search_Req.SetColumn(row,"STR_PATROL_YMD",Patrol_Str_ymd);
	ds_Search_Req.SetColumn(row,"END_PATROL_YMD",cal_PPatrolYmd.Value);

	ds_Req5.ClearData();

    var strSvcID        = "decide_serch";
	var strPart         = "D10";	             
	var strURL          = "handle.do?ServiceName=D1_PatrolCheckDiaryMgr-service";
	var strInDatasets   = "ds_Search_Req=ds_Search_Req";
	var strOutDatasets  = "ds_Req5=ds_Req5";
	var strArgument     = "decide_serch=1";

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}			   

//  결재 테이블 등록 
function fn_d1_DecideSave()
{
    ds_save.ClearData();
    ds_save.AddRow();
    ds_save.SetColumn(0,"PATROL_YMD",cal_PPatrolYmd.Value);
    ds_save.SetColumn(0,"BRANCH_CD",cob_branch.Value);
    ds_save.SetColumn(0,"TEAM_CD",cob_Team.Value);
    ds_save.SetColumn(0,"UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
    ds_save.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS"));
    ds_save.SetColumn(0,"CRT_EMPID", gfn_GetGlobalValue("G_USER_NO"));
    ds_save.SetColumn(0,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS"));

	var strSvcID = "";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_PatrolCheckDiaryMgr-service";
	var strInDatasets   = "ds_save=ds_save:u";
	var strOutDatasets  = "";
	var strArgument     = "save";

	gfn_SyncCall(strSvcID,
	              strPart,
	              strURL,
	              strInDatasets,
	              strOutDatasets,
	              strArgument);

	if(ErrorCode < 0)
	{    
		return false;
	}
}
