//-------------------------------------------------------------------------------
//  fv_saveMode   : 수정과 신규 저장 모드 플래그 
//	fv_faciNum	  : 선택된 시설물 번호
//-------------------------------------------------------------------------------

var fv_saveMode = "";
var fv_faciNum = "";
var fv_flag = "0";  
var fv_Row = 0;
//-------------------------------------------------------------------------------
// 함수명: fn_ZipSearch()
// 설명: 우편번호 조회클릭시 우편번호 조회 popup화면 로딩
// arguments   
// obj: 사용된 객체를 전달 한다.
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_ZipSearch(obj)
{
  // 2013-07-16 김형수 지사 정보가 없을 경우 지사를 선택하게 한다. 지사 선택후 해당 지사에 포함된 구 리스트가 나온다.
  if (cob_Branc.Value == "" || cob_Branc.Value == null) 
  {
     alert("지사를 선택해주세요.");
     cob_Branc.SetFocus();
     return;
  }   
  
    var arrList = gfn_Dialog("A10", "A1101020010U", "", 1100, 600, true, -1, -1);

	if(arrList == 0){
		return;
	}
	
	// 2013-07-16 김형수 시/구 정보를 가지고 있는 상태에서 다른 시구로 변경 못하게 하며, 시/구 정보가 없을때는 
	// 지사를 선택후 해당 지사에 포함된 시/구 범위에 없다면 제한하는 로직으로 변경
  var curZipNo = ds_Drainage.GetColumn(ds_Drainage.row, "ZIP_NO1");
  
  if(trim(curZipNo) != "" && trim(curZipNo) != null)
  {  
      if (curZipNo != arrList[2]) 
      {
         alert("현재 등록된 시/구 정보가 맞지 않습니다.");
         return false;
      }  
  }
  else
  {
      var tempDs = eval(cob_ZipNo1.InnerDataset);
      var rtnVal = tempDs.FindRow("CODE_ITEM", arrList[2]);
      
      var tempCnt = tempDs.getRowCount();
      var tempTxt = "";
      
      for(var i = 0; i < tempCnt - 1; i++)
      {          
          if(tempDs.getColumn(i, "CODE_ITEM") != "")
          {
              tempTxt = tempTxt + tempDs.getColumn(i, "ITEM_KNAME");
            
              if(i != tempCnt - 2)
              {
                  tempTxt = tempTxt + ",";
              }
          }
      }

      if(rtnVal == -1)
      {
          alert("해당 지사에 해당하는 시/구 정보가 다릅니다.\n-"+cob_Branc.Text+"("+tempTxt+")");
          return false;
      }
  }		
		
	
		
	if (arrList != "" && arrList != null) { 

		if(obj.id == "btn_DZip"){
			/*
			edt_lot_num_union.Text  = arrList[17];  		// 지번주소조합
			edt_new_addr_union.Text = arrList[23]; 		// 새주소조합
			edt_zip_seq.Text = arrList[0];         		// 우편일련번호     
			edt_hill.Text 	 = arrList[10];           		// 산번지
			edt_addr_m.Text  = arrList[11];         		// 본번
			edt_addr_s.Text  = arrList[12];         		// 부번  
			edt_second_addr.Text = arrList[13];    		// 보조주소
			edt_bld_dong.Text = arrList[14];       		// 동
			edt_hosu.Text = arrList[15];           		// 호수
			// edt_new_addr_bld_seq.Text = arrList[18]; 	 // 새주소일련번호
			// edt_new_addr_ungrnoud_yn.Text = arrList[25];  // 새주소지하여부
			// edt_new_hosu.Text = arrList[21];              // 새주소호수 
			edt_zip_no1.Text = arrList[2];				// 우편번호1
			edt_zip_no2.Text = arrList[3];              // 우편번호2
			*/
			
			var row = ds_DrainageDayDetail.row;
			ds_DrainageDayDetail.SetColumn( row , "ZIP_NO1" ,  arrList[2] );
			ds_DrainageDayDetail.SetColumn( row , "ZIP_NO2" ,  arrList[3] );
			ds_DrainageDayDetail.SetColumn( row , "ZIP_SEQ" ,  arrList[0] );
			ds_DrainageDayDetail.SetColumn( row , "HILL" , arrList[10]  );
			ds_DrainageDayDetail.SetColumn( row , "ADDR_M" , arrList[11]  );
			ds_DrainageDayDetail.SetColumn( row , "ADDR_S" , arrList[12]  );
			ds_DrainageDayDetail.SetColumn( row , "SECOND_ADDR" ,  arrList[13] );
			ds_DrainageDayDetail.SetColumn( row , "BLD_DONG" ,  arrList[14] );
			ds_DrainageDayDetail.SetColumn( row , "HOSU" ,  arrList[15] );
			ds_DrainageDayDetail.SetColumn( row , "LOT_NUM_UNION" , arrList[17]  );
			ds_DrainageDayDetail.SetColumn( row , "NEW_ADDR_UNION" , arrList[23]  );
		}
	}
		
	G_ACTIVEWIN = parent.id;
}

//-------------------------------------------------------------------------------
// 함수명: D1205010030F_OnLoadCompleted()
// 설명: 화면 로드 후 조회 조건 기본 셋팅
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function D1205010040F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj); 
	//-----------------------------------------
	//  콤보박스의 내용을 셋팅한다.
	//-----------------------------------------
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branc"));
	
	gfn_D1_NULL_SetComboBox_Comcode("D10039", gfn_setDsObject_Comcode("cob_RoadFlag"));
	gfn_D1_NULL_SetComboBox_Comcode("D10062", gfn_setDsObject_Comcode("cob_MakerCd"));
	gfn_D1_NULL_SetComboBox_Comcode("D10311", "ds_InputFlagCombo" ); 
	gfn_D1_NULL_SetComboBox_Comcode("D10312", "ds_SectorTypeCombo" ); 
	//gfn_D1_NULL_SetComboBox_Comcode("D10061", gfn_setDsObject_Comcode("cob_LayTypeUpper"));
	gfn_D1_NULL_SetComboBox_Comcode("A10200", gfn_setDsObject_Comcode("cob_LeaseManageYn"));
	gfn_D1_NULL_SetComboBox_Comcode("B20023", gfn_setDsObject_Comcode("cob_LeaseFlag"));
	gfn_D1_NULL_SetComboBox_Comcode("A10200", gfn_setDsObject_Comcode("cob_ElectPotYn"));
	gfn_D1_NULL_SetComboBox_Comcode("D10063", gfn_setDsObject_Comcode("cob_UseFormalCapa"));
	gfn_D1_NULL_SetComboBox_Comcode("D10064", gfn_setDsObject_Comcode("cob_AcElect")); 
		
	// 콤보박스를 가져오는 공통함수 호출
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") {
	    cob_Branc.value = '';
	} else {
	    cob_Branc.Value = gfn_GetGlobalValue("G_BRANCH_CD");
	    cob_Branc.Enable = false;
	}
	
	cob_BranchCd_OnChanged();
	gfn_D1_SetGrid_Sort(grd_Dra);
	//ds_InputFlagCombo.DeleteRow( ds_InputFlagCombo.rowcount - 1  );
}

//-------------------------------------------------------------------------------
// 기      능   :  지사별 구를 조회하여 콤보박스에 셋팅
//-------------------------------------------------------------------------------
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

    // 지사별 구 콤보 셋팅 시
    fn_SetComboBox_Comcode("1", cob_Branc.value, fn_setDsObject_Comcode("1", "cob_ZipNo1"));

    if (cob_Branc.Value == "" || cob_Branc.Value == null) {
		cob_ZipNo1.Value = "";
    } else {
		cob_ZipNo1.Value = "";
    }
    
    gfn_D1_SetComboBox_Teamcd("ds_Team",cob_Branc);
    cob_Team.Value = "";
    
    cob_Team_OnChanged();
}    

//---------------------------------------------------------------------------------
// 기      능   :  공통 조회
//---------------------------------------------------------------------------------
function fn_CommonSearch()
{
	fn_D1_Search();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_Search()
// 설명: 정류기 등록/수정 대장 화면 리스트를 조회 하는 함수
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_Search()
{	
    grd_Dra.NoDataText = "";
    ds_Drainage.ClearData();
    ds_Search.DeleteAll();
    
    //Row 추가후 위치 Return
    var strRow = ds_Search.AddRow();
    
    //조회조건 Setting
    ds_Search.SetColumn(strRow, "BRANCH_CD", cob_Branc.Value);
    ds_Search.SetColumn(strRow, "ZIP_NO1", cob_ZipNo1.Value);
    ds_Search.SetColumn(strRow, "DRAIN_NM", edt_DraNm.Text);
    ds_Search.SetColumn(strRow, "TEAM_CD", cob_Team.Value);
    ds_Search.SetColumn(strRow, "BLOCK_CD", cob_PBlockCd.Value);
    
	//-------------------------------------------------------------------------------
	//  Transition   : Service id
	//  ServicePart   : Service part
	//  Url   : Server url
	//  InputDsList   : Input Dataset List
	//  OutputDsList   : Output Dataset List
	//  Argument   :  Argument
	//-------------------------------------------------------------------------------	
    var ServiceId     = "selectDrainList";
    var ServicePart   = "D10";
    var Url           = "handle.do?ServiceName=D1_FacilityInfoDraMgr-service";
    var InputDsList   = "ds_Search=ds_Search";
    var OutputDsList  = "ds_Drainage=ds_Drainage";
    var Argument      = "selectDrainList";
  
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);   

    //데이터갯수 출력및 데이터가 없을시 Grid에 조회결과 유무 출력	
    if( ds_Drainage.GetRowCount() < 1) {
        grd_Dra.NoDataText="조회결과가 없습니다."; 
	} else {
		st_Reg.Text = ds_Drainage.GetRowCount();
	} 
		 
    fv_faciNum = "";
    edt_FileList1.text = "";
    edt_FileList2.text = "";      
    grd_Dra_OnCellClick();
}

