	var strPart      = "D10";           
	var strReport    = "D1202010024P"; 
	var strParameter = "FACI_NUM=" + ds_Drainage.GetColumn(nRow,"FACI_NUM")    
					 ;
					
	// 공통 레포트 팝업 함수 호출 
	gfn_ReportPopup(strTitle,strPart,strReport,strParameter);
} 

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FileListNm(strFaciNum)
// 설명: 시설물 파일명을 조회 하는 함수
// arguments   
// 		strFaciNum  : 시설물 번호
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FileListNm(strFaciNum)
{	
    var ServiceId     = "search";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+ strFaciNum;
    var InputDsList     = "";
    var OutputDsList = "ds_FileList=ds_FileList";
    var Argument      = "findFile";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}
	
	edt_FileList1.text = "";
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		edt_FileList1.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) edt_FileList1.text += "/";
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_D1_FileListNmImg(strFaciNum)
// 설명: 시설물 파일명을 조회 하는 함수
// arguments   
// strFaciNum  : 시설물 번호
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_FileListNmImg(strFaciNum)
{	
    var ServiceId     	= "search";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName=D1_FacilityInfoMgr-service&FACI_NUM="+ strFaciNum;
    var InputDsList     = "";
    var OutputDsList 	= "ds_FileList2=ds_FileList2";
    var Argument      	= "findFile2";
    
    gfn_SyncCall(ServiceId, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ErrorCode < 0)
	{
		return;
	}
	
	edt_FileList2.text = "";
	for(var i=0; i<ds_FileList2.RowCount(); i++)
	{
		edt_FileList2.text += ds_FileList2.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList2.RowCount()-1 != i) edt_FileList2.text += "/";
	}
}

//------------------------------------------------------------------------------- 
// 함수명: fn_RectifierDayAddRow()
// 설명: 정류기 이력 조회 내용를 신규 추가 하는 모드로 변경 하는 함수
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_DrainDayAddRow()
{	  
	
	if(ds_Drainage.row < 0) return; 

	

    if( ds_DrainageDayDetail.CaseCount( "INPUT_YMD='"+cal_InputYmd.Value+"'" , "INPUT_YMD")  >= ds_InputFlagCombo.rowcount - 1) {
		alert( "정류기 이력은 입력구분 갯수(3항목)이상 등록이 불가능합니다.\n입력구분별 1가지씩만 등록가능합니다." );
		return;
    }    
	
	fv_saveMode = "insertDraDay"; 
		
	if(cal_InputYmd.Value == null || cal_InputYmd.Value == "")
	{
		cal_InputYmd.Value = today();
	}
	ds_DrainageDayDetail.AddRow();  
	var curRow = ds_Drainage.currow; 
	var curRDDRow = ds_DrainageDayDetail.currow;
	ds_DrainageDayDetail.SetColumn(curRDDRow, "FACI_NUM"    , ds_Drainage.GetColumn(curRow, "FACI_NUM"));
	ds_DrainageDayDetail.SetColumn(curRDDRow, "DRAINAGE_NM" , ds_Drainage.GetColumn(curRow, "DRAINAGE_NM"));  
	ds_DrainageDayDetail.SetColumn(curRDDRow, "ZIP_NO1"     , ds_Drainage.GetColumn(curRow, "ZIP_NO1")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "ZIP_NO2"     , ds_Drainage.GetColumn(curRow, "ZIP_NO2")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "WORK_FIRM_CD"     , ds_Drainage.GetColumn(curRow, "WORK_FIRM_CD")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "WORK_FIRM_NM"     , ds_Drainage.GetColumn(curRow, "WORK_FIRM_NM"));  
	ds_DrainageDayDetail.SetColumn(curRDDRow, "INST_YMD"     , ds_Drainage.GetColumn(curRow, "INST_YMD"));  
	ds_DrainageDayDetail.SetColumn(curRDDRow, "M_PRESS_CATHODIC_LENG"     , ds_Drainage.GetColumn(curRow, "M_PRESS_CATHODIC_LENG")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "L_PRESS_CATHODIC_LENG"     , ds_Drainage.GetColumn(curRow, "L_PRESS_CATHODIC_LENG")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "OPERAT_VOLT"     , ds_Drainage.GetColumn(curRow, "OUTPUT_VOLTAGE")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "OPERAT_ELECT"     , ds_Drainage.GetColumn(curRow, "OUTPUT_VOLTAIC"));  
	ds_DrainageDayDetail.SetColumn(curRDDRow, "AC_ELECT"     , ds_Drainage.GetColumn(curRow, "AC_ELECT"));  
	ds_DrainageDayDetail.SetColumn(curRDDRow, "MTR_NUM"     , ds_Drainage.GetColumn(curRow, "MTR_NUM")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "CUST_NUM"     , ds_Drainage.GetColumn(curRow, "CUST_NUM")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "SQUARE"     , ds_Drainage.GetColumn(curRow, "SQUARE")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "LEASE_FLAG"     , ds_Drainage.GetColumn(curRow, "LEASE_FLAG")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "LEASE_MANAGE_YN"     , ds_Drainage.GetColumn(curRow, "LEASE_MANAGE_OBJ_YN")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "ELECT_POT_YN"     , ds_Drainage.GetColumn(curRow, "ELECT_POT_YN")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "RMK"     , ds_Drainage.GetColumn(curRow, "RMK")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "ZIP_SEQ"     , ds_Drainage.GetColumn(curRow, "ZIP_SEQ")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "HILL"     , ds_Drainage.GetColumn(curRow, "HILL")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "ADDR_M"     , ds_Drainage.GetColumn(curRow, "ADDR_M")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "ADDR_S"     , ds_Drainage.GetColumn(curRow, "ADDR_S")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "SECOND_ADDR"     , ds_Drainage.GetColumn(curRow, "SECOND_ADDR")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "BLD_DONG"     , ds_Drainage.GetColumn(curRow, "BLD_DONG")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "HOSU"     , ds_Drainage.GetColumn(curRow, "HOSU")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "LOT_NUM_UNION"     , ds_Drainage.GetColumn(curRow, "LOT_NUM_UNION")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "NEW_ADDR_UNION"     , ds_Drainage.GetColumn(curRow, "NEW_ADDR_UNION"));    
	ds_DrainageDayDetail.SetColumn(curRDDRow, "ROAD_FLAG"     , ds_Drainage.GetColumn(curRow, "ROAD_FLAG"));    
	ds_DrainageDayDetail.SetColumn(curRDDRow, "MAKE_YMD"     , ds_Drainage.GetColumn(curRow, "MAKE_YMD"));
	ds_DrainageDayDetail.SetColumn(curRDDRow, "MAKER_CD"     , ds_Drainage.GetColumn(curRow, "MAKER_NM"));
	ds_DrainageDayDetail.SetColumn(curRDDRow, "IMPEDANCE_BOND_NUM"     , ds_Drainage.GetColumn(curRow, "IMPEDANCE_BOND_NUM"));     
	ds_DrainageDayDetail.SetColumn(curRDDRow, "SUBWAY_NM"     , ds_Drainage.GetColumn(curRow, "SUBWAY_NM")); 
	ds_DrainageDayDetail.SetColumn(curRDDRow, "USE_FORMAL_CAPA"     , ds_Drainage.GetColumn(curRow, "USE_FORMAL_CAPA"));   
	ds_DrainageDayDetail.SetColumn(curRDDRow, "WORK_NUM"     , ds_Drainage.GetColumn(curRow, "WORK_NUM"));   

	ds_DrainageDayPoint.ClearData();
	ds_DrainageDaySector.ClearData();
	//정류점 추가
	//fn_AddRowRectifierDayPoint();
	//fn_D1_Common02();
}

//------------------------------------------------------------------------------- 
// 함수명: fn_AddRowRectifierDayPoint()
// 설명: 배류기 이력추가시 배류점데이터 로우가 추가된다.
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_AddRowDrainageDayPoint()
{	
	if( ds_DrainageDayPoint.GetRowCount() == 0 ) { 
		ds_DrainageDayPoint.ClearData();
		var cRow = ds_DrainageDayPoint.AddRow();
		ds_DrainageDayPoint.SetColumn( cRow, "FACI_NUM"     , ds_DrainageDayDetail.GetColumn( cRow , "FACI_NUM"));
		ds_DrainageDayPoint.SetColumn( cRow, "INPUT_YMD"    , cal_InputYmd.Value);
		ds_DrainageDayPoint.SetColumn( cRow, "INPUT_FLAG"   , 3);
		ds_DrainageDayPoint.SetColumn( cRow, "DRAINAGE_NM"  , ds_DrainageDayDetail.GetColumn( cRow , "DRAINAGE_NM"));
		
		ds_DrainageDayPoint.SetColumn( cRow, "OPERAT_ELECT_ON"  , 0 );
		ds_DrainageDayPoint.SetColumn( cRow, "OPERAT_ELECT_OFF" , 0 );
		ds_DrainageDayPoint.SetColumn( cRow, "ELECT_ON"  , 0 );
		ds_DrainageDayPoint.SetColumn( cRow, "ELECT_OFF" , 0 );
		ds_DrainageDayPoint.SetColumn( cRow, "CATHODIC_LENG"   , 0 );
		ds_DrainageDayPoint.SetColumn( cRow, "CATHODIC_SQUARE" , 0 );
		ds_DrainageDayPoint.SetColumn( cRow, "AC_ELECT"     , 0 );
		ds_DrainageDayPoint.SetColumn( cRow, "AC_FREQUENCY" , 0 );
		ds_DrainageDayPoint.SetColumn( cRow, "SOIL_RESIS"   , 0 );
	}  
	//trace(ds_RectifierDayDetail.GetColumn( ds_RectifierDayDetail.currow , "FACI_NUM"));
	//trace(ds_RectifierDayDetail.GetColumn( ds_RectifierDayDetail.currow , "INPUT_FLAG")) ;
}



//------------------------------------------------------------------------------- 
// 함수명: fn_DrainageDaySave()
// 설명: 배류기 정보 상세 내용를 저장 하는 함수
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_DrainageDaySave()
{	   	
	
	if( ds_DrainageDayDetail.GetRowType(grd_DraDay.CurrentRow) == "update"){
		fv_saveMode = "updateDraDay";
	}else if(ds_DrainageDayDetail.GetRowType(grd_DraDay.CurrentRow) == "normal"){
		alert("변경된 내용이 없습니다.");
		return;
	} 
	
	// 필수조회조건 확인 
    if(!gfn_B3_CheckRequired( this , 10, 50 ) ) return false;  
     
	//변경 수정자 세팅.
	gfn_setGloalAuditValue( "ds_DrainageDayDetail" ); 
    
    var input_flag_chk = true;
    for( var i = 0; i  < ds_DrainageDayDetail.rowcount ; i ++ )
    {
		if( gfn_isNull(ds_DrainageDayDetail.GetColumn( i , "INPUT_FLAG" )) ) input_flag_chk= false;
    }
    if(!input_flag_chk) {
		alert( "배류기 이력조회 그리드중 입력구분이 입력되지 않은 행이 있습니다." );
		return;
    } 
    
    if( ds_DrainageDayDetail.GetColumn( ds_DrainageDayDetail.row , "INPUT_FLAG" ) == '3' )
    { 
		if( !fn_mergeDrainageDayPoint() ) return;
		if( !fn_DelInsertDrainageDaySector()) return;
    } 
	
	var Transition     	= fv_saveMode;
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoDraMgr-service";
    var InputDsList     = "ds_DrainageDayDetail=ds_DrainageDayDetail:u";
    var OutputDsList 	= "ds_DrainageDayDetail=ds_DrainageDayDetail";
    var Argument      	= fv_saveMode;
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);

	if(ErrorCode<0)
	{
		return false;
	}
	
	
    var str_Max = ds_DrainageDayDetail.Max("INPUT_YMD"); 
    
	if(str_Max == ds_DrainageDayDetail.GetColumn(0, "INPUT_YMD")){
		fn_UpdateDrainage();
	} 
	
	   
	alert( gfn_GetMessage("M000009","%AAA%","배류기 이력 저장을")); 
	
	if( fv_saveMode=="insertDraDay" )
	{
		// 팀-전체
		cob_Team.Value = "";     // 전체
		cob_PBlockCd.Value = ""; // 전체
	}
	
	fn_D1_Search(); 
}

function fn_UpdateDrainage()
{
	ds_DrainageDayDetail.SetColumn(0, "UPD_DTM", today());
		
	var Transition		= "updateDrainage";
    var ServicePart     = "D10";
    var Url           	= "handle.do?ServiceName=D1_FacilityInfoDraMgr-service";
    var InputDsList     = "ds_DrainageDayDetail=ds_DrainageDayDetail:u";
    var OutputDsList 	= "";
    var Argument      	= "updateDrainage";

    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
    if(ErrorCode<0)
	{
		return false;
	}
}

//------------------------------------------------------------------------------- 
// 함수명: mergeRectifierDayPoint()
// 설명: 정류점 Merge 처리
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_mergeDrainageDayPoint()
{ 	/*
	trace( ds_RectifierDayPoint.GetColumn( 0 , "FACI_NUM" ) );
	trace( ds_RectifierDayPoint.GetColumn( 0 , "INPUT_YMD" ) );
	trace( ds_RectifierDayPoint.GetColumn( 0 , "INPUT_FLAG" ) );
	*/  
	if( cob_InputFlag.Value != '3' ) { alert( '배류기 입력구분이 영역조사인경우만 등록가능합니다.' ); return; }
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "LOT_NUM_UNION") ))
	{
		alert("[배류점] 주소를 입력해주세요.");
		return false;
	}
	
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "OPERAT_ELECT_ON") ))
	{
		alert("[배류점] 출력전류ON 값을 입력해주세요.");
		return false;
	}
	
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "OPERAT_ELECT_OFF") ))
	{
		alert("[배류점] 출력전류OFF 값을 입력해주세요.");
		return false;
	}
	
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "ELECT_ON") ))
	{
		alert("[배류점] 전위ON 값을 입력해주세요.");
		return false;
	}
	
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "ELECT_OFF") ))
	{
		alert("[배류점] 전위OFF 값을 입력해주세요.");
		return false;
	}
	
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "CATHODIC_LENG") ))
	{
		alert("[배류점] 방식연장 값을 입력해주세요.");
		return false;
	}
	
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "CATHODIC_SQUARE") ))
	{
		alert("[배류점] 방식면적 값을 입력해주세요.");
		return false;
	}
	
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "AC_ELECT") ))
	{
		alert("[배류점] AC전위 값을 입력해주세요.");
		return false;
	}
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "AC_FREQUENCY") ))
	{
		alert("[배류점] AC주파수 값을 입력해주세요.");
		return false;
	}
	if( gfn_isNull ( ds_DrainageDayPoint.GetColumn(0, "SOIL_RESIS") ))
	{
		alert("[배류점] 토양비저항 값을 입력해주세요.");
		return false;
	}   
	
	ds_DrainageDayPoint.SetColumn(0, "UPD_EMPID" , gfn_GetGlobalValue("G_USER_NO"));
	ds_DrainageDayPoint.SetColumn(0, "UPD_IP"    , gfn_GetGlobalValue("G_IPADDRESS"));
	
	//alert(ds_DrainageDayPoint.SaveXML());
	//======================================================== 정류점 자료 저장
	var Transition     	= "mergeDrainageDayPoint";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoDraMgr-service";
    var InputDsList     = "ds_DrainageDayPoint=ds_DrainageDayPoint:u";
    var OutputDsList 	= "";
    var Argument      	= "mergeDrainageDayPoint";
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    //========================================================
    
    if(ErrorCode<0)
	{
		return false;
	}
	return true;
}

//------------------------------------------------------------------------------- 
// 함수명: fn_DelInsertRectifierDaySector()
// 설명: 영역조사 측정 데이터 해당날짜 자료 삭제후 재등록 처리
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_DelInsertDrainageDaySector()
{  
	if( cob_InputFlag.Value != '3' ) { alert( '배류기 입력구분이 영역조사인경우만 등록가능합니다.' ); return; }
	//통합저장시 데이터가 없을때 건너 띄우게 한다.
	if( cob_InputFlag.Value == '3' && ds_DrainageDaySector.GetRowCount() < 1 && !ds_DrainageDaySector.GetUpdate() ) 
	{ 	
		return true;
	}
	
	var arrColId = Array(1);
	var arrColNm = Array(1);
	arrColId[0] = "SECTOR_FACI_FLAG";
	/*
	arrColId[1] = "OPERAT_ELECT_ON"; 
	arrColId[2] = "OPERAT_ELECT_OFF"; 
	arrColId[3] = "ELECT_ON"; 
	arrColId[4] = "ELECT_OFF";
	arrColId[5] = "CATHODIC_LENG"; 
	arrColId[6] = "CATHODIC_SQUARE";
	arrColId[7] = "AC_ELECT"; 
	arrColId[8] = "AC_FREQUENCY";
	arrColId[9] = "SOIL_RESIS"; 
	*/
	arrColNm[0] = "유형";
	/*
	arrColNm[1] = "출력전류ON"; 
	arrColNm[2] = "출력전류OFF"; 
	arrColNm[3] = "전위ON"; 
	arrColNm[4] = "전위OFF";
	arrColNm[5] = "방식연장"; 
	arrColNm[6] = "방식면적";
	arrColNm[7] = "AC전위"; 
	arrColNm[8] = "AC주파수";
	arrColNm[9] = "토양비저항"; 
	*/
	for( var i = 0 ; i < ds_DrainageDaySector.rowcount ; i ++ ){  
		for ( n = 0 ; n < Length( arrColId ) ; n ++ )
		{ 
			if( gfn_isNull ( ds_DrainageDaySector.GetColumn( i , arrColId[n] ) ))
			{
				alert( "[영역조사데이터] " 
					 + ds_DrainageDaySector.GetColumn( i , "SECTOR_NUM" )
					 + "섹터 "
					 + ds_DrainageDaySector.GetColumn( i , "SECTOR_SUB_NUM" )
					 + "번째 행의 " 
					 + arrColNm[n] 
					 + " 값을 입력해주세요."); 
				return false;
			}
		} 
	}          
      
    
	/*
	trace( ds_RectifierDaySector.SaveXML() ); 
	trace( ds_RectifierDaySector.GetColumn( 0 , "FACI_NUM" ) );
	trace( ds_RectifierDaySector.GetColumn( 0 , "INPUT_YMD" ) );
	trace( ds_RectifierDaySector.GetColumn( 0 , "INPUT_FLAG" ) ); 
	*/ 
	//변경 수정자 세팅.
	gfn_setGloalAuditValue( "ds_DrainageDaySector" );
    
	//ds_RectifierDaySector.SetUpdate(false);
	//======================================================== 정류점 자료 저장
	var Transition     	= "delInsertDraSector";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoDraMgr-service";
    var InputDsList     = "ds_DrainageDaySector=ds_DrainageDaySector:u";
    var OutputDsList 	= "";
    var Argument      	= "delInsertDraSector";
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    //======================================================== 
    
    if(ErrorCode < 0)
	{
		return false;
	} else 
	{ 
		return true;
	}
	//alert( "영역조사 측정데이터값이 저장되었습니다." );
}

//------------------------------------------------------------------------------- 
// 함수명: fn_CommonDeleteRow()
// 설명: 정류기등록를 삭제 하는 함수
// arguments   
// return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_DrainageDayDelete()
{  
    if(!Confirm(" ========================================= " 
			   + "\n - 배류기 번호 : " + edt_FaciNum.Text
			   + "\n - 입력일자 : " + cal_InputYmd.Text
			   + "\n - 입력구분 : " + cob_InputFlag.Text
			   + "\n ========================================= \n" 
			   + "배류기 정보를 삭제하게 되면 등록된 정류점 및 영역조사 측정 데이터 값이 모두 삭제 됩니다. 진행하시겠습니까?"))
    {
        return false;
    }

    if(ds_DrainageDayDetail.GetRowCount() == 0){
        alert("삭제할 내용이 없습니다.");
		return;
    }
 
	ds_DrainageDayDetail.DeleteRow( ds_DrainageDayDetail.row ); 
	
	/*
    for( var i = 0 ; i < ds_RectifierDayDetail.rowcount ; i ++ ) {
		 trace( ds_RectifierDayDetail.GetRowType( i ) );
		 trace( ds_RectifierDayDetail.GetColumn( i  , "FACI_NUM" ) );
		 trace( ds_RectifierDayDetail.GetColumn( i , "INPUT_YMD") );
		 trace( ds_RectifierDayDetail.GetColumn( i , "INPUT_FLAG") ); 
    }*/ 

    var Transition		= "deleteDraDay";
    var ServicePart     = "D10";
    var Url             = "handle.do?ServiceName="+"D1_FacilityInfoDraMgr-service";
    var InputDsList     = "ds_DrainageDayDetail=ds_DrainageDayDetail:u";
    var OutputDsList 	= "";
    var Argument      	= "deleteDraDay";
    
    gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
    
	if(ErrorCode<0)	{
		if(ErrorMsg!="") {
	       alert(ErrorMsg);
	    } else {   
	       alert("삭제 중 오류가 발생했습니다.");
	    }
	    
		//ds_RectifierDay.Reset();
		return false;
	}else{
		
	}
    fn_D1_Search();
}


function fn_D1_FileDelete(str_FACI_NUM){
  	    
	ds_FaciFile.ClearData();
	ds_FaciFile.AddRow();
	
	ds_FaciFile.SetColumn(0, "FACI_NUM", str_FACI_NUM);
	
	ds_FaciFile.UpdateControl = false;
	ds_FaciFile.SetRowType(0, "delete");
	
	// 첨부파일 삭제
	var Transition    = "deleteGov";
	var ServicePart   = "D10";
	var Url           = "handle.do?ServiceName=D1_FacilityFileInfoMgr-service";
	var InputDsList   = "ds_FaciFile=ds_FaciFile:u";
	var OutputDsList  = "";
	var Argument      = "deleteFaciFile";
	
	gfn_SyncCall(Transition, ServicePart, Url, InputDsList, OutputDsList, Argument);
}  

